"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[9141],{1582:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>o,frontMatter:()=>a,metadata:()=>i,toc:()=>l});const i=JSON.parse('{"id":"archiv/2000-2009/2009","title":"2009","description":"20. Januar - Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder","source":"@site/docs/archiv/2000-2009/2009.md","sourceDirName":"archiv/2000-2009","slug":"/archiv/2000-2009/2009","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2009","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2000-2009/2009.md","tags":[],"version":"current","sidebarPosition":1,"frontMatter":{"sidebar_position":1},"sidebar":"archivSidebar","previous":{"title":"2000 bis 2009","permalink":"/archiv-frohe-stunde/docs/category/2000-2009"},"next":{"title":"2008","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2008"}}');var s=r(4848),t=r(8453);const a={sidebar_position:1},d="2009",h={},l=[{value:"20. Januar - Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder",id:"20-januar---gedenkgottesdienst-f\xfcr-die-lebenden-und-verstorbenen-mitglieder",level:2},{value:"01. Februar - Matinee des Chorverbandes Rheinland-Pfalz im Schlo\xdf Waldthausen bei Mainz",id:"01-februar---matinee-des-chorverbandes-rheinland-pfalz-im-schlo\xdf-waldthausen-bei-mainz",level:2},{value:"21. M\xe4rz - Konzert mit dem Musikverein Hundsangen im B\xfcrgerhaus Weroth",id:"21-m\xe4rz---konzert-mit-dem-musikverein-hundsangen-im-b\xfcrgerhaus-weroth",level:2},{value:"25. April - Konzert \u201eLiederbl\xfcte\u201c Hirschberg",id:"25-april---konzert-liederbl\xfcte-hirschberg",level:2},{value:"17. Mai - Kritiksingen der S\xe4ngergruppe III in Girod",id:"17-mai---kritiksingen-der-s\xe4ngergruppe-iii-in-girod",level:2},{value:"07. Juni - Chorwettbewerb in Wenden",id:"07-juni---chorwettbewerb-in-wenden",level:2},{value:"18. Juli - 80. Geburtstag von Helmut Schwarz",id:"18-juli---80-geburtstag-von-helmut-schwarz",level:2},{value:"26. August - Silberhochzeit Annette und Wolfgang Malm",id:"26-august---silberhochzeit-annette-und-wolfgang-malm",level:2},{value:"06. September - Diamantene Hochzeit der Eheleute Kratz",id:"06-september---diamantene-hochzeit-der-eheleute-kratz",level:2},{value:"19. September -Landeschorwettbewerb in Bingen",id:"19-september--landeschorwettbewerb-in-bingen",level:2},{value:"31. Oktober - Konzert der Chorvereinigung \u201eLiedertafel\u201c Oberzeuzheim",id:"31-oktober---konzert-der-chorvereinigung-liedertafel-oberzeuzheim",level:2},{value:"07. November - Workshop mit Prof. Volker Hempfling",id:"07-november---workshop-mit-prof-volker-hempfling",level:2},{value:"14. November - Konzert MGV \u201eLiederkranz\u201c Berod",id:"14-november---konzert-mgv-liederkranz-berod",level:2},{value:"15. November - Gedenkfeier der Ortsgemeinde am Ehrenmal",id:"15-november---gedenkfeier-der-ortsgemeinde-am-ehrenmal",level:2},{value:"28. November - 75. Geburtstag von Hans Kaiser",id:"28-november---75-geburtstag-von-hans-kaiser",level:2},{value:"12. Dezember - Jahresabschlussfeier im B\xfcrgerhaus",id:"12-dezember---jahresabschlussfeier-im-b\xfcrgerhaus",level:2},{value:"Was passiert 2009 sonst noch ...",id:"was-passiert-2009-sonst-noch-",level:2}];function u(e){const n={h1:"h1",h2:"h2",header:"header",li:"li",p:"p",strong:"strong",ul:"ul",...(0,t.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"2009",children:"2009"})}),"\n",(0,s.jsx)(n.h2,{id:"20-januar---gedenkgottesdienst-f\xfcr-die-lebenden-und-verstorbenen-mitglieder",children:"20. Januar - Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder"}),"\n",(0,s.jsx)(n.p,{children:"Musikalisch begann das Jahr wieder mit dem Gedenkgottesdienst f\xfcr unsere lebenden und verstorbenen Mitglieder an unserem Sebastianstag, dem 20. Januar in unserer Kapelle. Besonders gedachten wir bei dieser Messe unseren im Jahr 2008 verstorbenen Mitgliedern Robert Sternberger und Josef P\xf6rtner."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohin soll ich mich wenden v. Franz Schubert"}),"\n",(0,s.jsx)(n.li,{children:"Schlaf Freund v. ?"}),"\n",(0,s.jsx)(n.li,{children:"Forschen nach Gott v. Konradin Kreutzer"}),"\n",(0,s.jsx)(n.li,{children:"Sanctus v. Franz Schubert"}),"\n",(0,s.jsx)(n.li,{children:"Harre meine Seele v. Arnold Kempkens"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"01-februar---matinee-des-chorverbandes-rheinland-pfalz-im-schlo\xdf-waldthausen-bei-mainz",children:"01. Februar - Matinee des Chorverbandes Rheinland-Pfalz im Schlo\xdf Waldthausen bei Mainz"}),"\n",(0,s.jsx)(n.p,{children:"Den ersten musikalischen H\xf6hepunkt bildete schon ziemlich fr\xfch im Jahr die Mitwirkung bei einer Veranstaltung des Chorverbandes Rheinland-Pfalz im Schlo\xdf Waldthausen bei Mainz. Der Chorverband feierte im Jahr 2009 sein 60 j\xe4hriges Gr\xfcndungsfest und hatte am 01. Februar zu einer Matinee mit Festakt eingeladen. Neben einem Jugendstreichorchester vom Landesmusikgymnasium Montabaur, den Dombl\xe4sern vom Mainzer Dom, dem Landes Pop- und Jazzchor und dem Kammerchor \u201eBelcanto\u201c aus dem Sauerland wurde auch uns die Ehre zuteil, quasi als Vertreter der M\xe4nnerch\xf6re an dieser Veranstaltung teilzunehmen. Den \xe4u\xdferen Rahmen bildete ein eher kleiner Konzertsaal, der aber auf Grund seiner H\xf6he eine au\xdfergew\xf6hnlich gute Akustik besitzt. Und dies kam nat\xfcrlich allen Akteuren entgegen, soda\xdf sich ein Konzert auf allerh\xf6chstem Niveau entwickelte. Auch wir konnten an diesem Sonntagmorgen das Publikum mit unseren Vortr\xe4gen beeindrucken. Nach lang anhaltendem Beifall kamen wir nicht ohne eine Zugabe von der B\xfchne. F\xfcr uns war es wichtig, da\xdf wir hier eine sehr gute Visitenkarte abgegeben hatten, da der gesamte Vorstand des Chorverbandes, ein Staatssekret\xe4r vom Landeskulturministerium sowie der Pr\xe4sident des Landesmusikrates anwesend war. Und wenn dann eben dieser Pr\xe4sident Prof. Mahling bei seiner Rede vor allen Zuh\xf6rern unsere Leistung besonders hervorhebt und uns mit Nachdruck dazu einl\xe4dt, am rheinland-pf\xe4lzischen Chorwettbewerb teilzunehmen kann man schon von einem Erfolgsmoment reden, der vielleicht richtungsweisend f\xfcr die Zukunft unseres Chores war. Nach einem anschlie\xdfenden Imbiss mit Umtrunk konnten wir zufrieden die Heimreise antreten."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Der Tambour v. Hugo Distler"}),"\n",(0,s.jsx)(n.li,{children:"Abschied v. Mathieu Neumann"}),"\n",(0,s.jsx)(n.li,{children:"What shall we do with the drunken sailor v. A. Parker / R. Shaw"}),"\n",(0,s.jsx)(n.li,{children:"als Zugabe: Das Herz v. Friedrich Silcher"}),"\n"]}),"\n",(0,s.jsx)(n.h1,{id:"13-februar---konzert-mit-ren\xe9-kollo-in-weilburg",children:"13. Februar - Konzert mit Ren\xe9 Kollo in Weilburg"}),"\n",(0,s.jsx)(n.p,{children:"Ein zus\xe4tzlicher Auftritt kam dann recht kurzfristig hinzu. Der bekannte Tenor Ren\xe9 Kollo war mit seinem Programm \u201eEin Tag, ein Jahr, ein Leben\u201c auf Deutschland und gastierte am 13. Februar in der Schlo\xdfkirche in Weilburg. Wie es scheinbar bei seinen Konzerten \xfcblich ist, l\xe4dt er zur Unterst\xfctzung des Programms immer noch einen Chor aus der Umgebung als Mitwirkenden ein. Und so kam \xfcber seinen musikalischen Begleiter der Kontakt mit Jens R\xf6th zustande. Der wiederum setzte sich, da die Veranstaltung schon f\xfcnf Tage sp\xe4ter stattfinden sollte, sofort mit uns und den S\xe4ngern aus Frickhofen in Verbindung, soda\xdf wir als Chorgemeinschaft Frickhofen/Weroth bei dem Kollo-Konzert mitwirkten. Ob es nun daran lag, da\xdf der 13. Februar auch noch ein Freitag war, auf jeden Fall war das Konzert eher als m\xe4\xdfig zu bezeichnen. Die Schlo\xdfkirche war mit ca. 300 Besuchern gerade mal zur H\xe4lfte besetzt und auch der Meister selbst wirkte bei den Proben recht fahrig und unkonzentriert. Beim Konzert steigerte er sich zwar, aber das Programm bot nur wenig Abwechslung und so zog sich der Abend etwas z\xe4h dahin. Die Lieder, die gemeinsam vorgetragen werden sollten, konnte oder wollte er nicht singen, soda\xdf nur zum Schlu\xdf das Lied \u201eDer Mond ist aufgegangen\u201c gemeinsam mit Ren\xe9 Kollo gesungen wurde. Positiv zu erw\xe4hnen war nur, da\xdf der gemeinsame Auftritt mit Frickhofen sehr gut gelang, obwohl wir schon lange nicht mehr zusammen aufgetreten waren."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Soave fia il morir v. Giovanni P. da Palestrina"}),"\n",(0,s.jsx)(n.li,{children:"Nachtgebet v. Mathieu Neumann"}),"\n",(0,s.jsx)(n.li,{children:"Sancta Maria v. Johannes Schweitzer"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"21-m\xe4rz---konzert-mit-dem-musikverein-hundsangen-im-b\xfcrgerhaus-weroth",children:"21. M\xe4rz - Konzert mit dem Musikverein Hundsangen im B\xfcrgerhaus Weroth"}),"\n",(0,s.jsx)(n.p,{children:"\u201eGesang trifft Blasmusik\u201c - unter diesem Motto fand am 21. M\xe4rz ein Konzert zusammen mit dem Musikverein Hundsangen in unserem B\xfcrgerhaus statt. Es war ein lang gehegter Wunsch beider Vereine, einmal gemeinsam eine Veranstaltung hier in Weroth durchzuf\xfchren. Als Gastchor wirkten noch die S\xe4nger der \u201eEintracht aus Frickhofen mit. Weiterhin stellte sich an diesem Abend der von uns im letzten Herbst gegr\xfcndete \u201eFamilienchor der Frohen Stunde\u201c dem Publikum vor. Im Rahmen der Mitglieder- und S\xe4ngerwerbung hatten wir den \u201eFamilienchor\u201c ins Leben gerufen und alle Familienangeh\xf6rige, Frauen und Kinder zur Mitwirkung eingeladen. Die Resonanz darauf fiel aber recht bescheiden aus, sodass letztendlich nur ca. 20 S\xe4ngerinnen und S\xe4nger hier mitwirkten. Aber diese 20 waren mit Eifer bei der Sache und man hatte auch den Eindruck, dass ihnen die Proben durchaus gefielen. Nach einem \xe4u\xdferst z\xe4hen Kartenvorverkauf und einer ziemlich kaotischen Generalprobe sahen die meisten mit sehr gemischten Gef\xfchlen dem Konzertabend entgegen. Aber entgegen allen Bef\xfcrchtungen war die Halle mit fast 180 zahlenden Besuchern sehr gut gef\xfcllt. Und auch alle Mitwirkende steigerten sich an diesem Abend gegen\xfcber den Proben, sodass dem Publikum ein musikalisch hochklassiges und vor allem auch sehr abwechslungsreiches Programm geboten werden konnte. Besonders der Musikverein hat durch eine eindrucksvolle Leistung wohl viele neue Freunde gefunden. Ebenso wie die M\xe4dels und Jungs vom \u201eFamilienchor\u201c, die sich \xfcber den lang anhaltenden Beifall des Publikums besonders freuten. In der Nassauischen Neuen Presse war sp\xe4ter zu lesen: \u201eEin unvergesslicher Konzertabend\u201c der nach einer Wiederholung fordert.\nAuch im Anschlu\xdf an das Programm wurde noch bis sp\xe4t in die Nacht gesungen und gefeiert, sodass man insgesamt von einer doch recht erfolgreichen Veranstaltung sprechen konnte."}),"\n",(0,s.jsx)(n.h2,{id:"25-april---konzert-liederbl\xfcte-hirschberg",children:"25. April - Konzert \u201eLiederbl\xfcte\u201c Hirschberg"}),"\n",(0,s.jsx)(n.p,{children:"Am 25. April nahmen wir wieder einmal an einer Veranstaltung in Hirschberg teil. Anscheinend hatten wir bei unserem letzten Gastspiel im Herbst 2007 so viel Eindruck hinterlassen, dass uns die Verantwortlichen des Gemischten Chores \u201eLiederbl\xfcte\u201c Hirschberg auch zu ihrem Jubil\xe4umskonzert aus Anlass ihres 100-j\xe4hrigen Bestehens eingeladen hatten. Die Veranstaltung fand wiederum im etwas beengten Dorfgemeinschaftshaus statt und zog sich durch die vielen Reden der Ehreng\xe4ste und Ehrungen verdienter Mitglieder doch sehr in die L\xe4nge. Trotzdem fanden wir auch diesmal wieder ein dankbares Publikum vor, das unsere gelungenen Vortr\xe4ge mit langanhaltendem Beifall belohnte, sodass wir nicht ohne eine Zugabe die B\xfchne verlassen konnten. Uns allen wird aber sicherlich die Feier nach dem Ende der Veranstaltung in guter Erinnerung bleiben, wo alle teilnehmende Ch\xf6re noch lange gemeinsam Lieder anstimmten, nat\xfcrlich mit dem entsprechenden Konsum von Bier und Wein. Mehrere Male wurde die R\xfcckfahrt des Busses verschoben, sodass wir erst sp\xe4t in der Nacht wieder heimkehrten."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Der Tambour v. Hugo Distler"}),"\n",(0,s.jsx)(n.li,{children:"Soave fia il morir v. Giovanni P. da Palestrina"}),"\n",(0,s.jsx)(n.li,{children:"Nachtgesang im Walde v. Franz Schubert"}),"\n",(0,s.jsx)(n.li,{children:"Das Herz v. Friedrich Silcher"}),"\n",(0,s.jsx)(n.li,{children:"What shall we do with the drunken sailor v. A. Parker/ Robert Shaw"}),"\n",(0,s.jsx)(n.li,{children:"Im Feuerstrom der Reben v. Jens R\xf6th"}),"\n",(0,s.jsx)(n.li,{children:"Zugabe: Ermunterung v. Herbert Wallas"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"17-mai---kritiksingen-der-s\xe4ngergruppe-iii-in-girod",children:"17. Mai - Kritiksingen der S\xe4ngergruppe III in Girod"}),"\n",(0,s.jsx)(n.p,{children:"Das j\xe4hrliche Kritiksingen der S\xe4ngergruppe III wurde im Jahr 2009 vom MGV \u201eConcordia\u201c Girod durchgef\xfchrt, der gleichzeitig sein 100-j\xe4hriges Bestehen feierte. Chorleiter Jens R\xf6th hatte uns schon fr\xfchzeitig informiert, dass er an diesem Tag als Wertungsrichter bei einem Chorwettbewerb t\xe4tig sei und uns somit nicht zur Verf\xfcgung stand. Da wir den Auftritt bei unseren Freunden aus Girod jedoch nicht absagen wollten, erkl\xe4rte sich unser Vizechorleiter Christoph Weimer bereit, uns an diesem Tag zu leiten, jedoch ohne eine anschlie\xdfende Bewertung durch den Kritiker Martin Winkler. Zum Vortrag brachten wir an diesem Sonntagmorgen \u201eUnter der Linde\u201c von Mathieu Neumann und \u201eDas Herz\u201c von Friedrich Silcher, und beide Lieder gelangen uns ausnehmend gut. Den Unterschied in der Chorleitung bemerkte man kaum. Entgegen der Absprache lie\xdf es sich Martin Winkler nicht nehmen, ein paar kurze aber recht vielsagende Bemerkungen \xfcber den Leistungsstand unseres Chores abzugeben. Auch in der anschlie\xdfenden zweiten Halbzeit im Festzelt hinterlie\xdfen wir in Girod einen guten Eindruck."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Unter der Linde v. Mathieu Neumann"}),"\n",(0,s.jsx)(n.li,{children:"Das Herz v. Friedrich Silcher"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"07-juni---chorwettbewerb-in-wenden",children:"07. Juni - Chorwettbewerb in Wenden"}),"\n",(0,s.jsx)(n.p,{children:"Der Chorwettbewerb am 7. Juni im sauerl\xe4ndischen Wenden bildete den ersten H\xf6hepunkt im Jahr 2009. Wir traten dort in der Klasse M3 an, die, nachdem zwei Ch\xf6re im Vorfeld die Teilnahme abgesagt hatten, neben unserem Chor nur noch aus dem MGV \u201eConcordia\u201c Hainchen bestand. Als Jury fungierten an diesem Tag die uns bestens bekannten Artur Gro\xdf und Gerold Engelhard. Der Wettbewerb selbst fand in der Aula des Wendener Schulzentrums statt, die auch noch \xfcber eine ausreichend gute Akustik verf\xfcgte. Wir trugen hier die St\xfccke \u201eSoave fia il morir\u201c, \u201eWohlauf, ihr G\xe4ste\u201c und \u201eWhat shall we do with the drunken sailor\u201c vor. Obwohl uns keine gravierenden Fehler unterliefen, fehlte uns bei den Vortr\xe4gen die n\xf6tige Lockerheit, es wirkte alles einwenig leicht angestrengt, wie es auch von vielen unserer mitgereisten Fans bemerkt wurde. Und da der Konkurrent aus dem benachbarten Hainchen eine sehr ansprechende Leistung bot, rechnete man mit einem sehr knappen Ergebnis. Bei der Bekantgabe der Punkte im Festzelt wurde dies auch best\xe4tigt. F\xfcr das Chorwerk erhielten beide Ch\xf6re je 143 Punkte und nur bei den Volkliedern erhielten wir mit 135 und 137 Punkten je 2 Punkte mehr, sodass wir insgesamt mit 4 Punkten vor dem Chor aus Hainchen lagen. Die Wertung bekommt jedoch eine ganz andere Bedeutung, wenn man bedenkt, dass beide Ch\xf6re mit einigem Abstand \xfcber der h\xf6herklassigen M2 lag. Schlie\xdflich hatten wir auch noch den Meisterpreis in den kleinen Klassen erreicht und Jens R\xf6th konnte sich \xfcber den Gewinn des Dirigentenpreises freuen. Nachdem unseren beiden j\xfcngsten S\xe4ngern Lukas und Martin die Pokale \xfcberreicht wurden, feierten wir diesen Erfolg nat\xfcrlich noch ausgiebig im Festzelt, solange, bis auch die letzte Getr\xe4nkemarke vernichtet war und wir dann so ziemlich als letzter Chor die Heimreise antraten."}),"\n",(0,s.jsx)(n.h2,{id:"18-juli---80-geburtstag-von-helmut-schwarz",children:"18. Juli - 80. Geburtstag von Helmut Schwarz"}),"\n",(0,s.jsx)(n.p,{children:"Das erste St\xe4ndchen im Jahr brachten wir am 18. Juli unserem Ehrenmitglied Helmut Schwarz dar. Er war am 14. Juli 80 Jahre alt geworden und feierte diesen Tag mit seiner Familie, Freunden und Nachbarn in unserem B\xfcrgerhaus. Hans-Georg Jung ging in seiner Dankesrede besonders auf die liebenswerten Eigenheiten von Helmut als eines der letzten Wer\xf6ther \u201eOriginale\u201c ein. Ein sichtlich bewegter Jubilar dankte anschlie\xdfend dem Chor und lud uns zu Speis und Trank ein. Der Chor stand an diesem Abend unter der souver\xe4nen Leitung unseres Vizes Manfred Fachinger."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Unter der Linde v. Mathieu Neumann"}),"\n",(0,s.jsx)(n.li,{children:"Das Morgenrot v. Robert Pracht"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"26-august---silberhochzeit-annette-und-wolfgang-malm",children:"26. August - Silberhochzeit Annette und Wolfgang Malm"}),"\n",(0,s.jsx)(n.p,{children:"Am 26. August brachten wir nach unserer w\xf6chentlichen Probe ein weiteres St\xe4ndchen dar. Annette und Wolfgang Malm hatten Ende Juli ihre \u201eSilberne Hochzeit\u201c begannen, und waren an diesem Mittwochabend extra von Hundsangen nach Weroth gekommen um dies gemeinsam mit unserem Chor zu feiern. Johannes Hannappel dankte in seiner Dankesrede unserem passiven Mitglied Wolfgang f\xfcr seine langj\xe4hrige Arbeit als Vizechorleiter unseres Chores, der uns immer, wenn es irgend ging, mit Rat und Tat unterst\xfctzte. Zu erw\xe4hnen ist noch, dass es uns tats\xe4chlich gelang an diesem Abend das von den beiden gestiftete 50-Liter Fass Bier zu leeren. Eine au\xdfergew\xf6hnliche Leistung."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Das Herz v. Friedrich Silcher"}),"\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Der Tambour v. Hugo Distler"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"06-september---diamantene-hochzeit-der-eheleute-kratz",children:"06. September - Diamantene Hochzeit der Eheleute Kratz"}),"\n",(0,s.jsx)(n.p,{children:"Ein weiteres St\xe4ndchen brachten wir aus Anlass der Diamantenen Hochzeit der Eheleute Kratz am 6. September dar. Unser f\xf6rderndes Mitglied Werner Kratz hatte unseren Chor zu einem mitt\xe4glichen Empfang auf der Freim\xfchle eingeladen, wo wir den offiziellen Teil der Feier mit einigen Liedern musikalisch mitgestalteten. Der anschlie\xdfenden Einladung zu Speis und Trank kamen wir, bei herrlichstem Sp\xe4tsommerwetter, gerne nach."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Das Morgenrot v. Robert Pracht"}),"\n",(0,s.jsx)(n.li,{children:"Forschen nach Gott v. Konradin Kreutzer"}),"\n",(0,s.jsx)(n.li,{children:"Unter der Linde v. Mathieu Neumann"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"19-september--landeschorwettbewerb-in-bingen",children:"19. September -Landeschorwettbewerb in Bingen"}),"\n",(0,s.jsx)(n.p,{children:"Die Teilnahme am 8. Rheinland-Pf\xe4lzischen Chorwettbewerb in Bingen bildete den absoluten H\xf6hepunkt des Jahres. Der Wettbewerb fand diesmal im Rahmen des \u201eFestivals der Stimmen\u201c bei sch\xf6nem Sp\xe4tsommerwetter am 19. September in Bingen statt. Der Umstand, dass in diesem Jahr das Chorwerk \u201eSoave fia il morir\u201c eines der vorgegebenen Pflichtst\xfccke kam uns bei der Vorbereitung nat\xfcrlich sehr entgegen und war auch mit ausschlaggebend f\xfcr unsere Entscheidung zur Teilnahme am Landeschorwettbewerb. Jedoch erfuhren wir schon im Fr\xfchjahr des Jahres, dass sich Dirigent J\xfcrgen Fassbender schon seit geraumer Zeit einen Auswahlchor mit dem Namen \u201eLiedertafel\u201c zusammenstellte und diesen Chor in unserer Klasse C2 ab 41 S\xe4nger angemeldet hatte. Dieser Chor stellt eine Auswahl der besten S\xe4nger der Ch\xf6re aus Freiendiez, Niederzeuzheim, Ebernhahn und Plaidt dar und bei uns kam schnell der Verdacht auf, dass dies alles von langer Hand geplant war um eine Teilnahme J\xfcrgen Fassbenders beim n\xe4chsten Deutschen Chorwettbewerb zu erm\xf6glichen. Chorleiter Jens R\xf6th und der gesch\xe4ftsf\xfchrende Vorstand dachte ernsthaft \xfcber die R\xfccknahme der Anmeldung oder zumindest einen schriftlichen Protest beim Landesmusikrat nach, einigten sich dann jedoch darauf, die Herausforderung anzunehmen und sich bestm\xf6glichst vorzubereiten. Mit der von uns ausgew\xe4hlten Literatur wollten wir den Juroren die ganze Bandbreite unseres K\xf6nnens aufzeigen. Neben dem \u201eSoave\u201c hatten wir noch \u201eWohlauf, ihr G\xe4ste\u201c, \u201eDer Tambour\u201c, \u201e Veno\u201c von Smetana, den \u201eDrunken Sailor\u201c und zum Schlu\xdf \u201eDas Herz\u201c von Silcher gemeldet. Kurz nach der Ankunft in Bingen kam aber schon die erste Aufregung. Der Austragungsort des Wettbewerbs war die Mehrzweckhalle der Grundschule \u201eAm M\xe4useturm\u201c und eigentlich f\xfcr eine solche Veranstaltung viel zu klein. Das gr\xf6\xdfte Problem aber war, dass die aufgebauten drei Stufen auf den die Ch\xf6re singen sollten h\xf6chstens Platz f\xfcr 25 S\xe4nger hatte. Wir entschlossen uns aber schnell in unserer gewohnten Aufstellung vor den Stufen den Auftritt zu absolvieren. Und diese Entscheidung war goldrichtig. Trotz der recht eigenartigen Akustik der Halle waren wir von Anfang an hoch konzentriert, gleichzeitig besa\xdfen wir aber auch die n\xf6tige Lockerheit und Beweglichkeit im Chor. Bei allen St\xfccken konnten wir unsere St\xe4rken in der Dynamik und Textinterpretation zeigen. Jens R\xf6th sprach sp\xe4ter von einer 98prozentigen Leistung, die mit der in Prag zu vergleichen sei. Neben dem Publikum spendeten auch die 5 Juroren reichlich Beifall. H\xf6chst zufrieden verlie\xdfen wir die B\xfchne und waren uns einig, gegen\xfcber der starken Leistung der Fassbender\u2019chen \u201eLiedertafel\u201c nicht allzu viel abgefallen zu sein. Einige von unseren zahlreich mitgefahrenen Fans sahen uns sogar ein wenig im Vorteil. Und so warteten wir gespannt auf die Bekanntgabe der Wertung durch Prof. Mahling, dem Pr\xe4sidenten des Landesmusikrates. Die \u201eLiedertafel\u201c erhielt mit 22 Punkte das Pr\xe4dikat \u201emit sehr gutem Erfolg teilgenommen\u201c, gleichzeitig die Weitermeldung zum Deutschen Chorwettbewerb. Doch dann die \xdcberraschung, die hochkar\xe4tige Jury unter Vorsitz von Prof. Hempfling, mit J\xfcrgen Budday, Susanne Rohn, Wolfgang Sch\xe4fer und Stefan Kalmer gab unserem Chor mit 21 Punkten das gleiche Pr\xe4dikat und vergaben an uns eine Option, am Deutschen Chorwettbewerb 2010 in Dortmund teilzunehmen. Noch vor der Halle ging dann das Feiern lo\xdf, nachdem man sich aus den umliegenden Tankstellen und Superm\xe4rkten mit Bier versorgt hatte, da am Veranstaltungsort Biernotstand herrschte. Nach einer feucht-fr\xf6hlichen Heimfahrt, einem Empfang vor unserem Vereinslokal mit den Gl\xfcckw\xfcnschen der Ortsgemeinde feierte man noch bis sp\xe4t in die Nacht mit Freibier und Pizza.\nAber es ging ja noch weiter. Die eigentliche \xdcberraschung aber geschah bei der Preisverleihung am n\xe4chsten Tag in der Binger Basilika St. Martin. Sabine und Johannes Hannappel und meine Wenigkeit waren an diesem Sonntagnachmittag eher widerwillig nach Bingen gefahren, weil halt jemand die Urkunden abholen musste. Und nach einem 45min\xfctigen Stau auf der A3 und Regen in Bingen war man bei der Ankunft gleich ganz bedient. Nachdem jeder Chor seine Teilnehmerurkunde von Prof. Mahling erhielt bereitete sich man innerlich schon wieder auf die Heimfahrt vor. Doch es gab ja noch die Verleihung der Sonderpreise. Zur gro\xdfen Freude der Drei wurde der \u201eFrohen Stunde\u201c der Sonderpreis f\xfcr die beste Interpretation eines Volkliedes zugesprochen. Der von Ministerpr\xe4sident Kurt Beck gestiftete Preis wurde durch Frau Schumacher vom Kultusministerium verliehen. Das Ausma\xdf der Freude ist im dann etwas verwackelten Videomitschnitt von Sabine nur andeutungsweise zu erkennen. Wenn man bedenkt, das in einem fast nur aus Auswahlch\xf6ren und Vokalensembles bestehenden Teilnehmerfeld dieser Preis unserem Chor zugesprochen wurde, ist dies wieder einmal eine hohe, vielleicht sogar die h\xf6chste Auszeichnung in der langen Vereinsgeschichte der \u201eFrohen Stunde\u201c Weroth."}),"\n",(0,s.jsx)(n.h2,{id:"31-oktober---konzert-der-chorvereinigung-liedertafel-oberzeuzheim",children:"31. Oktober - Konzert der Chorvereinigung \u201eLiedertafel\u201c Oberzeuzheim"}),"\n",(0,s.jsx)(n.p,{children:"Die Chorvereinigung \u201eLiedertafel\u201c Oberzeuzheim feierte im Jahr 2009 ihr 90j\xe4hriges Bestehen und hatte unseren Chor zur Mitwirkung bei einem Konzert am 31. Oktober eingeladen. Die Veranstaltung fand in der Mehrzweckhalle des Dorfes statt das recht zahlreich erschienene Publikum erlebte ein Konzert auf ansprechendem Niveau, wof\xfcr allein schon die \u201eDi\xdfharmoniker\u201c aus Obertiefenbach sorgten, die ebenfalls mitwirkten. Aber auch unser Chor konnte eine gute Visitenkarte in Oberzeuzheim abgeben. Alle Vortr\xe4ge gelangen ausnehmend gut, wobei besonders die \u201eSchilflieder\u201c und das \u201eSteal away\u201c beim Publikum besonderen Anklang fand und wir um eine Zugabe nicht herum kamen. Nat\xfcrlich war auch hier nach dem Konzert wieder mal nicht Schlu\xdf mit dem Singen, sodass wir nach dem Genuss von reichlich Gerstensaft als letzte die Halle verlie\xdfen."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Der Tambour v. Hugo Distler"}),"\n",(0,s.jsx)(n.li,{children:"Veno v. Bedrich Smetana"}),"\n",(0,s.jsx)(n.li,{children:"Ermunterung v. Herbert Wallas"}),"\n",(0,s.jsx)(n.li,{children:"Schilflieder v. Heinrich Sutermeister"}),"\n",(0,s.jsx)(n.li,{children:"What shall we do with the drunken sailor v. Parker/Shaw"}),"\n",(0,s.jsx)(n.li,{children:"Steal away v. Jens R\xf6th"}),"\n",(0,s.jsx)(n.li,{children:"Als Zugabe: Im Feuersturm der Reben v. Jens R\xf6th"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"07-november---workshop-mit-prof-volker-hempfling",children:"07. November - Workshop mit Prof. Volker Hempfling"}),"\n",(0,s.jsx)(n.p,{children:"Am 7. November f\xfchrte der Fachverband Deutscher Berufschorleiter im Sebastiansaal unseres B\xfcrgerhauses eine Chorleiterfortbildung durch. Da unser Chorleiter im erweiterten Vorstand dieses Verbandes t\xe4tig ist, hatte er diese Veranstaltung nach Weroth geholt und unser Chor diente dabei quasi als Studiochor und Versuchsobjekt f\xfcr die weiteren teilnehmenden Chorleiter. Als Dozent konnte der uns schon aus Bingen bestens bekannte Prof. Volker Hempfling gewonnen werden. Und es war schon ein beeindruckendes Ereignis, einem der profiliertesten Chorleiter und Musiklehrer der deutschen Chorszene bei der Arbeit zuzuschauen und von ihm zu lernen. Einige sehr n\xfctzliche \xdcbungen beim Singen, besonders durch k\xf6rperliche Bewegungen, brachten eine erstaunliche Verbesserung in unserem Chorklang und unserer Intonation. Der Nachmittag brachte uns viele neue Erkenntnisse, die wir in unseren Chorproben sinnvoll n\xfctzen k\xf6nnen. Doch auch Prof. Hempfling machte die Veranstaltung sichtlich Spa\xdf und lobte zum Ende die gute Zusammenarbeit mit unserem Chor und war besonders vom hohen Leitungsverm\xf6gen beeindruckt. Schade war nur, dass lediglich 3 Chorleiter an dieser Fortbildung teilgenommen hatten."}),"\n",(0,s.jsx)(n.h2,{id:"14-november---konzert-mgv-liederkranz-berod",children:"14. November - Konzert MGV \u201eLiederkranz\u201c Berod"}),"\n",(0,s.jsx)(n.p,{children:"In ihrer Konzertreihe \u201e Der MGV \u201eLiederkranz\u201c trifft Freunde\u201c veranstalteten die Ber\xf6der S\xe4nger zum zweiten Mal ein Konzert in der sehr anspruchsvoll dekorierten Sport- und Kulturhalle der Verbandsgemeinde in Wallmerod und wir waren der Einladung zur Mitwirkung nat\xfcrlich gerne nachgekommen. Als weitere Mitwirkende traten hier noch der \u201eJunge Chor Wallmerod\u201c und der Pop- und Jazzchor YOU 'N' JOY Lindenholzhausen, einer der vielem Ch\xf6re der \u201eC\xe4cilia\u201c Familie des Chorleiters Matthias Schmidt auf. Dementsprechend war nat\xfcrlich das gesamte Programm eher auf moderne Unterhaltungsliteratur ausgelegt und bestand haupts\xe4chlich aus Bearbeitungen von Pop-, Swing- und Jazzsongs. Wir fielen da mit unserer eher klassischen M\xe4nnerchorliteratur wohl etwas aus dem Programmrahmen, aber aus Termingr\xfcnden konnten wir speziell f\xfcr dieses Konzert keine modernen St\xfccke ein\xfcben. Doch dem Publikum schien auch dies sehr zu gefallen, was der langanhaltende Applaus am Ende unserer Vortr\xe4ge bewies. Aber auch die anderen Ch\xf6re boten durchweg eine sehr gute Leistung sodass diese gut besuchte Veranstaltung insgesamt gesehen sehr gelungen war. Und da ja auch unsere Freunde aus Zehnhausen und Berod auch sehr gerne feiern, wurde noch bis sp\xe4t in die Nacht bei Speis und Trank gemeinsam musiziert."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohlauf, ihr G\xe4ste v. Erasmus Widmann"}),"\n",(0,s.jsx)(n.li,{children:"Der Tambour v. Hugo Distler"}),"\n",(0,s.jsx)(n.li,{children:"Veno v. Bedrich Smetana"}),"\n",(0,s.jsx)(n.li,{children:"Ermunterung v. Herbert Wallas"}),"\n",(0,s.jsx)(n.li,{children:"Schilflieder v. Heinrich Sutermeister"}),"\n",(0,s.jsx)(n.li,{children:"What shall we do with the drunken sailor v. Parker/Shaw"}),"\n",(0,s.jsx)(n.li,{children:"Steal away v. Jens R\xf6th"}),"\n",(0,s.jsx)(n.li,{children:"Als Zugabe: Im Feuersturm der Reben v. Jens R\xf6th"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"15-november---gedenkfeier-der-ortsgemeinde-am-ehrenmal",children:"15. November - Gedenkfeier der Ortsgemeinde am Ehrenmal"}),"\n",(0,s.jsx)(n.p,{children:"Schon am n\xe4chsten Morgen, dem 15. November, fand die Gedenkfeier der Ortsgemeinde f\xfcr die Gefallenen und Vermissten der beiden Weltkriege am Ehrenmal statt. Unter Leitung unseres Vizes Christoph Weimer umrahmten wir die Andacht mit zwei geistlichen Ges\xe4ngen."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Wohin soll ich mich wenden v. Franz Schubert"}),"\n",(0,s.jsx)(n.li,{children:"Forschen nach Gott v. Konradin Kreutzer"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"28-november---75-geburtstag-von-hans-kaiser",children:"28. November - 75. Geburtstag von Hans Kaiser"}),"\n",(0,s.jsx)(n.p,{children:"Ein weiteres St\xe4ndchen fand am 28. November statt. Unser aktives Ehrenmitglied feierte an diesem Tag seinen 75. Geburtstag und wir gratulierten ihm dazu mit ein paar Liedern. Johannes Hannappel hob in seiner Dankesrede vor allem Hans Kaisers Treue zum Chor und die Loyalit\xe4t gegen\xfcber dem Vorstand hervor. Der anschlie\xdfenden Einladung zum Umtrunk in unserem Vereinslokal kamen wir nat\xfcrlich gerne und auch ausgiebig nach."}),"\n",(0,s.jsx)(n.p,{children:"Zum Vortrag kamen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Unter der Linde v. Konradin Kreutzer"}),"\n",(0,s.jsx)(n.li,{children:"Der Spielmann v. Arnold Kempkens"}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"12-dezember---jahresabschlussfeier-im-b\xfcrgerhaus",children:"12. Dezember - Jahresabschlussfeier im B\xfcrgerhaus"}),"\n",(0,s.jsx)(n.p,{children:"Den Abschluss des Jahres 2009 bildete eine Feier in unserem B\xfcrgerhaus, zu der alle Mitglieder mit deren Angeh\xf6rigen sowie die Witwen unserer verstorbenen Mitglieder eingeladen waren. Johannes Hannappel konnte an diesem Abend fast 90 G\xe4ste begr\xfc\xdfen und \xfcbergab nach dem offiziellen Teil auch gleich das Wort an den eigentlichen Chef des Abends, unseren Koch Leo Weyand. Er und seine Helfer Walter Fasel, Alex Hustiak, Torsten Meuer, Andreas Malm und Paul Weimer hatten ein B\xfcfett zusammengestellt, das es so wohl noch nicht in Weroth gegeben hatte. \xdcber Lachs, luftgetrockneten Schinken, Krustenbraten, Ente verschiedene Nudelpfannen und leckerem Nachtisch gab es alles was das Genie\xdferherz begehrte. Neben unserem genialen Kochteam gilt es aber auch unserer Kirmesgesellschaft zu danken, die uns an diesem Abend sehr gut bewirtete."}),"\n",(0,s.jsx)(n.h2,{id:"was-passiert-2009-sonst-noch-",children:"Was passiert 2009 sonst noch ..."}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"09. Januar"})," - Jahreshauptversammlung"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"31. Juli"})," - Thekendienst Sportwoche Spielvereinigung"]}),"\n"]})]})}function o(e={}){const{wrapper:n}={...(0,t.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(u,{...e})}):u(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const s={},t=i.createContext(s);function a(e){const n=i.useContext(t);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:a(e.components),i.createElement(t.Provider,{value:n},e.children)}}}]);