"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[7863],{1311:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>d,contentTitle:()=>a,default:()=>o,frontMatter:()=>h,metadata:()=>i,toc:()=>u});const i=JSON.parse('{"id":"archiv/2000-2009/2001","title":"2001","description":"06. Januar - Jahreshauptversammlung mit Neuwahlen","source":"@site/docs/archiv/2000-2009/2001.md","sourceDirName":"archiv/2000-2009","slug":"/archiv/2000-2009/2001","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2001","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2000-2009/2001.md","tags":[],"version":"current","sidebarPosition":9,"frontMatter":{"sidebar_position":9},"sidebar":"archivSidebar","previous":{"title":"2002","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2002"},"next":{"title":"2000","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2000"}}');var t=r(4848),s=r(8453);const h={sidebar_position:9},a="2001",d={},u=[{value:"06. Januar - Jahreshauptversammlung mit Neuwahlen",id:"06-januar---jahreshauptversammlung-mit-neuwahlen",level:2},{value:"24. M\xe4rz - Konzert mit dem \u201eCanticum\u201c Weilburg",id:"24-m\xe4rz---konzert-mit-dem-canticum-weilburg",level:2},{value:"04. April - Tod unseres Aktiven Erwin H\xf6hn",id:"04-april---tod-unseres-aktiven-erwin-h\xf6hn",level:2},{value:"10. Mai - Goldene Hochzeit unseres Aktiven Hans Kronenberg",id:"10-mai---goldene-hochzeit-unseres-aktiven-hans-kronenberg",level:2},{value:"18. August - Scheunenfest",id:"18-august---scheunenfest",level:2},{value:"01. September - Konzertbesuch zum 100j\xe4hrigen des MGV \u201eFrohsinn\u201c in Steinefrenz",id:"01-september---konzertbesuch-zum-100j\xe4hrigen-des-mgv-frohsinn-in-steinefrenz",level:2},{value:"02. September - Gruppensingen in Steinefrenz",id:"02-september---gruppensingen-in-steinefrenz",level:2},{value:"05. September - Tod unseres Aktiven Reinhold H\xf6hn",id:"05-september---tod-unseres-aktiven-reinhold-h\xf6hn",level:2},{value:"31. Oktober - Gesangstunde",id:"31-oktober---gesangstunde",level:2},{value:"14. November - Gesangstunde",id:"14-november---gesangstunde",level:2},{value:"Was passiert 2001 sonst noch ...",id:"was-passiert-2001-sonst-noch-",level:2}];function l(e){const n={br:"br",h1:"h1",h2:"h2",header:"header",li:"li",p:"p",strong:"strong",ul:"ul",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"2001",children:"2001"})}),"\n",(0,t.jsx)(n.h2,{id:"06-januar---jahreshauptversammlung-mit-neuwahlen",children:"06. Januar - Jahreshauptversammlung mit Neuwahlen"}),"\n",(0,t.jsx)(n.p,{children:"Die Versammlung verl\xe4uft in harmonischen Bahnen und wird in erster Linie durch die R\xfcckschau auf das verflossene Festjahr 2000 gepr\xe4gt.\nDer 1. Vorsitzende Herbert Malm zieht eine \xfcberaus positive Bilanz der Festveranstaltungen und kann sich insbesondere dar\xfcber freuen, da\xdf die \u201eFrohe Stunde\u201c trotz aller Festaktivit\xe4ten ihren musikalischen Stellenwert gefestigt und weiter ausgebaut hat.\nAuch Kassierer Frank Reusch kann \u2013 Dank der \u201efestlichen Einnahmen\u201c \u2013 beruhigt in die Zukunft schauen.\nStellvertretend f\xfcr die Ortsgemeinde Weroth bedankt sich der I. Beigeordnete Hermann Kleppel f\xfcr das erfolgreiche Festjahr, durch da\xdf auch die Gemeinde Weroth einmal mehr positiv in den Blickpunkt einer breiten \xd6ffentlichkeit ger\xfcckt ist."}),"\n",(0,t.jsx)(n.p,{children:"Vor den Neuwahlen bedankt sich Herbert Malm bei den ausscheidenden Vorstandsmitgliedern Frank Reusch, Thomas Malm, Rudi Pehl und Hans Giesen. Er hebt besonders die Arbeit von Thomas Malm hervor, der dem Vorstand seit 25 Jahren angeh\xf6rt und in Krisenzeiten auch als 1 Vorsitzender das Vereinsschiff um einige gef\xe4hrliche Klippen herum man\xf6vriert hat.\nNach der sich anschlie\xdfenden Wahl unter der Leitung von Helmut Schwarz setzt sich der Vorstand k\xfcnftig so zusammen:"}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsx)(n.li,{children:"1.Vorsitzender: Herbert Malm"}),"\n",(0,t.jsx)(n.li,{children:"2.Vorsitzender: Hans-Georg Jung"}),"\n",(0,t.jsx)(n.li,{children:"Kassierer: Peter Duchscherer"}),"\n",(0,t.jsx)(n.li,{children:"Stellv. Kassierer: Eduard Hannappel"}),"\n",(0,t.jsx)(n.li,{children:"Gesch\xe4ftsf\xfchrer: Bernd-Josef Metternich"}),"\n",(0,t.jsx)(n.li,{children:"Stellv. Gesch.f\xfchrer: Johannes Hannappel"}),"\n",(0,t.jsx)(n.li,{children:"Notenwart: Markus Fein"}),"\n",(0,t.jsx)(n.li,{children:"Stellv. Notenwart: Gerhard Weimer"}),"\n",(0,t.jsx)(n.li,{children:"Beisitzer: Alois Sch\xf6nberger, Gerold Haselsteiner, Franz Klein, Bruno Heep"}),"\n"]}),"\n",(0,t.jsx)(n.p,{children:"Angesichts seiner seit Jahren bew\xe4hrten und souver\xe4nen Wahlleitung, erh\xe4lt Helmut Schwarz nicht nur den spontanen Beifall der Versammlung, sondern gegen Abnahme des Versprechens, auch bei der n\xe4chsten Wahl wieder als Wahlleiter zur Verf\xfcgung zu stehen, ein Original \u201eFrohe Stunde\u201c Schreibset als Geschenk."}),"\n",(0,t.jsx)(n.h2,{id:"24-m\xe4rz---konzert-mit-dem-canticum-weilburg",children:"24. M\xe4rz - Konzert mit dem \u201eCanticum\u201c Weilburg"}),"\n",(0,t.jsx)(n.p,{children:"Ihren ersten \xf6ffentlichen Auftritt im Jahr 2001 hat die \u201eFrohe Stunde\u201c am 24. M\xe4rz in der Stadthalle Weilburg. Eingeladen hat der gemischte Chor \u201eCanticum\u201c Weilburg. Ebenfalls unter der Stabf\xfchrung von Jens R\xf6th singen im \u201eCanticum\u201c sieben M\xe4nner der \u201eFrohen Stunde\u201c ( Thomas Malm, Christoph Malm, Johannes Hannappel, Christoph Weimer, Bernd-Josef Metternich, Andreas Malm und Paul Weimer) und weitere sechs S\xe4ngerinnen ( Maria Malm, Sabine Malm, Sabine Hannappel und Andrea Jung) und S\xe4nger ( Reinhard Reimann, G\xfcnther Kniehl) aus Weroth und Umgebung. Insgesamt hat der Chor ca. 35 S\xe4ngerinnen und S\xe4nger.\nBei diesem insgesamt sehr gelungenen und anspruchsvollen Chor- und Solistenkonzert wei\xdf sich die \u201eFrohe Stunde\u201c durch ihren vielger\xfchmten homogenen Chorklang in Szene zu setzen.\n\u201eGr\xfcndlich durchdachte Interpretationen und intensives Hineinversenken in die Texte der einzelnen Chorwerke machten das Zuh\xf6ren zum Genu\xdf\u201c urteilt die Nassauische Neue Presse. \u201eHier zahlt sich st\xe4ndige Chorschulung aus. Da wird auch g\xe4ngige M\xe4nnerchorliteratur wieder interessant und h\xf6renswert und vom Publikum geforderte Zugaben werden dann zur Regel.\u201c\nGanz und gar nicht zur g\xe4ngigen M\xe4nnerchorliteratur geh\xf6rt allerdings das an diesem Abend von der \u201eFrohen Stunde\u201c uraufgef\xfchrte Chorwerk \u201eVeit Sto\xdf\u201c von Klaus Ochs. Trotz einiger Probleme beim Einsingen \u2013 die m\xf6glicherweise auch daraus resultieren, das zun\xe4chst keine Noten verf\xfcgbar sind \u2013 gelingt die Premiere so gut, da\xdf sich die mitgereisten \u201eFans\u201c sehr angetan zeigen. Das St\xfcck \u2013 erstmalig mit ( von Thomas Malm) gesprochenen Rezitativen \u2013 hat offenbar eine sehr eindringliche Wirkung auf den Zuh\xf6rer und wird uns daher hoffentlich noch manchen Publikumserfolg bescheren."}),"\n",(0,t.jsx)(n.h2,{id:"04-april---tod-unseres-aktiven-erwin-h\xf6hn",children:"04. April - Tod unseres Aktiven Erwin H\xf6hn"}),"\n",(0,t.jsx)(n.p,{children:"Mit einer traurigen Nachricht begr\xfc\xdft uns der aus dreiw\xf6chigem Kuraufenthalt zur\xfcck gekehrte Herbert Malm zur Gesangstunde. In der Nacht vom 03.04 zum 04.04. ist unser aktiver S\xe4nger im 2. Ba\xdf, Erwin H\xf6hn, einem nur recht kurze Zeit w\xe4hrenden Krebsleiden erlegen.\nDie S\xe4nger ehren ihn mit einer Gedenkminute zu Beginn der Gesangstunde und nehmen bei der Beerdigung in gewohnter Weise von ihrem Sangesbruder Abschied.!"}),"\n",(0,t.jsx)(n.h2,{id:"10-mai---goldene-hochzeit-unseres-aktiven-hans-kronenberg",children:"10. Mai - Goldene Hochzeit unseres Aktiven Hans Kronenberg"}),"\n",(0,t.jsx)(n.p,{children:"Mit dem traditionellen Fackelzug der Gemeinde und der Vereine begeht auch die Frohe Stunde den Tag der Goldenen Hochzeit im Hause Kronenberg, Waldstr. 10. Bei herrlichem Fr\xfchlingswetter beteiligen sich die S\xe4nger an der kleinen Feier vor dem Haus der Jubilare und beim anschlie\xdfenden Umtrunk im Vereinslokal. F\xfcr viele S\xe4nger ist es seit Jahren wieder das erste Mal, da\xdf sie am Donnerstag vor der Kirmes in der Gastwirtschaft sind. An diesem Tag findet nach alter Tradition die Kirmesburschenversammlung statt, bei der zun\xe4chst der Vort\xe4nzer ausgestochen wird und sich die Mitglieder der Gesellschaft dann - nach meist erheblichem Alkohol (insbesondere Wacholder-) konsum \u2013 zum Haus der Angebeteten begeben, um sie f\xfcr die Kirmes zu bestellen. Auch am 10. Mai des Jahres 2001 haben sich die Kirmesburschen eingefunden und unter den Augen ihrer Vorg\xe4nger erhebliche M\xfche, ihren Mageninhalt bei sich zu behalten.\nAuch Anla\xdf genug f\xfcr die Exkirmesburschen und jetzigen S\xe4nger die eigenen Erlebnisse aus dieser Zeit aufzu frischen und eifrig zu diskutieren. Recht schnell hat sich auch der Jubilar eingefunden, um im Kreise seiner Sangesbr\xfcder noch einige sch\xf6ne Stunden zu verbringen."}),"\n",(0,t.jsx)(n.h1,{id:"27-mai---wettstreit-in-oberndorf",children:"27. Mai - Wettstreit in Oberndorf"}),"\n",(0,t.jsx)(n.p,{children:"Stark dezimiert \u2013 von noch 48 Aktiven k\xf6nnen nur 40 mitfahren ( Gerhard Weimer bspw. hat sich freitags zuvor einen Lendenwirbel gebrochen und mit Josef und Klaus Hannappel fehlen damit bereits in der I. Reihe drei Mann) \u2013 fahren die S\xe4nger um 09.00 Uhr ab Vereinslokal in Richtung Spessart. Buschauffeur ist \u2013 wie immer in den letzten Jahren \u2013 Klaus Schwarz. Angef\xfchrt wird die Truppe vom 2. Vorsitzenden Hans-Georg Jung, der den urlaubsbedingt abwesenden Herbert Malm vertritt.\nAustragungsort des Wettstreites ist Jossgrund/Oberndorf im hessischen Spessart, ca. 15 km s\xfcd\xf6stlich von Bad Orb gelegen. Am Morgen ist es leicht bew\xf6lkt bei sommerlich angenehmen Temperaturen.\nDer Saal des B\xfcrgerhauses, dessen Akustik durch viele Vorh\xe4nge den Vortragenden nicht gerade entgegen kommt, ist gut gef\xfcllt, als die S\xe4nger der \u201eFrohen Stunde\u201c in der Gruppe M 3 als letzter Chor \xfcber eine Au\xdfentreppe die B\xfchne betreten.\nMit \u201eVeit Sto\xdf\u201c von Klaus Ochs kommt erstmals ein Chorwerk bereits nach sieben Monaten Probezeit auf eine Wettstreitb\xfchne. Das St\xfcck ist gemessen an der Auff\xfchrungsdauer gleichzeitig das bisher l\xe4ngste von der \u201eFrohen Stunde\u201c vorgetragene a capella Werk.\nWertungsrichter Michael Rinscheid h\xf6rt einen \xfcberzeugenden Vortrag. Auf Grund der Tatsache, da\xdf es unserem Chorleiter erst in der ersten und einzigen Sonderprobe am vergangenen Freitag gelang, kleine Intonationsprobleme bei den Eins\xe4tzen der einzelnen Stimmen ab zu stellen. konnte man die Steine nach dem St\xfcck f\xf6rmlich von den Herzen der S\xe4nger, aber auch des Chorleiters plumpsen h\xf6ren.\nVielleicht plumpsten sie hie und da etwas tief, den das folgende \u201eRosemarie\u201c hatte man schon besser dargeboten. Dennoch sind die S\xe4nger mit dem Auftritt insgesamt \xfcberaus zufrieden und verlassen entsprechend selbstbewu\xdft die B\xfchne.\nDas Ergebnis gibt ihnen schlie\xdflich recht. \u201eWer weite Wege auf sich nimmt, hat in der Regel etwas zu bieten. So verhielt es sich mit dem starken M\xe4nnerchor \u201eFrohe Stunde\u201c Weroth ( Jens R\xf6th ) in der Klasse 3 b\u201c schreibt die \u201eGelnh\xe4user Neue Zeitung\u201c in ihrer Ausgabe vom 29.05.01."}),"\n",(0,t.jsx)(n.p,{children:"Der 1. Platz in der dritten Klasse ( a und b) ist Lohn der intensiven, aber nat\xfcrlich recht einseitig von \u201eVeit Sto\xdf\u201c gepr\xe4gten, Probenarbeit der letzten Wochen. Mit 58 Punkten beim Chor liegt die \u201eFrohe Stunde\u201c punktgleich mit den beiden M\xe4nnerch\xf6ren der Klasse M 1 \u201eLiederkranz\u201c Zellhausen (Chorleiter Roman Z\xf6ller) und M\xe4nnerchor \u201e1891\u201c Somborn e.V. (Dr. Martin Trageser). Bezogen auf den gesamten Wettbewerb teilt sich die \u201eFrohe Stunde\u201c in der Gesamtabrechnung den 3. Platz mit dem Sieger der Klasse G 1, dem gemischten Chor GV \u201e1884 C\xe4cilia\u201c Somborn (Matthias Herr). Beide Vereine erreichen eine Gesamtpunktzahl von 115 Punkten. Damit lassen die S\xe4nger aus Weroth auch die zahlenm\xe4\xdfig st\xe4rkeren Vereine der M\xe4nnerchorklasse M 2 (GV \u201eHarmonie\u201c Kassel, Dr. Martin Trageser, 113 Pkt.; GV \u201eConcordia\u201c M\xfcnchholzhausen, Matthias Schmitt, 114 Pkt.) hinter sich."}),"\n",(0,t.jsx)(n.p,{children:"Der Erfolg wird traditionsgem\xe4\xdf gefeiert und findet seinen Abschlu\xdf, nach der Begr\xfc\xdfung in Weroth, im Vereinslokal.\nErstmalig werden die Erlebnisse auch im Bild festgehalten. Hermann Kleppel und die als weiblicher \u201eBeobachter\u201c mitgereiste Mariele Haselsteiner machen etliche Schnappsch\xfcsse mit der von MGV und Gemeinde gemeinsam angeschafften Digitalkamera. Der ortsans\xe4ssige Verein \u201eS\xe4ngerlust\u201c Oberndorf hat dar\xfcber hinaus Videos der Auftritte gedreht, so das erstmalig auch der Wettstreitauftritt selbst in bewegten Bildern der Nachwelt erhalten ist.\nDrei S\xe4nger ( Christoph und Andreas Malm sowie Johannes Hannappel ), die von Oberndorf noch zu einer Familienfeier ins Sauerland gefahren waren, finden am Abend in Weroth eine gl\xfcckliche und zufriedene \u201eFrohe Stunde\u201c vor, die sich nun wieder einmal neuem Liedgut widmen kann. Das gr\xf6\xdfte Lob, so erfahren Sie, kam diesmal wohl von den konkurrierenden S\xe4ngern der anderen Vereine: \u201e Ihr habt nicht nur gesungen, ihr habt musiziert und ward die eigentlichen Gewinner des heutigen Tages!\u201c \u2013\nEin Lob kommt auch vom Wertungsrichter, der in die Partitur des Veit Sto\xdf hineinschreibt: \u201e Chorleiter und Chor \u2013 hervorragend!\u201c"}),"\n",(0,t.jsx)(n.p,{children:(0,t.jsx)(n.strong,{children:"Klasse M 3b"})}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:['MGV "Frohe Stunde" Weroth',(0,t.jsx)(n.br,{}),"\n","Gemeldete S\xe4ngerzahl: 49, Chorleiter: Jens R\xf6th",(0,t.jsx)(n.br,{}),"\n","Angetreten mit 40 S\xe4ngern","\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsx)(n.li,{children:"Lied A: Veit Sto\xdf v. Klaus Ochs"}),"\n",(0,t.jsx)(n.li,{children:"Lied B: Rose Marie v. Rudi K\xfchn"}),"\n"]}),"\n"]}),"\n",(0,t.jsxs)(n.li,{children:["Konkurrenz:","\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:["S\xe4ngervereinigung Niederroden",(0,t.jsx)(n.br,{}),"\n","45 S\xe4nger, Chorleiter: Gottfried K\xe4rner"]}),"\n",(0,t.jsxs)(n.li,{children:["GV \u201eLiederkranz\u201c Kassel",(0,t.jsx)(n.br,{}),"\n","40 S\xe4nger, Chorleiter: Matthias Habermann"]}),"\n"]}),"\n"]}),"\n"]}),"\n",(0,t.jsx)(n.h2,{id:"18-august---scheunenfest",children:"18. August - Scheunenfest"}),"\n",(0,t.jsx)(n.p,{children:"Nach einer ausgiebigen Sommerpause l\xe4dt die \u201eFrohe Stunde\u201c nach vier Jahren zum zweiten Scheunenfest. Wie damals findet die Veranstaltung auch in diesem Jahr in der Feldscheune von \u201eDorisse und Hangerde\u201c am Weg zum Friedhof statt. Der Vorstand hat das Geb\xe4ude in der Woche zuvor einer Grundreinigung unterzogen und f\xfcr den Anla\xdf stilecht mit Heuballen, Laub und Lampingons im Au\xdfenbereich dekoriert. F\xfcr den Verzehr sind Erbelskrebbelscher mit Eppelmork, und Hefekuchen (sogar in doppelter Menge), dick Sopp und Werschtscher bestellt. Die Kinder finden eine H\xfcpfburg und k\xf6nnen auf den Ponys des Reitstalles \u201eAm Buch\u201c reiten.\nTrotz toller Angebote und optimaler Witterung findet das Fest nur m\xe4\xdfigen Besucherandrang. So macht sich zun\xe4chst etwas Entt\xe4uschung breit. Diejenigen aber, die der Einladung gefolgt sind, finden recht schnell die rechte Einstellung zur Situation und freuen sich schlie\xdflich \xfcber die gute Resonanz in Kreisen von denen man es nicht erwartet hatte, allen voran der Kirmesgesellschaft. Diese ist ziemlich komplett angetreten und nutzt das Scheunenfest zum Aufw\xe4rmtraining f\xfcr die am gleichen Wochenende stattfindende Hunds\xe4nger Kirmes."}),"\n",(0,t.jsx)(n.h2,{id:"01-september---konzertbesuch-zum-100j\xe4hrigen-des-mgv-frohsinn-in-steinefrenz",children:"01. September - Konzertbesuch zum 100j\xe4hrigen des MGV \u201eFrohsinn\u201c in Steinefrenz"}),"\n",(0,t.jsx)(n.p,{children:"Zur Gestaltung des Festkonzertes am Festwochenende anl\xe4\xdflich des 100j\xe4hrigen Bestehens hat der MGV \u201eFrohsinn\u201c Steinefrenz die Ch\xf6re aus der Nachbarschaft und den Kirchenchor \u201eSt. Matthias\u201c Steinefrenz/Weroth eingeladen. Neben der \u201eFrohen Stunde\u201c sind die M\xe4nnergesangvereine aus Berod und Girod mit von der Partie.\nDa unser Chorleiter seine Chorprobe in Frickhofen wegen bevorstehender Wettbewerbe nicht ausfallen lassen kann, ist er erst nach 21.30 Uhr verf\xfcgbar. Anders als die anderen beteiligten Ch\xf6re haben die S\xe4nger der \u201eFrohen Stunde\u201c damit nur einen Auftritt. Es wird ein voller Erfolg. Obwohl mit dem \u201eDrunken sailor\u201c f\xfcr den Chor eine Urauff\xfchrung auf dem Programm steht und auch der \u201eVeit Sto\xdf\u201c erstmals vor heimischen Publikum erklingen soll, ist .- anders als in fr\xfcheren Zeiten bei \xe4hnlichen Anl\xe4ssen \u2013 keine Spur von Nervosit\xe4t erkennbar.\nSouver\xe4n er\xf6ffnen die M\xe4nner um Jens R\xf6th ihren Auftritt mit den \u201eMin(ni)nes\xe4ngern\u201c und setzen einen ersten Kontrapunkt mit dem Gospel \u201eLieber Herrgott, h\xf6re mein Rufen\u201c.\nFasziniert lauscht das fachkundige Publikum dann dem Chorwerk \u201eVeit Sto\xdf\u201c von Klaus Ochs. Hier scheint f\xfcr den Chor nun endg\xfcltig \u201eder Knoten geplatzt zu sein.\u201c Ohne den Anflug einer Unsicherheit ist dieses St\xfcck nun ein \u201eHighlight\u201c im Repertoire der \u201eFrohen Stunde\u201c. Die zun\xe4chst f\xfcr\xb4s Publikum ungewohnten Harmonien (Cluster), die deklamatorischen Einw\xfcrfe eines Sprechers (Thomas Malm) und der sporadisch eingeworfene Sprechgesang verschmelzen zu einem v\xf6llig neuen, unkonventionellen Musikerleben. Die \u201eFan\xb4s\u201c zeigen sich im Anschlu\xdf an die Veranstaltung restlos begeistert. \u201eDieses St\xfcck ist einfach nur genial\u201c, so die vielfach ge\xe4u\xdferte Meinung. Es beweist im \xdcbrigen, da\xdf die \u201eFrohe Stunde\u201c ein neues Ma\xdf an Flexibilit\xe4t erreicht hat und durchaus in der Lage ist, auch zeitgen\xf6ssische Kompositionen \xfcberzeugend darzubieten.\nNicht viel anders ergeht es mit dem uraufgef\xfchrten \u201eDrunken Sailor\u201c. \u201eEin echter Rei\xdfer\u201c, sind sich die Fachleute einig. Wenn auch m\xf6glicherweise hie und da f\xfcr Insider noch einige Ungereimtheiten aufgetreten sein m\xf6gen, so ist das Publikum auch von diesem St\xfcck entz\xfcckt und der Chor hat sich f\xfcr das \u201eSoon ah will be done \u201e einen w\xfcrdigen Nachfolger erarbeitet.\nMit der \u201eRosemarie\u201c und dem \u201eSpielmann\u201c als Zugabe endet ein denkw\xfcrdiger Auftritt, der die \u201eOchsentour\u201c der vergangenen Wochen vergessen l\xe4\xdft. \xdcberschwenglich ist auch die Reaktion der S\xe4nger, die, im Bewu\xdftsein der gelungenen Darbietung, nach Ende des offiziellen Teils auch im gem\xfctlichen Teil zu \xfcberzeugen wissen. So gehen sie auch aus der \u201eNachspielzeit\u201c inoffiziell als Sieger hervor und sind \u2013 wie so oft - \u201eals Erste auch die Letzten (......die den Weg nach Hause finden).\u201c"}),"\n",(0,t.jsx)(n.h2,{id:"02-september---gruppensingen-in-steinefrenz",children:"02. September - Gruppensingen in Steinefrenz"}),"\n",(0,t.jsx)(n.p,{children:"Mit den bereits freitags hochgelobten St\xfccken \u201eVeit Sto\xdf\u201c und \u201eDrunken sailor\u201c bestreitet die \u201eFrohe Stunde\u201c auch das in die Feierlichkeiten zum 100j\xe4hrigen Bestehen des MGV \u201eFrohsinn\u201c Steinefrenz eingebettete Gruppensingen. Wie in den Jahren zuvor ist die Veranstaltung als Kritiksingen ausgestaltet. Als musikalischen Fachmann hat die S\xe4ngergruppe III Herrn Werner Tillmann gewinnen k\xf6nnen, der sich bestens vorbereitet zeigt und bei der anschlie\xdfenden \u201eMan\xf6verkritik\u201c detailliert zu den einzelnen Vortr\xe4gen Stellung bezieht. Nicht so bei der \u201eFrohen Stunde\u201c.\nNachdem ein im Wesentlichen gleicherma\xdfen \xfcberzeugender Auftritt, wie zwei Tage zuvor, gelungen ist, zeigt sich Herr Tillmann einigerma\xdfen ratlos. \u201eEr habe wohl das Ein oder Andere in die Partituren hineingeschrieben. Dies seien jedoch im Wesentlichen geschmackliche Unterschiede hinsichtlich der Auffassung. Ansonsten bliebe ihm nur zu sagen, einfach ausgezeichnet, vorz\xfcglich, hervorragend. Der Chor und sein Chorleiter musizieren auf oberem Niveau !\nEine Best\xe4tigung der gelungenen Probenarbeit aus berufenem Munde."}),"\n",(0,t.jsx)(n.h2,{id:"05-september---tod-unseres-aktiven-reinhold-h\xf6hn",children:"05. September - Tod unseres Aktiven Reinhold H\xf6hn"}),"\n",(0,t.jsxs)(n.p,{children:["Noch w\xe4hrend Herbert Malm in der folgenden Gesangstunde das gelungene Mitwirken beim Fest in Steinefrenz Revue passieren l\xe4\xdft, wird er \u2013 g\xe4nzlich ungew\xf6hnlich \u2013 zum Telefon gerufen. Die Nachricht mit der er schlie\xdflich zur\xfcckkehrt, l\xe4\xdft jede Euphorie verfliegen und f\xe4hrt wie ein Blitz in die Reihen der S\xe4nger.\nUnser Aktiver Reinhold H\xf6hn ist tot !\nNachdem er am Nachmittag pl\xf6tzlich ins Krankenhaus gebracht worden war, hatten die \xc4rzte sich zu einer Notoperation entschlie\xdfen m\xfcssen. Sein Herz war dieser Anstrengung offenbar nicht mehr gewachsen, da er in j\xfcngster Vergangenheit schon diverse Operationen wegen seiner H\xfcftbeschwerden \xfcber sich hatte ergehen lassen m\xfcssen. So konnte er letztmals bei der goldenen Hochzeit der Eheleute Alfred und Resi Eidt aktiv mitsingen.\nBei der Beerdigung am folgenden Samstag dem 08.09.01 nehmen die S\xe4nger Abschied und begleiten den letzten Weg ihres Sangesbruders Reinhold H\xf6hn unter dem Dirigat von Vizechorleiter Christoph Weimer mit dem \u201eWohin soll ich mich wenden\u201c aus der deutschen Festmesse und einem neu einstudierten Grablied \u201eHarre auf Gott\u201c von ",(0,t.jsx)(n.strong,{children:"**"}),"_",(0,t.jsx)(n.strong,{children:"**"})," .\n( Bei einem ersten Ansingen am Freitag vor der Beerdigung fehlten die ersten beiden Reihen des I. Ba\xdf komplett. Der Chor zeigte sich unter diesen Bedingungen au\xdferstande, einen reibungslosen Vortrag zu gew\xe4hrleisten, so da\xdf f\xfcr den Tag der Beerdigung unmittelbar vorher ein erneutes Ansingen vereinbart werden mu\xdfte, zu dem alle fehlenden S\xe4nger noch einmal pers\xf6nlich aktiviert wurden !!!)"]}),"\n",(0,t.jsx)(n.h2,{id:"31-oktober---gesangstunde",children:"31. Oktober - Gesangstunde"}),"\n",(0,t.jsx)(n.p,{children:"Unser Chorleiter verr\xe4t erstmals musikalische Einzelheiten des Konzertes, da\xdf f\xfcr den 26.10.2002 geplant ist. Treu der selbst auferlegten Verpflichtung, den Freunden der \u201eFrohen Stunde\u201c alle zwei Jahre ein \u201eau\xdfergew\xf6hnliches\u201c Konzert in Weroth zu bieten, ist erstmals f\xfcr 2002 ein Abend mit Orchester geplant. Dazu kommen soll noch eine Solistin.\nDen Auftakt der Probenarbeit f\xfcr diese besondere Veranstaltung bildet am heutigen Abend das Werk von Theo Fischer \u201eMusik \u2013 Stimme der Allmacht\u201c Hymnische Szenen f\xfcr M\xe4nnerchor, Solostimme und Kammerorchester unter Verwendung von Originalkompositionen der Klassik und der Romantik. Nach dem ersten Eindruck scheint dieses Opus dem Chor zu liegen."}),"\n",(0,t.jsx)(n.h2,{id:"14-november---gesangstunde",children:"14. November - Gesangstunde"}),"\n",(0,t.jsx)(n.p,{children:"Jens R\xf6th wendet sich heute gleich mit zweierlei Anliegen an seine S\xe4nger."}),"\n",(0,t.jsx)(n.p,{children:"Zum Einen stellt er die Verh\xe4ltnisse \u201ein Sachen Dieter Graf\u201c aus seiner Sicht dar."}),"\n",(0,t.jsx)(n.p,{children:"Zum Anderen fragt er, ob die S\xe4nger bereit w\xe4ren, im n\xe4chsten Juni (2002) an einem Sonntag zwei Wettstreittermine wahrzunehmen. Dies w\xfcrde sich aus den verschiedensten \u201echorpolitischen\u201c Erw\xe4gungen heraus anbieten und erspare dem Chor einen zus\xe4tzlichen Termin.\nDa sich kein Widerspruch erhebt, ist dieses Vorgehen beschlossene Sache. Ob es sich in die Realit\xe4t umsetzen l\xe4\xdft, wird sich nach einem Deligiertentag am kommenden Sonntag und Absprachen des Chorleiters mit dem jeweiligen Veranstalter ergeben."}),"\n",(0,t.jsx)(n.h2,{id:"was-passiert-2001-sonst-noch-",children:"Was passiert 2001 sonst noch ..."}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"20. Oktober"})," - St\xe4ndchen zum 70. Geburtstag unseres Aktiven Rudi Pehl"]}),"\n"]})]})}function o(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(l,{...e})}):l(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>h,x:()=>a});var i=r(6540);const t={},s=i.createContext(t);function h(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:h(e.components),i.createElement(s.Provider,{value:n},e.children)}}}]);