"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[3869],{4256:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>o,frontMatter:()=>a,metadata:()=>i,toc:()=>u});const i=JSON.parse('{"id":"archiv/2000-2009/2003","title":"2003","description":"Die Frohe Stunde startet nach langer Abstinenz wieder die Mission \\"Meisterchor\\" und beschreitet die ersten beiden Stufen.","source":"@site/docs/archiv/2000-2009/2003.md","sourceDirName":"archiv/2000-2009","slug":"/archiv/2000-2009/2003","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2003","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2000-2009/2003.md","tags":[],"version":"current","sidebarPosition":7,"frontMatter":{"sidebar_position":7,"description":"Die Frohe Stunde startet nach langer Abstinenz wieder die Mission \\"Meisterchor\\" und beschreitet die ersten beiden Stufen."},"sidebar":"archivSidebar","previous":{"title":"2004","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2004"},"next":{"title":"2002","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2002"}}');var s=r(4848),t=r(8453);const a={sidebar_position:7,description:'Die Frohe Stunde startet nach langer Abstinenz wieder die Mission "Meisterchor" und beschreitet die ersten beiden Stufen.'},d="2003",h={},u=[{value:"04. Januar - Tod unseres passiven Mitglieds Giselbert Wagenbach",id:"04-januar---tod-unseres-passiven-mitglieds-giselbert-wagenbach",level:2},{value:"10. Januar - Jahreshauptversammlung",id:"10-januar---jahreshauptversammlung",level:2},{value:"25. Januar - Wandertag der Vereinsfamilie",id:"25-januar---wandertag-der-vereinsfamilie",level:2},{value:"26. Januar - Gottesdienst f\xfcr unser verstorbenes passives Mitglied Ewald Spreen",id:"26-januar---gottesdienst-f\xfcr-unser-verstorbenes-passives-mitglied-ewald-spreen",level:2},{value:"24. Februar - St\xe4ndchen zum 80. Geburtstag von Fritz Thierbach",id:"24-februar---st\xe4ndchen-zum-80-geburtstag-von-fritz-thierbach",level:2},{value:"22. M\xe4rz - St\xe4ndchen zum 75. Geburtstag von Josef Merfels",id:"22-m\xe4rz---st\xe4ndchen-zum-75-geburtstag-von-josef-merfels",level:2},{value:"05. April - Gottesdienst zur Goldenen Hochzeit von Alois und Josefine Fein",id:"05-april---gottesdienst-zur-goldenen-hochzeit-von-alois-und-josefine-fein",level:2},{value:"06. April - Kreisleistungssingen in Westerburg",id:"06-april---kreisleistungssingen-in-westerburg",level:2},{value:"10. April - Aktivenversammlung",id:"10-april---aktivenversammlung",level:2},{value:"12. April - Konzert beim MGV &quot;Konkordia&quot; Winnen in Gem\xfcnden",id:"12-april---konzert-beim-mgv-konkordia-winnen-in-gem\xfcnden",level:2},{value:"31. Mai - Volkliederwettbewerb in Gro\xdfholbach",id:"31-mai---volkliederwettbewerb-in-gro\xdfholbach",level:2},{value:"04. Juni - Tod unseres f\xf6rdernden Mitglieds Manfred Kremer",id:"04-juni---tod-unseres-f\xf6rdernden-mitglieds-manfred-kremer",level:2},{value:"22. Juni - Thekendienst beim 25-j\xe4hrigen Jubil\xe4um des Musikverein Hundsangen",id:"22-juni---thekendienst-beim-25-j\xe4hrigen-jubil\xe4um-des-musikverein-hundsangen",level:2},{value:"06. Juli - St\xe4ndchen zum 70. Geburtstag von Manfred Munsch",id:"06-juli---st\xe4ndchen-zum-70-geburtstag-von-manfred-munsch",level:2},{value:"12. Juli - St\xe4ndchen zur Silbernen Hochzeit von Christel und Heinz-Josef Kronimus",id:"12-juli---st\xe4ndchen-zur-silbernen-hochzeit-von-christel-und-heinz-josef-kronimus",level:2},{value:"13. Juli - Chorworkshop der S\xe4ngergruppe III in Ruppach-Goldhausen",id:"13-juli---chorworkshop-der-s\xe4ngergruppe-iii-in-ruppach-goldhausen",level:2},{value:"30. + 31. August - Chorfreizeit in Traben-Trabach",id:"30--31-august---chorfreizeit-in-traben-trabach",level:2},{value:"06. September - Konzert des &quot;Jungen Chores&quot; Wallmerod",id:"06-september---konzert-des-jungen-chores-wallmerod",level:2},{value:"03. Oktober - St\xe4ndchen zur Silbernen Hochzeit von Waltraut und Hermann Malm",id:"03-oktober---st\xe4ndchen-zur-silbernen-hochzeit-von-waltraut-und-hermann-malm",level:2},{value:"12. Oktober - Konzert &quot;Musik in St. Matthias&quot; in Steinefrenz",id:"12-oktober---konzert-musik-in-st-matthias-in-steinefrenz",level:2},{value:"15. November - Lindenfest",id:"15-november---lindenfest",level:2},{value:"16. November - Gedenken am Ehrenmal (Volkstrauertrag)",id:"16-november---gedenken-am-ehrenmal-volkstrauertrag",level:2},{value:"21. + 22. November - Thekendienst beim Musical der &quot;Red Flames&quot; in Weroth",id:"21--22-november---thekendienst-beim-musical-der-red-flames-in-weroth",level:2},{value:"14. Dezember - Seniorentag",id:"14-dezember---seniorentag",level:2},{value:"Was passiert 2003 sonst noch ...",id:"was-passiert-2003-sonst-noch-",level:2}];function l(e){const n={h1:"h1",h2:"h2",header:"header",li:"li",p:"p",strong:"strong",ul:"ul",...(0,t.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"2003",children:"2003"})}),"\n",(0,s.jsx)(n.h2,{id:"04-januar---tod-unseres-passiven-mitglieds-giselbert-wagenbach",children:"04. Januar - Tod unseres passiven Mitglieds Giselbert Wagenbach"}),"\n",(0,s.jsx)(n.p,{children:'Das vergangene Jahr begann f\xfcr den MGV anders als gewohnt, denn schon kurz nach dem Jahreswechsel hatten wir den ersten Trauerfall zu beklagen. Am 04. Januar war unser passives Mitglied Giselbert Wagenbach gestorben. Da Giselbert Wagenbach Mitglied in vielen anderen Vereinen war, kam man in Absprache mit den betreffenden Vereinen zu dem Schluss, bei der Beerdigung am 08.01. nicht zu singen. Die "Frohe Stunde" wurde an diesem Tag von ihrem Vorsitzenden Herbert Malm und seinem Stellvertreter Hans-Georg Jung vertreten.'}),"\n",(0,s.jsx)(n.h2,{id:"10-januar---jahreshauptversammlung",children:"10. Januar - Jahreshauptversammlung"}),"\n",(0,s.jsx)(n.p,{children:"Am 10. Januar fand dann die Jahreshauptversammlung statt. Nach der Begr\xfc\xdfung der 31 aktiven und 4 passiven Mitglieder durch unseren Vorsitzenden, den Berichten des Gesch\xe4ftsf\xfchrers und Kassierers, der Entlastung des Vorstandes durch die Versammlung stand als Tagesordnungspunkt 9 eine Neuregelung bei Gratulationen und Ehrungen an. Mit 33 Ja-Stimmen und 2 Enthaltungen wurde folgender Regelung zugestimmt."}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Ab dem 70. Geburtstag eines passiven Mitgliedes erfolgt eine Gratulation durch den Vorstand"}),"\n",(0,s.jsx)(n.li,{children:"Ab dem 70. Geburtstag eines aktiven Mitgliedes sowie eines passiven Ehrenmitgliedes erfolgt ein St\xe4ndchen durch den Chor."}),"\n",(0,s.jsx)(n.li,{children:"Ab dem 75. Geburtstag wird bei jedem Mitglied ein St\xe4ndchen dargebracht. Diese Regelung wird dann im 5 Jahres-Rythmus fortgesetzt."}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"25-januar---wandertag-der-vereinsfamilie",children:"25. Januar - Wandertag der Vereinsfamilie"}),"\n",(0,s.jsx)(n.p,{children:"Als geselligen Einstand in das neue Jahr veranstalteten wir am 25. Januar einen Wandertag f\xfcr alle Mitglieder und deren Angeh\xf6rige. Schon am fr\xfchen Nachmittag bestieg man den Bus und fuhr gemeinsam nach Dreifelden. Von dort ging es dann zu Fu\xdf zur Alpenr\xf6der H\xfctte. Bei winterlich kalten Temperaturen und strahlenden Sonnenschein nahm man den Fu\xdfmarsch in Angriff, bestens versorgt mit allerlei hochprozentigen Getr\xe4nken. Oben an der H\xfctte wartete schon das Team der Fam. J\xf6ckl um uns mit Speis und Trank zu bedienen. Und so verbrachte man einige sch\xf6ne Stunden in der H\xfctte und die Zeit bis zur R\xfcckfahrt verging wie im Flug, wobei der ein oder andere zum Schluss doch mehr oder weniger orientierungslos wirkte."}),"\n",(0,s.jsx)(n.h2,{id:"26-januar---gottesdienst-f\xfcr-unser-verstorbenes-passives-mitglied-ewald-spreen",children:"26. Januar - Gottesdienst f\xfcr unser verstorbenes passives Mitglied Ewald Spreen"}),"\n",(0,s.jsx)(n.p,{children:"Einen Tag sp\xe4ter erwiesen wir unserem verstorbenen passiven Mitglied Ewald Spreen die letzte Ehre. Da es uns aus personellen Problemen nicht m\xf6glich war, bei der Beerdigung von Ewald Spreen zu singen, hatten wir der Familie versprochen dies bei dem Trauergottesdienst am 26.01. in der evangelischen Kirche in Wallmerod nachzuholen."}),"\n",(0,s.jsx)(n.h2,{id:"24-februar---st\xe4ndchen-zum-80-geburtstag-von-fritz-thierbach",children:"24. Februar - St\xe4ndchen zum 80. Geburtstag von Fritz Thierbach"}),"\n",(0,s.jsx)(n.p,{children:"Das erste Geburtstagsst\xe4ndchen im neuen Jahr brachten wir am 24. Februar dar. Unser passives Mitglied Fritz Thierbach feierte an diesem Tag seinen 80. Geburtstag und wir gratulierten abends bei frostigen Temperaturen mit einigen Liedern."}),"\n",(0,s.jsx)(n.h2,{id:"22-m\xe4rz---st\xe4ndchen-zum-75-geburtstag-von-josef-merfels",children:"22. M\xe4rz - St\xe4ndchen zum 75. Geburtstag von Josef Merfels"}),"\n",(0,s.jsx)(n.p,{children:"Zu seinem 75. Geburtstag hatte unser aktives Ehrenmitglied Josef Merfels am 22. M\xe4rz ins B\xfcrgerhaus eingeladen. Gerne kamen wir dieser Einladung nach und gratulierten einem unserer treuesten S\xe4nger mit einem St\xe4ndchen. Hans-Georg Jung hob besonders die Zuverl\xe4ssigkeit und Hilfsbereitschaft von Josef Merfels hervor."}),"\n",(0,s.jsx)(n.h2,{id:"05-april---gottesdienst-zur-goldenen-hochzeit-von-alois-und-josefine-fein",children:"05. April - Gottesdienst zur Goldenen Hochzeit von Alois und Josefine Fein"}),"\n",(0,s.jsx)(n.p,{children:"Am 5. April stand dann bei \u201eFonnehangerde\u201c ein gro\xdfer Festtag an. Unser aktives Ehrenmitglied Alois Fein feierte an diesem Tag zusammen mit seiner Frau Josefine das Fest ihrer goldenen Hochzeit. Der Chor gestaltete den sehr sch\xf6nen Dankgottesdienst in der Pfarrkirche Steinefrenz mit und unterst\xfctzte auch den abendlichen Fackelzug der Ortsgemeinde."}),"\n",(0,s.jsx)(n.h2,{id:"06-april---kreisleistungssingen-in-westerburg",children:"06. April - Kreisleistungssingen in Westerburg"}),"\n",(0,s.jsx)(n.p,{children:"Schon am n\xe4chsten Morgen traf man sich wieder, um nach einem l\xe4ngeren Einsingen zum Kreisleistungssingen nach Westerburg zu fahren. In einer fr\xfcheren Aktivenversammlung hatte der Chor sich daf\xfcr entschieden, wieder einmal an einem Meisterchorsingen teilzunehmen, so dass wir uns f\xfcr diese 1. Stufe angemeldet hatten. Unser Chorleiter hatte als Chorwerk \u201eVersus memoriales\u201c von Carl Friedrich Zelter und als Volkslied \u201eRosemarie\u201c von Rudi K\xfchn ausgew\xe4hlt und uns, wie immer, sehr gut vorbereitet. Man merkte dem Chor vor dem Auftritt eine gewisse Nervosit\xe4t an, war es doch f\xfcr die meisten neuen S\xe4nger das erste Wertungssingen an dem sie teilnahmen. Jedoch konnte man mit den Vortr\xe4gen insgesamt sehr zufrieden sein. Besonders das Chorwerk gelang uns sehr gut, was uns auch vom Wertungsrichter Hubertus Weimer best\xe4tigt wurde. In seiner sehr ausf\xfchrlichen Besprechung fand er aber auch einige kritische Ans\xe4tze bei unserem Volkslied, hatten wir hier nach gutem Beginn doch etwas an Spannung verloren. Insgesamt hatte Hubertus Weimer an diesem Tag aber wieder einen, wie er selbst sagte \u201eph\xe4nomenal guten Chor\u201c geh\xf6rt. Er gab uns f\xfcr das Chorwerk 24 von 25 m\xf6glichen Punkten und f\xfcr das Volkslied 22 Punkte was zweimal das Pr\xe4dikat \u201esehr gut\u201c bedeutete."}),"\n",(0,s.jsx)(n.h2,{id:"10-april---aktivenversammlung",children:"10. April - Aktivenversammlung"}),"\n",(0,s.jsx)(n.p,{children:"Da es f\xfcr viele aktive S\xe4nger, insbesondere aus beruflichen Gr\xfcnden, immer schwieriger wird bei Beerdigungen von Vereinsmitgliedern anwesend zu sein, war es dringend erforderlich auf diese Tatsache zu reagieren. Eine Neuregelung hier\xfcber war dann auch das einzige Thema einer Aktivenversammlung, die am 10. April nach einer Gesangstunde stattfand. In einer namentlich Abstimmung wurde schlie\xdflich folgende Regelung mit 35 Ja und 17 Gegenstimmen beschlossen:"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsx)(n.li,{children:"Im Sterbefall eines aktiven S\xe4ngers, eines Ehrenmitgliedes sowie eines passiven Mitgliedes, das mindestens 25 Jahre gesungen hat (Besitz der silbernen Ehrennadel), wird auch weiterhin bei der Beerdigung auf dem Friedhof gesungen und ein Vertreter des Vorstandes h\xe4lt eine Grabrede."}),"\n",(0,s.jsx)(n.li,{children:"F\xfcr alle verstorbenen passiven Mitglieder, die keine 25 Jahre gesungen haben, gestaltet der Chor einmal im Jahr einen Gedenkgottesdienst in der Pfarrkirche Steinefrenz musikalisch mit. Die einzelnen Verstorbenen werden namentlich erw\xe4hnt. Als festen Termin wird der Samstag oder Sonntag nach dem Fest des Hl. Sebastian festgelegt."}),"\n",(0,s.jsx)(n.li,{children:"Bei der Beerdigung des Verstorbenen wird der Chor durch eine Abordnung vertreten und ein Vertreter des Vorstandes h\xe4lt eine Grabrede."}),"\n"]}),"\n",(0,s.jsx)(n.h2,{id:"12-april---konzert-beim-mgv-konkordia-winnen-in-gem\xfcnden",children:'12. April - Konzert beim MGV "Konkordia" Winnen in Gem\xfcnden'}),"\n",(0,s.jsx)(n.p,{children:"Am 12. April nahmen wir auf Einladung des MGV \u201eKonkordia\u201c Winnen an dessen Konzert in der evangelischen Kirche in Gem\xfcnden teil. Auch hier wusste der Chor dem Publikum mit einem anspruchsvollem Programm zu gefallen, abgesehen von einigen Problemstellen beim \u201eDrunken Sailor\u201c. Zum gem\xfctlichen Ausklang ging es nach dem Konzert ins B\xfcrgerhaus nach Winnen."}),"\n",(0,s.jsx)(n.h2,{id:"31-mai---volkliederwettbewerb-in-gro\xdfholbach",children:"31. Mai - Volkliederwettbewerb in Gro\xdfholbach"}),"\n",(0,s.jsx)(n.p,{children:"Der musikalische H\xf6hepunkt des Jahres stand am 31. Mai an. Der MGV \u201eC\xe4cilia\u201c Gro\xdfholbach veranstaltete an diesem Tag im Rahmen seines 100-j\xe4hrigen Bestehens einen Volksliederwettbewerb, f\xfcr den wir uns angemeldet hatten. Da sich die Vorbereitung f\xfcr diesen Wettbewerb a) durch mangelnden Probenbesuch und b) durch \xe4u\xdferst ungl\xfcckliche Vorf\xe4lle im direkten Vorfeld des Wertungssingens sehr schwierig gestaltete, sah man diesem Auftritt mit gemischten Gef\xfchlen entgegen, traten wir doch in der M\xe4nnerchorklasse 3 gegen die starke heimische Konkurrenz des MGV \u201eArion\u201c Nomborn mit ihrem Chorleiter Hubertus Weimer an. Doch auch ein w\xe4hrend unseres Vortrags einsetztendes Gewitter konnte unseren an diesem Tag sehr souver\xe4n und locker auftretenden Chor nicht aus der Ruhe bringen. Unsere 3 Volkslieder \u201eWhat shall we do with the drunken sailor\u201d, \u201eWohl auf ihr G\xe4ste\u201d und \u201eRosemarie\u201c gelangen allesamt sehr gut und Chor und Chorleiter verlie\xdfen zufrieden die B\xfchne. Dies wurde auch in der Bewertung der beiden Wertungsrichter Richard Trares und Bernhard van Almsick deutlich. Von 60 m\xf6glichen Punkten gaben sie uns im Volkslied A 56,6 Punkte im Volkslied B 55,5 Punkte und im Volkslied C 54,5 Punkte. In der Gesamtpunktzahl lagen wir mit 166,5 Punkten einen Punkt vor den Nombornern. Und so gewannen wir im Klassensingen den 1. Preis, im Ehrensingen den 2. Preis und unser Chorleiter konnte sich verdienter Ma\xdfen \xfcber den Gewinn des 1. Dirigentenpreises freuen. Anschlie\xdfen wurde dieser Erfolg im gro\xdfen Festzelt, zusammen mit den S\xe4ngern aus Nomborn geb\xfchrend gefeiert."}),"\n",(0,s.jsx)(n.h2,{id:"04-juni---tod-unseres-f\xf6rdernden-mitglieds-manfred-kremer",children:"04. Juni - Tod unseres f\xf6rdernden Mitglieds Manfred Kremer"}),"\n",(0,s.jsx)(n.p,{children:"Den n\xe4chsten Trauerfall hatten wir am 04. Juni zu beklagen. An diesem Tag verstarb nach kurzer aber schwerer Krankheit unser langj\xe4hriges f\xf6rderndes Mitglied Manfred Kremer. Bei seiner Beerdigung hielt Herbert Malm eine Grabrede und eine Blumenschale wurde ihm zum Gedenken niedergelegt. Es war dies das erste Mal, dass hier die Regelung griff, die wir bei der Aktivenversammlung am 10. April beschlossen hatten."}),"\n",(0,s.jsx)(n.h2,{id:"22-juni---thekendienst-beim-25-j\xe4hrigen-jubil\xe4um-des-musikverein-hundsangen",children:"22. Juni - Thekendienst beim 25-j\xe4hrigen Jubil\xe4um des Musikverein Hundsangen"}),"\n",(0,s.jsx)(n.p,{children:"Der Hunds\xe4nger Musikverein feierte vom 18. \u2013 22.06. sein 25-j\xe4hriges Bestehen und wir hatten ihnen zugesagt beim Festzug am Sonntag den 22. Juni den Thekendienst zu \xfcbernehmen. Im Gegenzug w\xfcrde der Musikverein bei unserem Lindenfest umsonst aufspielen. An dieser Stelle m\xf6chte sich der Vorstand noch mal bei allen Helfern bedanken, besonders bei den S\xe4ngerfrauen Mechthild Eidt, Christa Solbach und Elfriede Malm, welche im Essenszelt t\xe4tig waren."}),"\n",(0,s.jsx)(n.h2,{id:"06-juli---st\xe4ndchen-zum-70-geburtstag-von-manfred-munsch",children:"06. Juli - St\xe4ndchen zum 70. Geburtstag von Manfred Munsch"}),"\n",(0,s.jsx)(n.p,{children:"Am 06. Juli gab es dann wieder was zu feiern. Unser aktives Mitglied Manfred Munsch wurde an diesem Tag 70 Jahre alt und hatte seine Familie und Freunde in den Sebastiansaal des B\xfcrgerhauses eingeladen. Auch die \u201eFrohe Stunde\u201c gratulierte ihn an diesem Tag mit einem St\xe4ndchen und einem Geschenk."}),"\n",(0,s.jsx)(n.h2,{id:"12-juli---st\xe4ndchen-zur-silbernen-hochzeit-von-christel-und-heinz-josef-kronimus",children:"12. Juli - St\xe4ndchen zur Silbernen Hochzeit von Christel und Heinz-Josef Kronimus"}),"\n",(0,s.jsx)(n.p,{children:"Kurz vor Beginn der Sommerpause stand dann das n\xe4chste St\xe4ndchen an, wiederum im Sebastiansaal. Christel und Heinz-Josef Kronimus feierten am 12. Juli den Tag ihrer Silbernen Hochzeit und wie \xfcblich brachte der Chor auch hier zwei Lieder zum Vortrag. Auch das \u201eHoch soll\u2019n sie leben\u201c bereitete uns diesmal, anders als vor 25 Jahren, keine Probleme."}),"\n",(0,s.jsx)(n.h2,{id:"13-juli---chorworkshop-der-s\xe4ngergruppe-iii-in-ruppach-goldhausen",children:"13. Juli - Chorworkshop der S\xe4ngergruppe III in Ruppach-Goldhausen"}),"\n",(0,s.jsx)(n.p,{children:"Einen Tag sp\xe4ter fand ein Chorworkshop der S\xe4ngergruppe III in Ruppach-Goldhausen statt. Auf vielfachen Wunsch hatte der Vorstand der S\xe4ngergruppe III beschlossen, anstatt des allj\xe4hrlichen Kritiksingens, den S\xe4ngerinnen und S\xe4ngern der Gruppe mal etwas Neues anzubieten. Man hatte 4 Workshops aus verschiedenen Chorgattungen zur Auswahl gestellt, die von speziell ausgesuchten Fachleuten geleitet wurden. Zum Abschluss des Tages brachten die einzelnen Gruppen ihre erarbeiteten Werke zum Vortrag. Auch wenn die Beteiligung der Ch\xf6re eher m\xe4\xdfig war, auch von unserem Chor waren nur 12 S\xe4nger anwesend, sollte eine solche Veranstaltung in einer verbesserten Form wiederholt werden."}),"\n",(0,s.jsx)(n.h2,{id:"30--31-august---chorfreizeit-in-traben-trabach",children:"30. + 31. August - Chorfreizeit in Traben-Trabach"}),"\n",(0,s.jsx)(n.p,{children:"Mit Beginn des 2. Halbjahres veranstalteten wir am 30. und 31. August zum ersten Mal eine zweit\xe4gige Chorfreizeit. Austragungsort war die Jugendherberge in Traben-Trabach an der Mosel. Hier arbeiteten wir in insgesamt 9 Stunden reiner Chorprobe unser aktuelles Repertuar intensiv durch. Besonderen Wert legte unser Chorleiter auf das neue Chorwerk \u201ePeriti Autem\u201c von Mendelssohn-Bartholdy. Hier geht auch ein besonderes Lob an Jens R\xf6th, der es sehr gut verstand, die auch f\xfcr ihn recht anstrengenden Chorproben, locker und abwechslungsreich zu gestalten. Schade das lediglich 28 S\xe4nger an dem Chorwochenende teilnahmen, aber eben diesen 28 hat es in Traben-Trabach ausnahmslos sehr gut gefallen. Auch das kleine Konzert am Samstagabend im Weingut Schuster war rund um gelungen, wenn auch Aussprache und Intonation mit zunehmendem Weinkonsum \xe4u\xdferst diffus wurden."}),"\n",(0,s.jsx)(n.h2,{id:"06-september---konzert-des-jungen-chores-wallmerod",children:'06. September - Konzert des "Jungen Chores" Wallmerod'}),"\n",(0,s.jsx)(n.p,{children:"Am 06. September wirkten wir beim Konzert des \u201eJungen Chores\u201c Wallmerod mit. Die Wallmer\xf6der feierten ihr 5-j\xe4hriges Bestehen und hatten uns hierzu eingeladen. Auch bei dieser Veranstaltung, die insgesamt auf sehr gutem Niveau stattfand, wusste unser Chor zu gefallen, so dass wir nicht ohne eine Zugabe von der B\xfchne kamen."}),"\n",(0,s.jsx)(n.h2,{id:"03-oktober---st\xe4ndchen-zur-silbernen-hochzeit-von-waltraut-und-hermann-malm",children:"03. Oktober - St\xe4ndchen zur Silbernen Hochzeit von Waltraut und Hermann Malm"}),"\n",(0,s.jsx)(n.p,{children:"Zu den vielen G\xe4sten, die Waltraut und Hermann Malm zu ihrer \u201eSilbernen Hochzeit\u201c gratulierten, z\xe4hlte auch die \u201eFrohe Stunde\u201c. Bei ihrer Feier am 3. Oktober brachten wir ein St\xe4ndchen dar und \xfcbermittelten die Gl\xfcckw\xfcnsche des Vereins."}),"\n",(0,s.jsx)(n.h2,{id:"12-oktober---konzert-musik-in-st-matthias-in-steinefrenz",children:'12. Oktober - Konzert "Musik in St. Matthias" in Steinefrenz'}),"\n",(0,s.jsx)(n.p,{children:"Im Rahmen der Konzertreihe \u201eMusik in St. Matthias\u201c veranstalteten wir in unserer Pfarrkirche, gemeinsam mit dem Kirchenchor Steinefrenz/Weroth und dem MGV \u201eFrohsinn\u201c Steinefrenz am 12. Oktober ein Chor und Orchesterkonzert. Zusammen mit der Sopranistin Jutta Sode, der Limburger Orchestervereinigung und Bl\xe4sern von der Kreismusikschule Limburg trugen wir hier noch mal das Werk \u201eStimme der Allmacht\u201c von Theo Fischer vor. Nach einem sehr schleppend verlaufenden Kartenvorverkauf, fand das Konzert beim doch recht zahlreich erschienenen Publikum sehr guten Anklang, wie der langanhaltende Beifall f\xfcr alle beteiligten Musiker zeigte. Dass man zum Schluss sogar noch einen Gewinn von ca. 1200 \u20ac an die drei Ch\xf6re verteilen konnte, ist in erster Linie ein Verdienst von Edi Hannappel, der bei vielen Firmen Spendengelder gesammelt hatte. Hierf\xfcr nochmals herzlichen Dank."}),"\n",(0,s.jsx)(n.h1,{id:"19-oktober---regionalleistungssingen-in-ochtendung",children:"19. Oktober - Regionalleistungssingen in Ochtendung"}),"\n",(0,s.jsx)(n.p,{children:"Nachdem wir im Fr\xfchjahr das Kreisleistungssingen in Westerburg mit Erfolg abgeschlossen hatten, fuhren wir am 19. Oktober nach Ochtendung, wo mit dem Regionalleistungssingen die 2. Stufe f\xfcr den Meisterchortitel bew\xe4ltigt werden musste. Leider brachten wir an diesem Tag gerade einmal 36 S\xe4nger auf die B\xfchne, was sicher nicht zur allgemeinen Beruhigung beitrug. Und so blieben wir, trotz bester Vorbereitung durch unseren Chorleiter, bei allen drei Vortr\xe4gen unter unseren M\xf6glichkeiten. Von den beiden Wertungsrichtern Rinnscheit und Schneider erhielten wir f\xfcr das Pflichtst\xfcck \u201eStabat mater\u201c 23 Punkte, f\xfcr den Wahlchor \u201eDer Tambour\u201c 25 Punkte und f\xfcr das Volkslied \u201eSoon ah will be done\u201c 24 Punkte. Da man pro Liedvortrag maximal 25 Punkte erreichen konnte war man mit dieser Bewertung sehr zufrieden, zumal wir zusammen mit dem MGV \u201eGermania\u201c Freiendiez die Tagesh\xf6chstpunktzahl von 72 Punkten erreicht hatten."}),"\n",(0,s.jsx)(n.h2,{id:"15-november---lindenfest",children:"15. November - Lindenfest"}),"\n",(0,s.jsx)(n.p,{children:"Am 15. November veranstalteten wir, nachdem der Termin einige Male verschoben worden war, endlich unser Lindenfest. Anlass war das 90-j\xe4hrige Jubil\xe4um unserer Dorflinde. Das Fest begann nachmittags mit Kaffee und Kuchen und die Jugendfeuerwehr f\xfchrte eine L\xf6sch\xfcbung an der Grundschule durch. Am fr\xfchen Abend brachte dann der Chor mehrere Lieder zum Vortrag. Hierbei wurden auch einige S\xe4nger f\xfcr ihr langj\xe4hriges Mitwirken im Chor geehrt. Dieter P\xf6rtner wurde f\xfcr 40 Jahre aktive Mitgliedschaft mit der Goldenen Ehrennadel ausgezeichnet und zum Ehrenmitglied ernannt, Gerold Haselsteiner erhielt die Silberne Ehrennadel f\xfcr 25 j\xe4hriges aktives Singen. Des weiteren wurde Edi Hannappel f\xfcr 31-j\xe4hrige und Hans-Georg Jung f\xfcr 26-j\xe4hrige Vorstandst\xe4tigkeit eine Urkunde \xfcberreicht. Bevor dann der Musikverein Hundsangen aufspielte beendete die Tanzgruppe \u201eRed Flames\u201c das offizielle Programm. \xdcberaus entt\xe4uschend war aber auch diesmal die Resonanz aus der Wer\xf6ther Bev\xf6lkerung, sodass der Chor doch mehr oder weniger unter sich war. Zu erw\xe4hnen ist noch, dass die eigentlich geplante Pflanzung einer neuen Linde am B\xfcrgerhaus so lang verschoben werden muss, bis die entg\xfcltige Neugestaltung des Vorplatzes feststeht."}),"\n",(0,s.jsx)(n.h2,{id:"16-november---gedenken-am-ehrenmal-volkstrauertrag",children:"16. November - Gedenken am Ehrenmal (Volkstrauertrag)"}),"\n",(0,s.jsx)(n.p,{children:"Am n\xe4chsten Morgen, dem 16.11., wirkten wir bei der Gedenkstunde der Ortsgemeinde f\xfcr die Vermissten und Gefallenen beider Weltkriege am Ehrenmal mit."}),"\n",(0,s.jsx)(n.h2,{id:"21--22-november---thekendienst-beim-musical-der-red-flames-in-weroth",children:'21. + 22. November - Thekendienst beim Musical der "Red Flames" in Weroth'}),"\n",(0,s.jsx)(n.p,{children:"Dass es immer noch m\xf6glich ist, die Halle unseres B\xfcrgerhauses bis auf den letzten Platz zu f\xfcllen bewies die Tanzgruppe \u201eRed Flames\u201c die am 21. und 22.11. das Musical \u201eTanz der Vampire\u201c in einer Playbackversion auff\xfchrte. Das Publikum war von den schauspielerischen und t\xe4nzerischen Leistungen der jungen Akteure begeistert. Wir von der \u201eFrohen Stunde\u201c hatten bei der organisatorischen Vorbereitung mitgewirkt und bei beiden Veranstaltungen den Thekendienst \xfcbernommen."}),"\n",(0,s.jsx)(n.h2,{id:"14-dezember---seniorentag",children:"14. Dezember - Seniorentag"}),"\n",(0,s.jsx)(n.p,{children:"Als letzter musikalischer Termin stand auch in diesem Jahr die Mitwirkung beim Seniorentag am 14. Dezember an. Wie immer bereicherten wir mit unseren Weihnachtsliedern die nachmitt\xe4gliche Feier. Der Chor wurde an diesem Tag von unserem Vizechorleiter Christoph Weimer geleitet, bei dem wir uns an dieser Stelle f\xfcr sein engagiertes Mitwirken im vergangenen Jahr herzlich bedanken."}),"\n",(0,s.jsx)(n.h2,{id:"was-passiert-2003-sonst-noch-",children:"Was passiert 2003 sonst noch ..."}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"07. Oktober"})," - Silberne Hochzeit von Manfred und Maria Kaiser"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"13. Oktober"})," - 75. Geburtstag unseres Ehrenmitglieds G\xfcnter Sch\xe4fer"]}),"\n"]})]})}function o(e={}){const{wrapper:n}={...(0,t.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(l,{...e})}):l(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const s={},t=i.createContext(s);function a(e){const n=i.useContext(t);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:a(e.components),i.createElement(t.Provider,{value:n},e.children)}}}]);