"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[98],{8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const t={},s=i.createContext(t);function a(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:a(e.components),i.createElement(s.Provider,{value:n},e.children)}},9767:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>g,frontMatter:()=>a,metadata:()=>i,toc:()=>u});const i=JSON.parse('{"id":"archiv/2000-2009/2006","title":"2006","description":"Unser eigenes Konzert mit dem \\"Siegtal-Quartett\\" sowie dem Jugend-Konzertchor Wallmerod l\xe4sst das B\xfcrgerhaus aus allen N\xe4hten platzen.","source":"@site/docs/archiv/2000-2009/2006.md","sourceDirName":"archiv/2000-2009","slug":"/archiv/2000-2009/2006","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2006","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2000-2009/2006.md","tags":[],"version":"current","sidebarPosition":4,"frontMatter":{"sidebar_position":4,"description":"Unser eigenes Konzert mit dem \\"Siegtal-Quartett\\" sowie dem Jugend-Konzertchor Wallmerod l\xe4sst das B\xfcrgerhaus aus allen N\xe4hten platzen."},"sidebar":"archivSidebar","previous":{"title":"2007","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2007"},"next":{"title":"2005","permalink":"/archiv-frohe-stunde/docs/archiv/2000-2009/2005"}}');var t=r(4848),s=r(8453);const a={sidebar_position:4,description:'Unser eigenes Konzert mit dem "Siegtal-Quartett" sowie dem Jugend-Konzertchor Wallmerod l\xe4sst das B\xfcrgerhaus aus allen N\xe4hten platzen.'},d="2006",h={},u=[{value:"07. Januar - Jahreshauptversammlung",id:"07-januar---jahreshauptversammlung",level:2},{value:"22. Januar - Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder der &quot;Frohen Stunde&quot; in Steinefrenz",id:"22-januar---gedenkgottesdienst-f\xfcr-die-lebenden-und-verstorbenen-mitglieder-der-frohen-stunde-in-steinefrenz",level:2},{value:"04. Februar - Wandertag",id:"04-februar---wandertag",level:2},{value:"18. Oktober - Konzert beim MGV &quot;Beethoven&quot; Dernbach",id:"18-oktober---konzert-beim-mgv-beethoven-dernbach",level:2},{value:"06. April - Beerdigung unseres Ehrenmitglieds Alfred Eidt",id:"06-april---beerdigung-unseres-ehrenmitglieds-alfred-eidt",level:2},{value:"09. April - Konzert beim M\xe4nnerchor 1870 Bassenheim",id:"09-april---konzert-beim-m\xe4nnerchor-1870-bassenheim",level:2},{value:"11. April - St\xe4ndchen zum 80. Geburtstag von Josef Kremer",id:"11-april---st\xe4ndchen-zum-80-geburtstag-von-josef-kremer",level:2},{value:"23. Apirl - Fr\xfchlings-Matinee beim GV &quot;S\xe4ngerkranz&quot; Watzenborn",id:"23-apirl---fr\xfchlings-matinee-beim-gv-s\xe4ngerkranz-watzenborn",level:2},{value:"06. + 07. Mai - Chorfreizeit in Bad-Marienberg",id:"06--07-mai---chorfreizeit-in-bad-marienberg",level:2},{value:"20. Mai - St\xe4ndchen zur Goldenen Hochzeit von Bernhard und Maria Metternich",id:"20-mai---st\xe4ndchen-zur-goldenen-hochzeit-von-bernhard-und-maria-metternich",level:2},{value:"23. Juni - Freundschaftssingen beim MGV 1866 Eitelborn",id:"23-juni---freundschaftssingen-beim-mgv-1866-eitelborn",level:2},{value:"25. Juni - Abschiedsfeier f\xfcr die Sch\xf6nst\xe4tter Marienschwestern in Steinefrenz",id:"25-juni---abschiedsfeier-f\xfcr-die-sch\xf6nst\xe4tter-marienschwestern-in-steinefrenz",level:2},{value:"30. Juni - Fackelzug zur Goldenen Hochzeit von Irmgard und Karl-Heinz Elsner",id:"30-juni---fackelzug-zur-goldenen-hochzeit-von-irmgard-und-karl-heinz-elsner",level:2},{value:"15. Juli - St\xe4ndchen zum 70. Geburtstag von Hermann Wieser",id:"15-juli---st\xe4ndchen-zum-70-geburtstag-von-hermann-wieser",level:2},{value:"08. September - Konzert beim MGV &quot;Frohsinn&quot; Staudt",id:"08-september---konzert-beim-mgv-frohsinn-staudt",level:2},{value:"16. September - Volkliederwettbewerb in Langenei",id:"16-september---volkliederwettbewerb-in-langenei",level:2},{value:"29. Oktober - Gruppenkritiksingen in Nomborgn",id:"29-oktober---gruppenkritiksingen-in-nomborgn",level:2},{value:"12. November - Konzert beim Kirchenchor Westerburg",id:"12-november---konzert-beim-kirchenchor-westerburg",level:2},{value:"18. November - Eigenes Konzert in Weroth",id:"18-november---eigenes-konzert-in-weroth",level:2},{value:"19. Januar - Singen am Ehrenmal (Volkstrauertag)",id:"19-januar---singen-am-ehrenmal-volkstrauertag",level:2},{value:"Was passiert 2006 sonst noch ...",id:"was-passiert-2006-sonst-noch-",level:2}];function l(e){const n={h1:"h1",h2:"h2",header:"header",hr:"hr",li:"li",p:"p",strong:"strong",ul:"ul",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"2006",children:"2006"})}),"\n",(0,t.jsx)(n.h2,{id:"07-januar---jahreshauptversammlung",children:"07. Januar - Jahreshauptversammlung"}),"\n",(0,t.jsx)(n.p,{children:"Den Beginn des Jahres bildete wieder einmal die Jahreshauptversammlung am 07. Januar im Vereinslokal. Johannes Hannappel begr\xfc\xdfte die 37 anwesenden Mitglieder und hielt eine kurze R\xfcckschau auf das erfolgreiche Jahr 2005. Neben den musikalischen Erfolgen konnte man sich nach langer Zeit auch \xfcber eine gestiegene Mitgliederzahl freuen. Im Mittelpunkt der Versammlung standen jedoch die Ehrungen verdienter S\xe4nger. F\xfcr 50 Jahre aktives Singen wurde Edi Hannappel geehrt. F\xfcr 40 Jahre aktives Singen wurden Axel Metternich und Hans-J\xfcrgen Sch\xf6nberger geehrt und f\xfcr 25 Jahre aktives Singen Bruno Heep und Gerhard Weimer. Die Ehrung nahm Herbert Malm, der Vorsitzende der S\xe4ngergruppe III vor, und \xfcberreichte die entsprechenden Ehrennadeln und Urkunden. Johannes Hannappel danke allen Jubilaren f\xfcr deren geleistete Arbeit und ernannte gleichzeitig Axel Metternich Hans-J\xfcrgen Sch\xf6nberger zu Ehrenmitgliedern. Besonders bedankte er sich bei Edi Hannappel, f\xfcr seine langj\xe4hrige, engagierte Mitarbeit im Vorstand. Nach den Berichten des Gesch\xe4ftsf\xfchrers, Kassierers und Kassenpr\xfcfer, der Entlastung des Vorstandes sowie der Vorschau auf das Jahr 2006 schloss die z\xfcgig durchgef\xfchrte Versammlung mit dem Lied \u201eDas Morgenrot\u201c."}),"\n",(0,t.jsx)(n.h2,{id:"22-januar---gedenkgottesdienst-f\xfcr-die-lebenden-und-verstorbenen-mitglieder-der-frohen-stunde-in-steinefrenz",children:'22. Januar - Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder der "Frohen Stunde" in Steinefrenz'}),"\n",(0,t.jsx)(n.p,{children:"Den musikalischen Auftakt ins Jahr 2006 bildete der Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder unseres Vereins. Am 22. Januar umrahmten wir die Messe in unserer Pfarrkirche mit einigen geistlichen Chorwerken. Der Beifall der Gottesdienstbesucher nach unserem Schlu\xdflied war Beweis, dass uns die Vortr\xe4ge recht gut gelungen waren."}),"\n",(0,t.jsx)(n.h2,{id:"04-februar---wandertag",children:"04. Februar - Wandertag"}),"\n",(0,t.jsx)(n.p,{children:"Am 4. Februar hie\xdf es dann die Wanderschuhe zu schn\xfcren. Um 14.00 Uhr trafen wir uns vor unserem Vereinslokal um dann eine ca. dreist\xfcndige Wanderung in Angriff zu nehmen. Bei doch recht tr\xfcben Wetter und leichten Minusgraden ging es dann \xfcber \u201eHereck\u201c und \u201eHanse\u201c auf Molsberg zu. Dort versorgten wir uns ausgiebig mit hei\xdfem Gl\xfchwein, ehe es dann durch die Mark wieder zur\xfcck ins Sportheim Weroth ging. Hier klang der Tag feucht fr\xf6hlich aus, wenn auch einige schon recht fr\xfchzeitig die Segel streichen mussten. Bedanken m\xfcssen wir uns bei den Kirmesburschen, die an diesem Abend die Bewirtung \xfcbernommen hatten."}),"\n",(0,t.jsx)(n.h2,{id:"18-oktober---konzert-beim-mgv-beethoven-dernbach",children:'18. Oktober - Konzert beim MGV "Beethoven" Dernbach'}),"\n",(0,t.jsx)(n.p,{children:"In der Dernbacher Turnhalle fand die erste Konzertveranstaltung im Jahr statt, an der wir auf Einladung des MGV \u201eBeethoven\u201c Dernbach teilnahmen. Insgesamt gesehen gelang uns hier ein recht ordentlicher Auftritt in einem Konzert, das sicherlich nicht auf h\xf6chstem musikalischen Niveau lag. Vielleicht lag es auch an unserer Liedauswahl, dass das Publikum doch recht reserviert auf unsere Vortr\xe4ge reagierte. Um so lockerer zeigten wir uns aber nach der Veranstaltung, wo wir bei einem guten \u201eFOHR-Pils\u201c noch jede Menge musikalischer Zugaben zum Besten gaben."}),"\n",(0,t.jsx)(n.h2,{id:"06-april---beerdigung-unseres-ehrenmitglieds-alfred-eidt",children:"06. April - Beerdigung unseres Ehrenmitglieds Alfred Eidt"}),"\n",(0,t.jsx)(n.p,{children:"Nachdem wir in den letzten Jahren vor Sterbef\xe4llen unserer Mitglieder verschont blieben, mussten wir am 6. April unser Ehrenmitglied Alfred Eidt zu Grabe tragen. Alfred Eidt war nach langer Krankheit im Alter von 81 Jahren am 2. April verstorben. Bei seiner Beerdigung ehrten wir unseren langj\xe4hrigen S\xe4nger, ehemaligen Kassierer im Vorstand sowie treuen Anh\xe4nger unseres Vereines mit zwei Ges\xe4ngen und legen, ihm zum Gedenken, eine Schale nieder."}),"\n",(0,t.jsx)(n.h2,{id:"09-april---konzert-beim-m\xe4nnerchor-1870-bassenheim",children:"09. April - Konzert beim M\xe4nnerchor 1870 Bassenheim"}),"\n",(0,t.jsx)(n.p,{children:"Einer R\xfcckverpflichtung aus unserem Jubil\xe4umsjahr 2000 kamen wir am 9. April nach. Der M\xe4nnerchor 1870 Bassenheim veranstaltete an diesem Tag ein Konzert und hatte uns zur Mitwirkung eingeladen. Da mit dem MGV R\xfcbenach noch ein weiterer M\xe4nnerchor anwesend war, gestaltete sich die Veranstaltung insgesamt ziemlich eint\xf6nig. Wir bem\xfchten uns, ein abwechslungsreiches Programm darzubringen, was uns auch recht gut gelang. Dem Publikum schienen unsere Vortr\xe4ge zu gefallen. Und auch in Bassenheim verlie\xdfen wir, nach einem ausgiebigen Umtrunk, die Konzerthalle wieder einmal als die Letzten."}),"\n",(0,t.jsx)(n.h2,{id:"11-april---st\xe4ndchen-zum-80-geburtstag-von-josef-kremer",children:"11. April - St\xe4ndchen zum 80. Geburtstag von Josef Kremer"}),"\n",(0,t.jsx)(n.p,{children:"Unserem passiven Mitglied Josef Kremer brachten wir am 11. April zu seinem 80. Geburtstag ein St\xe4ndchen dar. Johannes Hannappel hob in seiner Dankesrede die manchmal kantige aber immer verl\xe4ssliche und gradlinige Art unseres langj\xe4hrigen Mitgliedes hervor. \u201eEt es halt de Scheffes Jupp\u201c."}),"\n",(0,t.jsx)(n.h2,{id:"23-apirl---fr\xfchlings-matinee-beim-gv-s\xe4ngerkranz-watzenborn",children:'23. Apirl - Fr\xfchlings-Matinee beim GV "S\xe4ngerkranz" Watzenborn'}),"\n",(0,t.jsx)(n.p,{children:"Aus Anla\xdf seines 130-j\xe4hrigen Bestehens hatte der GV \u201eS\xe4ngerkranz\u201c Watzenborn am 23. April zu einer Fr\xfchlings-Matinee eingeladen. Neben der \u201eFrohen Stunde\u201c wirkten mit dem \u201eVokalensemble Jugendfreund\u201c Watzenborn-Steinberg, der \u201eCamerata Vocale\u201c Dutenhofen sowie dem Gastgeber \u201eVokale S\xe4ngerkranz\u201c weitere leistungsstarke Ch\xf6re bei dieser Veranstaltung mit. Schon im Vorfeld dieses Konzertes hatten wir Bedenken, da\xdf wir, als einziger gewachsener Laien-M\xe4nnerchor, gegen diese Auswahlch\xf6re mit doch durchweg j\xfcngeren Stimmen, leistungsm\xe4\xdfig stark abfallen w\xfcrden. Chorleiter Jens R\xf6th setzte an diesem Morgen ganz auf die St\xe4rken unseres Chores, die Gestaltung eines abwechslungsreichen Repertuars sowie dem Musizieren mit Herz. Trotz der sehr transparenten Akustik der Konzerthalle konnten wir auch das Publikum in Pohlheim \xfcberzeugen. Man lie\xdf uns nicht ohne Zugabe von der B\xfchne. Selbst Jens R\xf6th lie\xdf sich nachher zu der Bemerkung hinrei\xdfen, \u201edieser Auftritt war f\xfcr mich wie ein innerlicher Fackelzug\u201c."}),"\n",(0,t.jsx)(n.h2,{id:"06--07-mai---chorfreizeit-in-bad-marienberg",children:"06. + 07. Mai - Chorfreizeit in Bad-Marienberg"}),"\n",(0,t.jsx)(n.p,{children:"Am 6. und 7. Mai fand in der Jugendherberge Bad-Marienberg unsere zweite Chorfreizeit statt. Insgesamt 37 S\xe4nger nahmen an den Proben teil, von denen 24 auch in der modern eingerichteten Jugendherberge \xfcbernachteten. In den von Chorleiter Jens R\xf6th intensiv durchgef\xfchrten Probeneinheiten studierten wir in erster Linie das geistliche Werk \u201eCantate Domino\u201c von Hassler und das \u201eSteal away\u201c ein. Samstagabends ging es dann hinunter nach Bad-Marienberg, wo wir uns nach kurzer Suche in einem Lokal niederlie\xdfen um bei einem guten Glas Hopfenbl\xfctentee die Chorproben fortzusetzen. Allerdings ging mit steigendem Bierkonsum auch ein rapider Schwund der deutlichen Aussprache einher. Zum Ende der beiden Tage waren Chor und Chorleiter mit dem Ergebnis der Chorfreizeit zufrieden."}),"\n",(0,t.jsx)(n.h2,{id:"20-mai---st\xe4ndchen-zur-goldenen-hochzeit-von-bernhard-und-maria-metternich",children:"20. Mai - St\xe4ndchen zur Goldenen Hochzeit von Bernhard und Maria Metternich"}),"\n",(0,t.jsx)(n.p,{children:"Unser passives Mitglied Bernhard Metternich feierte mit seiner Frau Maria am 16. Mai das Fest ihrer goldenen Hochzeit. Auf Wunsch des Jubelpaares fand kein Fackelzug durch die Ortsgemeinde statt. Alle Gratulanten waren zu einer kleinen aber feinen Feier am 20. Mai ins B\xfcrgerhaus eingeladen. Auch die \u201eFrohe Stunde\u201c gratulierte durch eine Abordnung des Vorstandes den Jubilaren zu ihrem Festtag und dankte dem ehemaligen S\xe4nger f\xfcr seine geleistete Arbeit."}),"\n",(0,t.jsx)(n.h2,{id:"23-juni---freundschaftssingen-beim-mgv-1866-eitelborn",children:"23. Juni - Freundschaftssingen beim MGV 1866 Eitelborn"}),"\n",(0,t.jsx)(n.p,{children:"Der MGV 1866 Eitelborn veranstaltete am 23. Juni aus Anlass seines 140-j\xe4hrigen Bestehens ein Freundschaftssingen und hatte uns als Mitwirkende eingeladen. Als letzter von insgesamt 13 Vereinen betraten wir die B\xfchne der Augst-Halle. Trotz der fortgeschrittenen Zeit und des langen Wartens gelang uns ein recht guter Vortrag. Bemerkenswert war auch, dass ausgerechnet der schwierige \u201eAbschied\u201c von M. Neumann beim Publikum auf h\xf6chste Aufmerksamkeit stie\xdf. Langanhaltender Beifall zum Schlu\xdf unserer Vortr\xe4ge zeigte, dass wir auch in Eitelborn einen guten Eindruck hinterlassen hatten."}),"\n",(0,t.jsx)(n.h2,{id:"25-juni---abschiedsfeier-f\xfcr-die-sch\xf6nst\xe4tter-marienschwestern-in-steinefrenz",children:"25. Juni - Abschiedsfeier f\xfcr die Sch\xf6nst\xe4tter Marienschwestern in Steinefrenz"}),"\n",(0,t.jsx)(n.p,{children:"Nach insgesamt 63 Jahren fruchtbaren Wirkens innerhalb unserer Pfarrei, beendeten in diesem Jahr die Sch\xf6nst\xe4tter Marienschwestern ihre Arbeit in der Gemeinde. Aus diesem Anlass veranstaltete die Kirchengemeinde am 25. Juni eine Abschiedsfeier und wir trugen mit einigen Liedvortr\xe4gen zum Gelingen dieser Veranstaltung bei."}),"\n",(0,t.jsx)(n.h2,{id:"30-juni---fackelzug-zur-goldenen-hochzeit-von-irmgard-und-karl-heinz-elsner",children:"30. Juni - Fackelzug zur Goldenen Hochzeit von Irmgard und Karl-Heinz Elsner"}),"\n",(0,t.jsx)(n.p,{children:"Am 30. Juni feierte unser ehemaliges Mitglied Karl-Heinz Elsner zusammen mit seiner Frau Irmgard das Fest ihrer Goldenen Hochzeit. Auf Einladung der Ortsgemeinde umrahmten wir den abendlichen Fackelzug der Gemeinde mit zwei Liedbeitr\xe4gen."}),"\n",(0,t.jsx)(n.h2,{id:"15-juli---st\xe4ndchen-zum-70-geburtstag-von-hermann-wieser",children:"15. Juli - St\xe4ndchen zum 70. Geburtstag von Hermann Wieser"}),"\n",(0,t.jsx)(n.p,{children:"Zum Schlu\xdf des ersten Halbjahres stand noch ein St\xe4ndchen an. Unser langj\xe4hriges Vorstandsmitglied und ehemaliger Vorsitzender Hermann Wieser feierte am 15. Juli im B\xfcrgerhaus seinen 70. Geburtstag. Hans-Georg Jung ging in seiner Dankesrede besonders auf das Jubil\xe4umsjahr 1975 ein, bei dem Hermann Wieser als Festvorsitzender in erster Linie f\xfcr das gute Gelingen des gesamten Jubil\xe4ums verantwortlich war."}),"\n",(0,t.jsx)(n.h2,{id:"08-september---konzert-beim-mgv-frohsinn-staudt",children:'08. September - Konzert beim MGV "Frohsinn" Staudt'}),"\n",(0,t.jsx)(n.p,{children:"Mit einem Konzert beim MGV \u201eFrohsinn\u201c Staudt am 08. September starteten wir nach der Sommerpause ins 2. Halbjahr. Die Staudter feierten ihr 110-j\xe4hriges Bestehen und hatten uns zu ihrem Kommersabend eingeladen. Trotz nicht optimaler \xe4u\xdferer Bedingungen gelang uns ein recht ordentlicher Auftritt. Obwohl es doch einige Unsauberkeiten unsererseits gab, verlie\xdfen wir nicht ohne Zugabe die B\xfchne. Lobend anzumerken ist hier noch, dass viele S\xe4nger, die an diesem Abend zu privaten Feiern eingeladen waren, diese unterbrachen um unseren Auftritt mitzugestalten. Dies ist keinesfalls selbstverst\xe4ndlich und verdient unser Aller Dank und Anerkennung."}),"\n",(0,t.jsx)(n.h2,{id:"16-september---volkliederwettbewerb-in-langenei",children:"16. September - Volkliederwettbewerb in Langenei"}),"\n",(0,t.jsx)(n.p,{children:"Eine Woche sp\xe4ter stand dann gleich der erste musikalische H\xf6hepunkt an. Der MGV \u201eC\xe4cilia\u201c Langenei veranstaltete am 16. September einen Volksliederwettbewerb an dem insgesamt 42 Ch\xf6re teilnahmen. Wir trafen in der Klasse M3 auf den MGV \u201eEintracht\u201c 1873 Hillscheid und den MGV \u201eEintracht Adam Isheim\u201c Gr\xfcningen. Gut vorbereitet betraten wir die B\xfchne der Sauerlandhalle in Altenhundem und merkten sehr bald, dass uns die sehr gute Akustik der Halle sehr entgegenkam. Alle unsere drei Volkslieder, \u201eWohl auf, ihr G\xe4ste\u201c, \u201eWhat shall we do with the drunken sailor\u201c und \u201eDas Herz\u201c, gelangen sehr gut, sodass sp\xe4ter auch Chorleiter Jens R\xf6th feststellte: \u201eHeute war die \u201eFrohe Stunde\u201c nah an ihrer optimalen Leistung\u201c. Dies sahen auch die Wertungsrichter Gerd Zellmann und Felix Resch so und gaben uns f\xfcr jeden Vortrag je 136 von 140 m\xf6glichen Punkten. Mit insgesamt 408 Punkte lagen wir 10 Punkte vor dem zweitplatzierten Chor aus Gr\xfcningen und immer noch 2 Punkte \xfcber dem erstplatzierten der Klasse M2. Auch Chorleiter Jens R\xf6th konnte sich verdientermasssen \xfcber den Gewinn des Dirigentenpreises freuen. Da die Bekanntgabe der Ergebnisse erst gegen 21.00 Uhr stattfand und die R\xfcckfahrt ca. 2 Std. dauern w\xfcrde verzichteten wir auf einen Empfang in der Heimat und verlegten die Feierlichkeiten kurzerhand an den Ort des Geschehens und die S\xe4nger machten auch regen Gebrauch davon. Hermann Kleppel von der Ortsgemeinde gratulierte uns noch am Abend per SMS zu diesem gro\xdfen Erfolg im Sauerland."}),"\n",(0,t.jsx)(n.h2,{id:"29-oktober---gruppenkritiksingen-in-nomborgn",children:"29. Oktober - Gruppenkritiksingen in Nomborgn"}),"\n",(0,t.jsx)(n.p,{children:"Das diesj\xe4hrige Gruppenkritiksingen fand am 29. Oktober in Nomborn statt. Zum Vortrag brachten wir hier den \u201eAbschied\u201c von Mathieu Neumann und \u201eO, wie herbe ist das Scheiden\u201c von Friedrich Silcher. Als Kritiker hatte die S\xe4ngergruppe III den K\xf6lner Domkapellmeister Eberhard Metternich engagiert. Durch seine verwandtschaftlichen Beziehungen nach Weroth und Steinefrenz war es f\xfcr ihn wie er selbst sagte \u201eeine schwierige Mission\u201c und f\xfcr ihn sicher nicht einfach, die Leistung unseres Chores objektiv zu beurteilen. Um den Vorwurf der Voreingenommenheit nicht aufkommen zu lassen h\xf6rte er unseren Vortr\xe4gen eher noch genauer zu. Da uns aber an diesem Tag ein sehr guter Auftritt gelang, blieb ihm aber nichts anderes \xfcbrig, als unserem Chor und Chorleiter eine Leistung auf einem Niveau zu bescheinigen, wie er es an diesem Morgen noch nicht geh\xf6rt hatte. Was ihm besonders gefiel, war die Art und Weise wie wir unsere Lieder vortrugen. \u201eJeder S\xe4nger steht \xfcber den Noten\u201c , \u201eder Chor singt wie aus einem Guss\u201c schrieb er in den Kritikbogen. Einige hilfreiche Anregungen, besonders beim \u201eAbschied\u201c, gab er uns noch mit auf den Weg. Die leichte Kritik, die er am 2. Tenor \xfcbte, \xfcberraschte sicher alle S\xe4nger. In einem anschlie\xdfenden Gespr\xe4ch mit dem Domkapellmeister versicherte er mir jedoch, dass nur die 2. Ten\xf6re solch schwierige Verbesserungsvorschl\xe4ge auch umsetzten k\xf6nnten. Genauere Details hierzu sind in der 3. Verlautbarung der Interessengemeinschaft 2.Tenor nachzulesen."}),"\n",(0,t.jsx)(n.h2,{id:"12-november---konzert-beim-kirchenchor-westerburg",children:"12. November - Konzert beim Kirchenchor Westerburg"}),"\n",(0,t.jsx)(n.p,{children:"Aus Anlass seines 100-j\xe4hrigen Jubil\xe4ums veranstaltete der Katholische Kirchenchor Westerburg am 12. November in der Westerburger Christ-K\xf6nigs-Kirche ein geistliches Konzert mit Meisterch\xf6ren. Neben dem MGV \u201eFrohsinn\u201c Staudt waren auch wir zur Mitwirkung eingeladen und kamen Dieser, allein schon wegen unserer besonderen Beziehung zu deren Chorleiter Wolfgang Malm, nat\xfcrlich gerne nach. Die gute Akustik der Kirche kam allen Mitwirkenden sehr entgegen, sodass am Ende jeder Chor mit seinen Leistungen zufrieden war. Auch das zahlreich erschienene Publikum best\xe4tigte dies mit seinem Applaus. Ein anschlie\xdfendes gem\xfctliches Beisammensein, bei dem sich der Westerburger Kirchenchor sehr gastfreundlich zeigte, rundete die insgesamt gelungene Veranstaltung ab."}),"\n",(0,t.jsx)(n.h2,{id:"18-november---eigenes-konzert-in-weroth",children:"18. November - Eigenes Konzert in Weroth"}),"\n",(0,t.jsx)(n.p,{children:"Ein weiterer H\xf6hepunkt des Jahres war das eigene Konzert im B\xfcrgerhaus am 18. November. Die Veranstaltung stand unter dem Motto \u201eDie Frohe Stunde l\xe4dt ein\u201c und viele Besucher waren dieser Einladung gefolgt, sodass die Halle fast bis auf den letzten Platz besetzt war. Als weitere Mitwirkende stellten sich an diesem Abend die M\xe4nner vom \u201eSiegtal-Quartett\u201c mit ihrem Dirigenten Pfarrer Ullrich Bals, sowie der Jugendchor der Verbandsgemeinde Wallmerod unter Hubertus Weimer dem Publikum vor. Ob es nun am voll besetzten Saal oder am \xe4u\xdferen Rahmen mit einem besonders sch\xf6n gestalteten B\xfchnenbild lag, auf jeden Fall war allen Beteiligten die Freude am Singen und Musizieren anzusehen und auch anzuh\xf6ren. Besonders die jungen M\xe4dchen vom Jugendchor \xfcberraschten mit gekonnt dargebotenen Vortr\xe4gen. Aber auch unser Chor konnte endlich auch einmal vor heimischem Publikum eine \xfcberzeugende Leistung bieten, wobei besonders das Schubert-St\xfcck \u201eRuhe, sch\xf6nstes Gl\xfcck der Erde\u201c hervorstach. Auch das \u201eSiegtal-Quartett\u201c konnte gefallen, sodass das Publikum nicht mit Beifall geizte. Den eindrucksvollen Abschluss einer gelungenen Veranstaltung bildete das von allen Mitwirkenden vorgetragenen \u201eMorgenrot\u201c von Robert Pracht. Danken m\xfcssen wir den Damen der Frauengemeinschaft und der Tanzgruppe, die f\xfcr die Zubereitung und Verkauf des Essens verantwortlich zeichneten. Ebenso Dank den jungen M\xe4nnern der Thekenmannschaft, die meisten aus den Reihen der Feuerwehr. Alle Helfer trugen mit dazu bei, dass dieser Konzertabend auch wirtschaftlich ein Erfolg wurde. Ein dickes Lob seitens des Vorstandes geht aber vor allem an alle S\xe4nger des Chores, die durch einen engagierten Kartenvorverkauf mit dazu beitrugen, dass die Halle ausverkauft war. Zuletzt noch ein Dankesch\xf6n an Jens R\xf6th, dem musikalischen Gesamtleiter der Veranstaltung, f\xfcr seine Arbeit im Vorfeld des Konzertes sowie f\xfcr die \xfcberaus erfolgreiche Zusammenarbeit im gesamten Jahr 2007."}),"\n",(0,t.jsx)(n.h2,{id:"19-januar---singen-am-ehrenmal-volkstrauertag",children:"19. Januar - Singen am Ehrenmal (Volkstrauertag)"}),"\n",(0,t.jsx)(n.p,{children:"Gleich am darauffolgenden Tag, dem 19. November, wirkten wir bei der Gedenkfeier am Ehrenmal mit. Unter unserem Vizechorleiter Christoph Weimer umrahmten wir die Feier mit zwei geistlichen Werken. Da der Seniorennachmittag in anderer Form durchgef\xfchrt wurde, war dies auch gleichzeitig der musikalische Abschlu\xdf im abgelaufenen Jahr."}),"\n",(0,t.jsx)(n.hr,{}),"\n",(0,t.jsx)(n.h2,{id:"was-passiert-2006-sonst-noch-",children:"Was passiert 2006 sonst noch ..."}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"24. Mai"})," - Begr\xfc\xdfung eines neuen Vereinswirts"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"10. Juni"})," - Hochzeit von Sandra und Carsten Eichmann"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"20. August"})," - 40-j\xe4hriges Priesterjubil\xe4um von Pater Ottmar Steinebach"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"26. September"})," - Silberne Hochzeit von Beate und Alois Sch\xf6nberger"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"03. Oktober"})," - 75. Geburtstag von Bernhard Metternich"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"18. Oktober"})," - 75. Geburtstag von Rudi Pehl"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"24. November"})," - Thekendienst beim Rockkonzert von Dominik Malm und Christian Eidt in Ebernhahn"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"Sonstiges"})," - Neuanschaffung der Sitzkissen f\xfcr die interessengemeinschaft II. Tenor"]}),"\n"]})]})}function g(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(l,{...e})}):l(e)}}}]);