"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[743],{8339:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>g,frontMatter:()=>a,metadata:()=>i,toc:()=>l});const i=JSON.parse('{"id":"archiv/bis1989/bis 1924","title":"bis 1924","description":"","source":"@site/docs/archiv/bis1989/bis 1924.md","sourceDirName":"archiv/bis1989","slug":"/archiv/bis1989/bis 1924","permalink":"/archiv-frohe-stunde/docs/archiv/bis1989/bis 1924","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/bis1989/bis 1924.md","tags":[],"version":"current","sidebarPosition":6,"frontMatter":{"sidebar_position":6,"description":""},"sidebar":"archivSidebar","previous":{"title":"1925-1949","permalink":"/archiv-frohe-stunde/docs/archiv/bis1989/1925-1949"}}');var t=r(4848),s=r(8453);const a={sidebar_position:6,description:""},d=void 0,h={},l=[{value:"1871 - Die ersten Versuche \u2013 Dirigent Lehrer Metternich",id:"1871---die-ersten-versuche--dirigent-lehrer-metternich",level:2},{value:"1872 - Erstmalig \u201eGesangverein\u201c",id:"1872---erstmalig-gesangverein",level:2},{value:"1887 - Lehrer Jungels \xfcbernimmt den Dirigentenstab...",id:"1887---lehrer-jungels-\xfcbernimmt-den-dirigentenstab",level:2},{value:"1898 - ...und gibt ab an Lehrer Gro\xdf",id:"1898---und-gibt-ab-an-lehrer-gro\xdf",level:2},{value:"1900 - Zuerst J\xfcnglingsverein mit neuem Chorleiter und dann...",id:"1900---zuerst-j\xfcnglingsverein-mit-neuem-chorleiter-und-dann",level:2},{value:"Offizielle Gr\xfcndung des M\xe4nnergesangvereins \u201eFrohe Stunde\u201c am 1. Oktober 1900",id:"offizielle-gr\xfcndung-des-m\xe4nnergesangvereins-frohe-stunde-am-1-oktober-1900",level:2},{value:"1903 - Fahnenweihe",id:"1903---fahnenweihe",level:2},{value:"1911 - Gesangwettstreit in Villmar",id:"1911---gesangwettstreit-in-villmar",level:2},{value:"1912 - Wettstreit in Wetzlar",id:"1912---wettstreit-in-wetzlar",level:2},{value:"1913 - \u201eKaiserfeiern\u201c",id:"1913---kaiserfeiern",level:2},{value:"1913 - Wettstreit in Limburg/L.",id:"1913---wettstreit-in-limburgl",level:2},{value:"1914 - 1918",id:"1914---1918",level:2},{value:"1922 - Wettstreit in Freiendiez",id:"1922---wettstreit-in-freiendiez",level:2},{value:"1922 - Ein neuer Dirigent \xfcbernimmt \u2013 Musiklehrer Heinrich Hannappel",id:"1922---ein-neuer-dirigent-\xfcbernimmt--musiklehrer-heinrich-hannappel",level:2},{value:"1923 - M\xe4nnergesang und Inflation",id:"1923---m\xe4nnergesang-und-inflation",level:2},{value:"1924 - Teilnahme am Festzug des MGV \u201eGermania-Liederbl\xfcte\u201c Elz (7. Juli)",id:"1924---teilnahme-am-festzug-des-mgv-germania-liederbl\xfcte-elz-7-juli",level:2},{value:"1924 - Wettstreit in Koblenz (8. August)",id:"1924---wettstreit-in-koblenz-8-august",level:2}];function u(e){const n={h2:"h2",p:"p",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.p,{children:"Der M\xe4nnerchorgesang hat in Weroth eine lange Tradition. Bereits vor \xfcber 100 Jahren, genauer 1871, sammelte der erste Lehrer des Dorfes, Herr Metternich, junge sangesfreudige M\xe4nner um sich, um ohne sonderlich straffe Bindung mit ihnen geistliche Lieder zu singen."}),"\n",(0,t.jsx)(n.h2,{id:"1871---die-ersten-versuche--dirigent-lehrer-metternich",children:"1871 - Die ersten Versuche \u2013 Dirigent Lehrer Metternich"}),"\n",(0,t.jsx)(n.p,{children:"Das \xe4lteste bislang bekannte Datum ist der 23.November 1871:"}),"\n",(0,t.jsx)(n.p,{children:"An diesem Tag weihte Herr Pfarrer Link aus Hundsangen die neue Schule in Weroth ein. Vom ersten \xf6ffentlichen Auftritt noch nicht fest organisierter S\xe4nger aus Weroth berichtet Lehrer Metternich in der Schulchronik:"}),"\n",(0,t.jsx)(n.p,{children:"\u201e...Bei der neuen Schule errichteten die S\xe4nger Ehrenpforten und eine kleine Allee von Tannenb\xe4umen, wozu die M\xe4dchen Kr\xe4nze bereit hielten.......; w\xe4hrend Herr Pfarrer Link zu (aus) Hundsangen das Hochamt hielt, sangen die S\xe4nger unter mei-ner (des Lehrers Metternich / Anm.d.Red.) Leitung ein vierstimmiges deutsches Amt."}),"\n",(0,t.jsx)(n.p,{children:"Herr Pfarrer Link benedicierte hierauf die R\xe4ume des Schulgeb\xe4udes und unterdes\xacsen sangen die S\xe4nger das sch\xf6ne Lied von Fr. Abt: \u201eGott gr\xfc\xdfe Dich !\u201c\r\nAlle Anwesenden begaben sich nun in das Schullokal und die Kinder sangen das Einweihungslied aus K\xf6lkenbeck: \u201eAlleluja, die Stund ist da !\u201c. Nachdem Herr Pfarrer Link noch eine Rede gehalten, sangen die S\xe4nger den Choral aus Roos Liederheft (Schuleinweihungslied): \u201eVom heiligen Gef\xfchl der Andacht !\u201c. Zum Schlu\xdf sangen alle mit gro\xdfer Begeisterung: \u201e Te Deum laudamus !\u201c"}),"\n",(0,t.jsx)(n.p,{children:"Das bereits in der Gr\xfcnderzeit der gesellige Teil nicht zu kurz kam und auch damals schon \u2013 wie heute nach manchem Wettstreit \u2013 die Gemeinde ihren Teil dazu beitr\xe4gt, beweist das Ende des Berichtes:"}),"\n",(0,t.jsx)(n.p,{children:"Des Nachmittags um 2 Uhr versammelten sich die Schulkinder, S\xe4nger und erwach\xacsenen M\xe4dchen im Lehrzimmer, wo eine halbe Ohm Bier (ca. 65\u201380 Liter, Anm.d.Red.) ihrer erwartete, die der l\xf6bliche Gemeinderat f\xfcr sie bestellt hatte."}),"\n",(0,t.jsx)(n.h2,{id:"1872---erstmalig-gesangverein",children:"1872 - Erstmalig \u201eGesangverein\u201c"}),"\n",(0,t.jsx)(n.p,{children:"Eine weitere Festivit\xe4t unter Beteiligung der S\xe4nger lie\xdf nicht lange auf sich warten. Erstmalig verwendet der Chronist den Begriff \u201eGesangverein\u201c:"}),"\n",(0,t.jsx)(n.p,{children:"Ein brillianter Fackelzug, bestehend aus Theilnehmern von allen Ortschaften des Kirchspiels Hundsangen bewegte sich am 21. M\xe4rz (1872; Anm.d.Red.), am Vor-abend des 25 j\xe4hrigen Priesterjubil\xe4ums unseres Herrn Pfarrers Link zu Hundsan-gen durch die Stra\xdfen von Hundsangen in den Pfarrhof, um diesen t\xfcchtigen, von Jedermann geachteten, geehrten und geliebten geistlichen Herrn zu begl\xfcckw\xfcn-schen. Abwechselnd sangen die Gesangvereine zu Hundsangen, Steinefrenz, Obererbach und Weroth passende, sch\xf6ne Lieder......\r\nJeder Gesangverein erhielt von dem Herrn Pfarrer eine halbe Ohm gutes Bier ge-spendet. ......F\xfcr mich und im Namen des Gesangvereins dahier, spreche ich Herrn Pfarrer Link meinen Dank aus."}),"\n",(0,t.jsx)(n.h2,{id:"1887---lehrer-jungels-\xfcbernimmt-den-dirigentenstab",children:"1887 - Lehrer Jungels \xfcbernimmt den Dirigentenstab..."}),"\n",(0,t.jsx)(n.p,{children:"Seit 1887 pflegte, Herr Lehrer Jungels, das Erbe seines verdienten Vorg\xe4ngers. Unter seiner Stabf\xfchrung bestand der Verein \u2013 zu einem solchen hatten sich die S\xe4nger inzwischen zusammengeschlossen \u2013 die ersten ausw\xe4rtigen Leistungspro\xacben, so auf den S\xe4ngerfesten in Hundsangen, P\xfctschbach und Wallmerod, sowie auf dem Kriegerfest in Thalheim."}),"\n",(0,t.jsx)(n.p,{children:"\xdcberhaupt geh\xf6rte offenbar die Gestaltung vaterl\xe4ndischer Veranstaltungen zum festen Repertoire des Vereins. Am 26. M\xe4rz 1897 beispielsweise veranstaltete der Gesangverein eine \u201eFestfeier\u201c anl\xe4\xdflich des Geburtstages seiner Majest\xe4t des Kai-sers Wilhelm des I. (Schulchronik Weroth)."}),"\n",(0,t.jsx)(n.h2,{id:"1898---und-gibt-ab-an-lehrer-gro\xdf",children:"1898 - ...und gibt ab an Lehrer Gro\xdf"}),"\n",(0,t.jsx)(n.p,{children:"Im Jahre 1898 folgte Herr Lehrer Gro\xdf als Dirigent, allerdings nur f\xfcr kurze Zeit. Pers\xf6nliche Schwierigkeiten nahmen ihm den Stab aus der Hand. Ohne F\xfchrung geriet der junge Verein in seine erste und schwerste Krise, die schlie\xdflich zu seiner Aufl\xf6sung f\xfchrte."}),"\n",(0,t.jsx)(n.h2,{id:"1900---zuerst-j\xfcnglingsverein-mit-neuem-chorleiter-und-dann",children:"1900 - Zuerst J\xfcnglingsverein mit neuem Chorleiter und dann..."}),"\n",(0,t.jsx)(n.p,{children:"Die Erinnerung an die in dem fr\xfcheren Verein erlebte Geselligkeit war Anla\xdf genug, sie in einem zun\xe4chst gegr\xfcndeten J\xfcnglinsverein wieder aufleben zu lassen. Dies allein gen\xfcgte jedoch den Vereinsmitgliedern nicht. Sie wollten, wie ihre V\xe4ter, auch singen, denn irgendwie hatte sie der Zauber des Gesangs ergriffen. Sie sahen in der Pflege des Chorgesanges eine sinnvolle Bet\xe4tigung zur eigenen Freude und zur Erbauung von Jung und Alt des ganzen Dorfes. Der J\xfcnglingsverein suchte sehr bald einen Chorleiter, den er in dem musikbegeisterten Johann Hecker aus Wallmerod fand."}),"\n",(0,t.jsx)(n.p,{children:"Noch am Pfingstsonntag des Jahres 1900 brachte dieser Verein dem 1. Primizianten der Gemeinde Weroth, Herrn Peter Sch\xf6nberger, zusammen mit der Schuljugend ein St\xe4ndchen."}),"\n",(0,t.jsx)(n.h2,{id:"offizielle-gr\xfcndung-des-m\xe4nnergesangvereins-frohe-stunde-am-1-oktober-1900",children:"Offizielle Gr\xfcndung des M\xe4nnergesangvereins \u201eFrohe Stunde\u201c am 1. Oktober 1900"}),"\n",(0,t.jsx)(n.p,{children:"Der uns heute als M\xe4nnergesangverein \u201eFrohe Stunde\u201c Weroth bekannte Verein wurde nach einem handschriftlichen Eintrag im S\xe4ngerbuch des Eduard Sch\xf6nber-ger am 1. Oktober des Jahres 1900 gegr\xfcndet. Da eine Gr\xfcndungsurkunde nicht mehr existiert, ist die nachfolgend abgedruckte, damals vielleicht eher belanglose Notiz f\xfcr den Verein zum wichtigen Dokument geworden"}),"\n",(0,t.jsx)(n.p,{children:"Seit diesem 1. Oktober des Jahres 1900 ist der Name \u201eFrohe Stunde\u201c aus der Geschichte und dem Leben des Dorfes nicht mehr wegzudenken. Waren es zun\xe4chst nur 21 junge M\xe4nner, die den Verein gr\xfcndeten, so konnte er in stetiger Fortent-wicklung im Jubil\xe4umsjahr 1950 sogar 60 aktive S\xe4nger aufweisen, eine Zahl, die bis heute nicht wieder erreicht wurde.\r\nSchnell erwarb sich der junge Verein das Vertrauen und die Sympathie der Dorfbe-v\xf6lkerung, die fortan die \u201eFrohe Stunde\u201c als \u201eihren Verein\u201c betrachtete.\r\nBis zum Jahre 1939, dem Beginn des 2. Weltkrieges, in dem sich auch \xfcber den Chorgesang der \u201eFrohen Stunde\u201c das gro\xdfe Schweigen breitete, ragen nachfolgende Daten wie Marksteine aus der \xe4u\xdferen Entwicklung des Vereins hervor:"}),"\n",(0,t.jsx)(n.h2,{id:"1903---fahnenweihe",children:"1903 - Fahnenweihe"}),"\n",(0,t.jsx)(n.p,{children:"Seine Fahnenweihe beging der Verein 1903. Zun\xe4chst im \u201eNassauer Boten\u201c vom 17. Januar 1903 die Voranzeige mit folgendem Text"}),"\n",(0,t.jsx)(n.p,{children:"Der Gesangverein \u201eFrohe Stunde\u201c hierselbst will am 28. und 29. Juni d.J. das Fest seiner Fahnenweihe begehen. Der Verein, dessen Dirigent Herr Lehrer Pabst ist, wird daf\xfcr sorgen, da\xdf das Fest einen recht angenehmen Verlauf nimmt."}),"\n",(0,t.jsx)(n.p,{children:"Auch innerhalb des Dorfes wurde formell zu dem festlichen Anla\xdf eingeladen.\r\nIm Protokoll der Sitzung des Kriegervereins Weroth vom14 Mai 1903 hei\xdft es:"}),"\n",(0,t.jsx)(n.p,{children:"Zu Anfang der Versammlung teilte der Vorsitzende den Vereinsmitgliedern mit, da\xdf der Gesangverein \u201eFrohe Stunde\u201c den Kriegerverein zu dem S\xe4ngerfeste am 28./29. Juni freundlichst einladet."}),"\n",(0,t.jsx)(n.h2,{id:"1911---gesangwettstreit-in-villmar",children:"1911 - Gesangwettstreit in Villmar"}),"\n",(0,t.jsx)(n.p,{children:"[7. Klassenpreis v. 10 Vereinen]"}),"\n",(0,t.jsx)(n.p,{children:"Dazu berichtet Lehrer M\xfcller \u2013 der damalige Dirigent \u2013 in der Schulchronik"}),"\n",(0,t.jsx)(n.p,{children:"Am 16. Und 17. Juli des Jahres hielt der Gesangverein \u201eTeutonia\u201c in Villmar a.d. Lahn sein 75-j\xe4hriges Jubelfest, verbunden mit einem gro\xdfen Gesangswettstreit ab. Genannter Verein hatte die G\xfcte unsern Verein \u201eFrohe Stunde\u201c zu dieser Veranstaltung herzlich einzuladen.\r\nEs gelang uns denn auch nach hartem Waffengang in der II. Landklasse den 7. Preis \u2013 bestehend in einer kostbaren Fruchtschale( im Archiv des Vereins vorhanden) \u2013 zu erringen."}),"\n",(0,t.jsx)(n.p,{children:"Da unser D\xf6rfchen nur 250 Einwohner hat, so d\xfcrfte diese Leistung, in Anbetracht der Tatsache, da\xdf wir mit Orten, welche eine 10 \u2013 20 mal gr\xf6\xdfere Einwohnerzahl aufweisen, (gesungen haben) nicht hoch genug anzuschlagen sein"}),"\n",(0,t.jsx)(n.p,{children:"Gro\xdf war dann die Freude, als die preisgekr\xf6nte Schar von dem ganzen Dorfe an der Station Steinefrenz mit Begeisterung empfangen und unter Marschgesang und Trommelwirbel nach dem Heimatd\xf6rfchen begleitet wurde, woselbst eine kleine Nachfeier den Schlu\xdfakkord bildete.\r\n\u201eIn Freud und Leid, zum Liede stets bereit\u201c\r\nsoll auch f\xfcr die Folge unsere Parole sein, auf da\xdf diesem ersten Siege noch weitere folgen m\xf6gen.\r\nWeroth, den 01. August 1911\r\nM\xfcller, Lehrer"}),"\n",(0,t.jsx)(n.p,{children:"In der II. Landklasse ( 25 \u2013 39 S\xe4nger) starteten 10 Vereine. Gesungen wurde im Saalbau des Lokals \u201eDeutscher Kaiser\u201c. Zum Vortrag kamen ein aufgegebener Preischor und ein Chor nach eigener Wahl. Aufgabechor war eine Komposition v. E. Heuser \u201eDie letzte Wacht\u201c nach einem Gedicht von E. Sch\xf6nenberger. Als Chor eigener Wahl sang die \u201eFrohe Stunde\u201c mit 30 S\xe4ngern \u201eAm Rhein\u201c v. C. Attenhofer."}),"\n",(0,t.jsx)(n.p,{children:"Vorsitzender in dieser Zeit war Peter Sch\xf6nberger. Wann genau dieser den Vorsitz von Johann P\xf6rtner III. \xfcbernahm ist nicht bekannt."}),"\n",(0,t.jsx)(n.h2,{id:"1912---wettstreit-in-wetzlar",children:"1912 - Wettstreit in Wetzlar"}),"\n",(0,t.jsx)(n.p,{children:"[3. Klassenpreis, 1. Ehrenpreis]"}),"\n",(0,t.jsx)(n.p,{children:"(aus der Schulchronik)\r\nDer 27. Mai (1912;Anm.d.Red.) war ein Freudentag f\xfcr unsere Gemeinde. Unserem Gesangverein \u201eFrohe Stunde\u201c gelang es auf dem Wettstreite in Wetzlar-Niedergirmes im Klassensingen den III. Geldpreis nebst Diplom zu erringen und zwar bei starker Konkurrenz."}),"\n",(0,t.jsx)(n.p,{children:"Bei dem III. Geldpreis handelte es sich um 50 Mark. Das Diplom lagert im Archiv des Vereins. In der Klasse III starteten insgesamt acht Vereine. Vor der \u201eFrohen Stunde\u201c (176 Punkte) lagen der \u201eLiederkranz\u201c Burgsolms (206) und der \u201eLiederkranz\u201c Dietkir\xacchen (213). Die \u201eFrohe Stunde\u201c sang neben dem Aufgabechor \u201eAuf der Wacht, das Volkslied \u201eLandsknechts-Biwak\u201c von Jul. Wengert.\r\n(Anm.d.Red.)"}),"\n",(0,t.jsx)(n.h2,{id:"1913---kaiserfeiern",children:"1913 - \u201eKaiserfeiern\u201c"}),"\n",(0,t.jsx)(n.p,{children:"Zwischen den Wettstreitaktivit\xe4ten gab es nat\xfcrlich auch andere Auftritte, z. B die 25 j\xe4hrige Jubil\xe4umsfeier seiner Majest\xe4t welche vom Kriegerverein ausgestaltet und"}),"\n",(0,t.jsx)(n.p,{children:"in Gemeinschaft mit dem Gesangverein \u201eFrohe Stunde\u201c, der Schuljugend des Ortes und der B\xfcrgerschaft am 15 Juni (1913;Anm d Red.), nachmittags um \xbd 4 Uhr im Saale des Herrn Gastwirts Eidt abgehalten wurde ......\r\nHier empfand man so recht, wie unser mehrfach preisgekr\xf6nter S\xe4ngerchor bei einer nationalen Feier nicht fehlen darf und wie er einer solchen Veranstaltung Leben und Glanz zu geben vermag."}),"\n",(0,t.jsx)(n.h2,{id:"1913---wettstreit-in-limburgl",children:"1913 - Wettstreit in Limburg/L."}),"\n",(0,t.jsx)(n.p,{children:"(3. Klassenpreis, 1.Ehrenpreis, H\xf6chster Ehrenpreis, 1.Dirigentenpreis)"}),"\n",(0,t.jsx)(n.p,{children:"Interessant ist der Ablauf eines solchen Wettstreites.\r\nEr begann f\xfcr die \u201eFrohe Stunde\u201c am Sonntag, dem 06. Juli 1913 vormittags mit dem ersten Wettsingen im Saal der alten Post."}),"\n",(0,t.jsx)(n.p,{children:"Eintritt kostete f\xfcr die Zuh\xf6rer 1 Mark.\r\nIn der III. Landklasse trat die \u201eFrohe Stunde\u201c diesmal gegen den MGV \u201eHoffnung\u201c Niederelbert und den MGV \u201eDie Quelle\u201c aus Fachingen an.\r\nIm Klassensingen gewann Fachingen (138 Punkte), vor Niederelbert (134) und Weroth (125). Der selbstgew\xe4hlte Chor \u201eWalderwachen\u201c v. Georg Rathgeber war wohl doch noch eine Nummer zu gro\xdf. Das selbstgew\xe4hlte Volkslied \u201eDie Seerose\u201c von Julius Wengert zum Ehrensingen pa\xdfte da wohl schon besser. Hier gab es mit 79 Punkten den ersten Platz (Elbert 71; Fachingen 78).\r\nNach dem sogogenannten Ersten Wettsingen folgte dann nachmittags um 15.00 Uhr der obligatorische Festzug, bei dem die \u201eFrohe Stunde\u201c mit den 25 gemeldeten S\xe4ngern an 34. Stelle mitmarschierte."}),"\n",(0,t.jsx)(n.p,{children:"Nach dem Eintreffen des Festzuges auf dem Festplatz Salmers Wiesen sang die festgebende \u201eEintracht\u201c aus Limburg einen Begr\xfc\xdfungschor, an den sich die Fest-rede und Gesangsvortr\xe4ge der nicht wettstreitenden Vereine anschlossen. Der Sonntag endete dann mit einem Konzert in der Festhalle und einem Volksfest auf dem Platz davor. Als Festhalle hatten die Limburger S\xe4nger eine 2000 Personen fassende Werkhalle der Firma Ohl gemietet.\r\nAm Montag vormittag begann dann erst um 9.00 Uhr das sog. II. Wettsingen bzw. H\xf6chste Ehrensingen. Der Aufgabechor hie\xdf \u201eSommernacht\u201c von J. Werth. und schien dem Chor zu liegen. F\xfcr eine \xfcberzeugende Leistung gab es 132 Punkte (Elbert 125;Fachingen 129) und als H\xf6chsten Ehrenpreis einen Pokal, gestiftet von der Bierbrauerfamilie Busch.\r\nAus der Tatsache, wer die neun Preise f\xfcr die Sieger beim h\xf6chste Ehrensingen stiftete, kann man im \xdcbrigen die patriotische Bedeutung des M\xe4nnergesangver-einswesens dieser Zeit unschwer erkennen. Den neunten und achten Preis stifteten die beiden Limburger Kriegervereine Germania und Teutonia (einen solchen Krieger\xacverein gab es - wie oben bereits erw\xe4hnt- auch in Weroth), den siebten der Landrat, den sechsten der Kaiserjahrgang \u2013 d.h. alle Limburger, die wie der Kaiser im Jahre 1859 geboren waren - , den f\xfcnften die Stadt Limburg, den vierten Freifrau von Dun\xacgern vom Schlo\xdf Dehrn, den dritten die Gro\xdfherzogin Hilda von Baden, ehemalige Prinzessin von Nassau, den zweiten und ersten schlie\xdflich die Kaiserin Auguste Viktoria und Kaiser Wilhelm II."}),"\n",(0,t.jsx)(n.p,{children:"Der zweite Festtag in Limburg aber mu\xdf unserem Verein unverge\xdflich sein. Da wurde er im Ehrensingen erster Sieger. Mit begeisterter Freude empfing damals Jung und Alt die mit dem 1. Ehrenpreis gekr\xf6nten S\xe4nger (aus der Chronik in der Festschrift des Vereins von 1925)"}),"\n",(0,t.jsx)(n.h2,{id:"1914---1918",children:"1914 - 1918"}),"\n",(0,t.jsx)(n.p,{children:"W\xe4hrend des 1. Weltkrieges ruhte dann offenbar die Vereinst\xe4tigkeit mehr oder we\xacniger. Aus dieser Zeit sind zumindest keinerlei kulturelle Aktivit\xe4ten bekannt. Nach dem 1. Weltkrieg ist Peter Sch\xf6nberger wieder 1. Vorsitzender des Vereins."}),"\n",(0,t.jsx)(n.h2,{id:"1922---wettstreit-in-freiendiez",children:"1922 - Wettstreit in Freiendiez"}),"\n",(0,t.jsx)(n.p,{children:"Im Festbuch des MGV \u201eGermania\u201c Freiendiez, da\xdf dieser anl\xe4\xdflich seines 60-j\xe4hrigen Bestehens am 01.,02. und 03. Juli 1922 herausgab, ist in der 3. Landklasse auch die \u201eFrohe Stunde\u201c aufgef\xfchrt. Nach dieser Unterlage hatte der Verein mit 29 S\xe4ngern unter dem Vorsitz von Peter Sch\xf6nberger mit seinem Chorleiter Lehrer M\xfcller zumindest eine Teilnahme angemeldet. Als selbstgew\xe4hltes Lied sollte \u201eMein Heimdorf im Schnee\u201c von Fritz Krasinsky und ,als selbsgew\xe4hltes Volkslied \u201eVerlorenes Lieb\u201c von Karl Fr. Gl\xfcck zur Auff\xfchrung kommen. Ob man an diesem Wettstreit tats\xe4chlich teilgenommen hat ist fraglich, denn bislang existieren dazu keine Belege."}),"\n",(0,t.jsx)(n.h2,{id:"1922---ein-neuer-dirigent-\xfcbernimmt--musiklehrer-heinrich-hannappel",children:"1922 - Ein neuer Dirigent \xfcbernimmt \u2013 Musiklehrer Heinrich Hannappel"}),"\n",(0,t.jsx)(n.p,{children:"1922 \xfcbernahm der Musiklehrer Heinrich Hannappel die F\xfchrung des Vereins. Der erst 22 Jahre alte, kunstbegeisterte junge Mann setzte seine ganze Kraft ein, den Verein zur h\xf6chsten Entfaltung zu bringen.\r\nNoch im selben Jahr konnte der Verein drei Winterkonzerte geben, die ihn weit und breit popul\xe4r machten.\r\nMit der Verpflichtung dieses Dirigenten hatte man offenbar auch Weitblick bewiesen."}),"\n",(0,t.jsx)(n.p,{children:'Hannappel entwickelte sich sp\xe4ter zu einem der f\xfchrenden Dirigenten im nassaui-schen S\xe4ngerbund . Von seinen offenbar in reichem Ma\xdf vorhandenen, guten Anla\xacgen profitierte als einer der ersten Ch\xf6re die \u201eFrohe Stunde\u201c. In Weroth verdiente er sich zumindest seine ersten Sporen. (Ende des Jahres 1933 dirigierte H. Hannappel den MGV \u201eLiederkranz\u201c Dietkirchen mit 50 S\xe4ngern, die S\xe4ngervereinigung \u201eGermania\u201c Elz mit 60 S\xe4ngern, den MGV \u201eTeutonia\u201c Villmar mit 80 S\xe4ngern, den MGV \u201eEintracht\u201c Runkel mit 60 S\xe4ngern, den MGV \u201eHarmonie\u201c Staffel mit 50 S\xe4n\xacgern und schlie\xdflich den MGV \u201eEintracht\u201c Limburg mit 100 S\xe4ngern).\r\nDer Kulturkritiker in Limburg \u2013 Domkapellmeister Hans Pabst \u2013 lobte sp\xe4terhin die vornehme Ruhe beim Dirigieren, das sparsame Umgehen mit Fortissimo und die gute stimmliche Ausbildung der S\xe4nger ( am Beispiel der Limburger "\u201eEintracht"), die nur durch flei\xdfiges A-capella-\xdcben erreicht werde. Hannappel scheine \xfcberhaupt \u201eseinem musikalischen Wesen entsprechend, immer mehr zu feinsinnigen, poeti\xacschen Werken zu greifen ..., die er mit liebevollem Eingehen auf die kleinsten Einzel\xacz\xfcge der Partitur\u201c herausarbeite und gestaltete (Nassauer Bote vom 13. Dezember 1932). Interessanterweise spiegelt sich diese Einstellung bei fast allen nachfolgen\xacden Chorleitern der \u201eFrohen Stunde\u201c wieder, so da\xdf die Wurzeln des anerkannt gu\xacten Chorklanges vielleicht bereits hier zu suchen sind.'}),"\n",(0,t.jsx)(n.h2,{id:"1923---m\xe4nnergesang-und-inflation",children:"1923 - M\xe4nnergesang und Inflation"}),"\n",(0,t.jsx)(n.p,{children:"1923 r\xfcstete die \u201eFrohe Stunde\u201c zu einem ersten Wettstreit mit dem neuen Dirigenten und zwar in Hahnst\xe4tten. Dieser wurde jedoch \u2013 wie so vieles andere \u2013 ein Opfer der Inflation.\r\nBis in diese Zeit geh\xf6rte die \u201eFrohe Stunde\u201c noch nicht zum damaligen S\xe4ngerkreis 9 (Unterwesterwald), sondern hatte sich mit den Brudervereinen in unmittelbarer Nachbarschaft in der sogenannten Gruppe \u201eW\xe4llerstein\u201c zusammengeschlossen. Vorsitzender dieser Vereinigung war der sp\xe4tere Ortsb\xfcrgermeister Johann Eidt.\r\nNun hing sich diese Gruppe organisatorisch an den S\xe4ngerkreis und Johann Eidt geh\xf6rte ab diesem Zeitpunkt als Gruppenvorsitzender zum erweiterten Kreisvorstand des S\xe4ngerkreises Unterwesterwald.\r\n(Die \xe4ltesten Aufzeichnungen der Chronik des S\xe4ngerkreises belegen danach Kaspar Solbach aus Ruppach-Goldhausen als Vorsitzenden der S\xe4ngergruppe 3 in der die Gruppe \u201eW\xe4llerstein\u201c aufging, bis Johann Eidt am 28.11.1937 erneut als neuer Vorsitzender der S\xe4ngergruppe 3 in den Kreisvorstand aufgenommen wurde. Dieses Amt f\xfchrte er auch nach dem Krieg fort und sicher nicht nur dieses Ehrenamt, sondern auch seine Verdienste als Vorsitzender der \u201eFrohen Stunde\u201c und sein patriarchalisches Wesen trugen dem \u201eVornesegger Ferrer\u201c den Beinamen \u201eS\xe4ngervater Eidt\u201c ein. Er war im ganzen Umland bekannt und sein breites, wohlmeinendes \u201eBra..., Bra..., Bravo !!!\u201cnach einem gelungenen Vortrag war geradezu sprichw\xf6rtlich.)"}),"\n",(0,t.jsx)(n.h2,{id:"1924---teilnahme-am-festzug-des-mgv-germania-liederbl\xfcte-elz-7-juli",children:"1924 - Teilnahme am Festzug des MGV \u201eGermania-Liederbl\xfcte\u201c Elz (7. Juli)"}),"\n",(0,t.jsx)(n.h2,{id:"1924---wettstreit-in-koblenz-8-august",children:"1924 - Wettstreit in Koblenz (8. August)"}),"\n",(0,t.jsx)(n.p,{children:"[4. Klassenpreis der 2. Landklasse]"}),"\n",(0,t.jsx)(n.p,{children:"Die k\xfchnste Tat vollbrachte der Verein im darauffolgenden Jahre; er wagte sich unter der Leitung seines jungen Dirigenten auf den gro\xdfen Gesangwettstreit in Koblenz. Die gef\xfcrchtete Konkurrenz der Rhein-, Ruhr- und Moselvereine aber mu\xdfte ihm doch einen Erfolg lassen. Mit dem 4. Klassenpreis der 2. Landklasse kehrten die S\xe4nger heim.(Quelle: Festschrift v 1925)"}),"\n",(0,t.jsx)(n.p,{children:"Der Chor pr\xe4sentierte sich bei diesem Wettstreit mit 43 aktiven S\xe4ngern.\r\n(Im Klassensingen \u201eTreue Liebe\u201c von Hirsch, Ehrensingen \u201eMorgen im Walde\u201c von Hegar, H\xf6chstes Ehrensingen \u201e`s Herz\u201c von Silcher)\r\nErstmalig erscheint im Festbuch des MGV \u201eViktoria\u201c Koblenz Adam Metternich als 1. Vorsitzender der \u201eFrohen Stunde\u201c."})]})}function g(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(u,{...e})}):u(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const t={},s=i.createContext(t);function a(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:a(e.components),i.createElement(s.Provider,{value:n},e.children)}}}]);