"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[5883],{5742:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>c,frontMatter:()=>a,metadata:()=>i,toc:()=>u});const i=JSON.parse('{"id":"archiv/2010-2019/2019","title":"2019","description":"H\xf6hepunkt des Jahres sind der Internationale Chorwettbewerb in Salzburg und unsere erste Open-Air Matinee am B\xfcrgerhaus.","source":"@site/docs/archiv/2010-2019/2019.md","sourceDirName":"archiv/2010-2019","slug":"/archiv/2010-2019/2019","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2019","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2010-2019/2019.md","tags":[],"version":"current","sidebarPosition":1,"frontMatter":{"sidebar_position":1,"description":"H\xf6hepunkt des Jahres sind der Internationale Chorwettbewerb in Salzburg und unsere erste Open-Air Matinee am B\xfcrgerhaus."},"sidebar":"archivSidebar","previous":{"title":"2010 bis 2019","permalink":"/archiv-frohe-stunde/docs/category/2010-2019"},"next":{"title":"2018","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2018"}}');var t=r(4848),s=r(8453);const a={sidebar_position:1,description:"H\xf6hepunkt des Jahres sind der Internationale Chorwettbewerb in Salzburg und unsere erste Open-Air Matinee am B\xfcrgerhaus."},d="2019",h={},u=[{value:"11. Januar \u2013 Jahreshauptversammlung",id:"11-januar--jahreshauptversammlung",level:2},{value:"31. M\xe4rz \u2013 Kritiksingen S\xe4ngergruppe Ost",id:"31-m\xe4rz--kritiksingen-s\xe4ngergruppe-ost",level:2},{value:"13. April \u2013 Benefizkonzert in L\xf6hnberg",id:"13-april--benefizkonzert-in-l\xf6hnberg",level:2},{value:"04. Mai \u2013 Konzert beim MGV \u201eConcordia\u201c Girod",id:"04-mai--konzert-beim-mgv-concordia-girod",level:2},{value:"15. Juni \u2013 Westerw\xe4lder Chorfest im St\xf6ffel-Park",id:"15-juni--westerw\xe4lder-chorfest-im-st\xf6ffel-park",level:2},{value:"20. \u2013 23. Juni \u2013 Internationaler Chorwettbewerb in Salzburg",id:"20--23-juni--internationaler-chorwettbewerb-in-salzburg",level:2},{value:"Donnerstag 20. Juni 2019 (Fronleichnam)",id:"donnerstag-20-juni-2019-fronleichnam",level:3},{value:"Freitag, 21. Juni 2019 (Wettbewerbstag)",id:"freitag-21-juni-2019-wettbewerbstag",level:3},{value:"Samstag 22. Juni",id:"samstag-22-juni",level:3},{value:"Sonntag 23. Juni 2019",id:"sonntag-23-juni-2019",level:3},{value:"25. August \u2013 Eigene Open-Air Matinee am B\xfcrgerhaus",id:"25-august--eigene-open-air-matinee-am-b\xfcrgerhaus",level:2},{value:"31. August \u2013 Chorfest des Chorverbandes Rheinland-Pfalz",id:"31-august--chorfest-des-chorverbandes-rheinland-pfalz",level:2},{value:"07. Dezember \u2013 Adventskonzert der Mimos Staffel",id:"07-dezember--adventskonzert-der-mimos-staffel",level:2},{value:"Was passiert 2019 sonst noch \u2026",id:"was-passiert-2019-sonst-noch-",level:2}];function l(e){const n={h1:"h1",h2:"h2",h3:"h3",header:"header",li:"li",p:"p",ul:"ul",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"2019",children:"2019"})}),"\n",(0,t.jsx)(n.h2,{id:"11-januar--jahreshauptversammlung",children:"11. Januar \u2013 Jahreshauptversammlung"}),"\n",(0,t.jsx)(n.p,{children:"Er\xf6ffnet wurde das Jahr 2019 mit unserer Jahreshauptversammlung am 11. Januar im Sebastianssaal unseres B\xfcrgerhauses. Wie auch in den letzten Jahren hielt man hier in den einzelnen Tagesordnungspunkten zun\xe4chst R\xfcckschau auf das Jahr 2018. Besonders ging man hier auf die erfolgreiche Teilnahme am Wettbewerb in Erbach sowie das wiederholte Erreichen des Meisterchortitels beim Leistungssingen in Wirges ein, und auch \xfcber das \xfcberaus gelungene Konzert mit dem philippinischen Gastchor aus Manila wurde nochmals Bilanz gezogen. Nach der einstimmigen Entlastung des Vorstandes stand nun der Hauptpunkt der Versammlung an. Die anwesenden Mitglieder, 25 Aktive und 3 Passive, mussten einen neuen Vorstand w\xe4hlen, wobei sich der bisherige Vorstand komplett nochmals zur Wahl stellte. Und so ist der neue Vorstand genau gleich besetzt wie der Alte. Und der sieht folgenderma\xdfen aus. Der gesch\xe4ftsf\xfchrende Vorstand besteht aus den Chorsprechern Hans-Georg Jung und Johannes Hannappel, meiner Wenigkeit als Gesch\xe4ftsf\xfchrer sowie Manfred Fachinger als Kassierer. In der S\xe4ule 2 sind Markus Fein als Notenwart, Andreas Kronimus als stellvertretender Gesch\xe4ftsf\xfchrer sowie Paul Weimer und Torsten Meuer als Beisitzer t\xe4tig. Die S\xe4ule 3 besteht aus Edi Hannappel als stellvertretender Kassierer und Norbert Hannappel, Kurt Konh\xe4user und Elias Hannappel als Beisitzer. Nach der Vorschau auf die Aktivit\xe4ten im Jahr 2019 endete die Versammlung mit dem Lied \u201eDas Morgenrot\u201c."}),"\n",(0,t.jsx)(n.h2,{id:"31-m\xe4rz--kritiksingen-s\xe4ngergruppe-ost",children:"31. M\xe4rz \u2013 Kritiksingen S\xe4ngergruppe Ost"}),"\n",(0,t.jsx)(n.p,{children:"Am 31. M\xe4rz des Jahres fand das Kritiksingen der S\xe4ngergruppe OST statt, was man jetzt Beratungssingen nennt, wobei sich au\xdfer der Namens\xe4nderung nichts am Ablauf der Veranstaltung ge\xe4ndert hat. Ausrichter war diesmal der M\xe4nnerchor Berod, Veranstaltungsort war die Ber\xf6der Pfarrkirche und die Verk\xf6stigung der S\xe4nger und G\xe4ste fand im Dorfgemeinschaftshaus statt. Als Wertungsrichter fungierte in Vertretung f\xfcr den erkrankten Erni Rein, der uns bestens bekannte angesehene Chorleiter und Juror J\xfcrgen Fa\xdfbender aus Lindenholzhausen. Pikant war dies auch in sofern, da J\xfcrgen Fa\xdfbender auch Jurymitglied beim intern. Wettbewerb in Salzburg sein wird und wir f\xfcr das Singen in Berod zwei St\xfccke \u2013 n\xe4mlich \u201eUbi caritas\u201c und \u201eBonjour, mon coeur\u201c \u2013 ausgew\xe4hlt hatten, die wir auch beim Wettbewerb in Salzburg gemeldet hatten. Es war also schon mal eine Standortbestimmung f\xfcr den Wettbewerb. Gut vorbereitet betraten wir so am fr\xfchen Nachmittag als letzter der 10 teilnehmenden Ch\xf6re den Altarraum der Ber\xf6der Kirche. Sehr konzentriert brachten wir unsere St\xfccke vor und konnten sie auch teilweise schon gut zelebrieren. Auch J\xfcrgen Fa\xdfbender war von unseren Vortr\xe4gen sehr angetan und lobte uns in den h\xf6chsten T\xf6nen. Doch bei all dem Lob merkte er auch einige Dinge an, wo es noch Verbesserungspotential im Chor gibt. Besonders bei dem Liebeslied \u201eBonjour, mon coeur\u201c w\xfcrde uns ein freundlicheres Minenspiel sehr helfen und auch dem Betrachter verdeutlichen um was es sich bei diesem St\xfcck handelt. Alles in allem konnten wir mit unserem Auftritt in Berod sehr zufrieden sein."}),"\n",(0,t.jsx)(n.h2,{id:"13-april--benefizkonzert-in-l\xf6hnberg",children:"13. April \u2013 Benefizkonzert in L\xf6hnberg"}),"\n",(0,t.jsx)(n.p,{children:"In L\xf6hnberg, der Heimatgemeinde unseres Chorleiters Jens R\xf6th, gibt es schon seit geraumer Zeit in den R\xe4umen des ehemaligen Schulgeb\xe4udes das Demenzzentrum L\xf6hnberg. Diese Pflegeeinrichtung wurde vor allem als weitere Unterst\xfctzung und Hilfe bei der Pflege von Personen geschaffen, die an Demenz leiden. Um diese Einrichtung auch finanziell zu unterst\xfctzen, entwickelten der L\xf6hnberger B\xfcrgermeister Frank Schmitt gemeinsam mit Jens R\xf6th die Idee eines Benefizkonzertes. Und als Jens bei uns nachfragte, ob wir dabei mitwirken wollten, sagten wir nat\xfcrlich gerne zu. Das Konzert fand am 13. April im ziemlich neugebauten B\xfcrgerhaus \u201eL\xf6hnberger Lilie\u201c statt. Neben den S\xe4ngern der \u201eEintracht\u201c aus Frickhofen und weiteren Gesangsgruppen wirkten noch ein Musikverein sowie die Solisten Christina Kassel (Sopran) sowie Lea Wagenbach (Piano) und ihr Partner (Klarinette) mit. Dem zahlreich erschienenen Publikum wurde ein abwechslungsreiches Programm geboten und der Applaus der Zuh\xf6rer zeigte, das es bei den Anwesenden auch gut ankam. Auch unser Auftritt wurde mit viel Beifall bedacht, sodass wir ohne eine Zugabe nicht von der B\xfchne kamen. Und eins bleibt wohl auch immer gleich, egal an welcher Veranstaltung wir mitwirken, wir m\xfcssen immer als Letzte die Hallen abschlie\xdfen. Erst nach dem Anstimmen einiger Lieder fuhren wir zu sp\xe4ter Stunde wieder zur\xfcck in Richtung Heimat."}),"\n",(0,t.jsx)(n.h2,{id:"04-mai--konzert-beim-mgv-concordia-girod",children:"04. Mai \u2013 Konzert beim MGV \u201eConcordia\u201c Girod"}),"\n",(0,t.jsx)(n.p,{children:"Aus Anlass seines 110-j\xe4hrige Bestehens veranstaltete der MGV \u201eConcordia\u201c Girod am 4. Mai ein Festkonzert und wir hatten der Einladung zur Mitwirkung gerne angenommen. Neben uns wirkten noch weitere 6 Ch\xf6re aus der direkten Nachbarschaft mit, von Berod \xfcber Steinefrenz bis Heiligenroth. Dadurch, dass alle Chorgattungen vorhanden waren, entwickelte sich ein sehr abwechslungsreiches Programm was beim Publikum durchaus sehr gut ankam. Auch wir wussten mit unserem Auftritt durchweg gut zu gefallen, was wohl auch an der gut gew\xe4hlten Programmauswahl lag. Die zumeist modernen St\xfccke brachten wir mit der n\xf6tigen lockeren und doch musikalisch exakten Einstellung vor, sodass uns das Publikum mit lang anhaltendem Beifall belohnte. Alles in allem, eine durchaus gelungene Veranstaltung auf sehr gutem Niveau, sieht man mal von dem Moderator des Abends ab."}),"\n",(0,t.jsx)(n.h2,{id:"15-juni--westerw\xe4lder-chorfest-im-st\xf6ffel-park",children:"15. Juni \u2013 Westerw\xe4lder Chorfest im St\xf6ffel-Park"}),"\n",(0,t.jsx)(n.p,{children:"Der Chorverband Westerwald veranstaltete am 15. Juni im St\xf6ffel-Park, Enspel zum ersten Mal ein Westerw\xe4lder Chorfest. Zahlreiche Ch\xf6re aus dem Westerwald sowie aus benachbarten Chorverb\xe4nden waren der Einladung zur Mitwirkung gefolgt und machten das ganze Event zu einer insgesamt gelungenen Veranstaltung. Auch wir nahmen an diesem S\xe4ngertreffen teil und nutzten unseren Auftritt gleichzeitig als Generalporbe f\xfcr den Wettberb in Salzburg. Auf dem stillgelegten Werksgel\xe4nde wurde auf 4 verschiedenen B\xfchnen gesungen, wir hatten unseren Auftritt in der alten historischen Werkstatt. Auch wenn die Akustik des Raumes sehr eigenartig klang, gelangen unsere Vortr\xe4ge doch recht gut, soda\xdf wir dem heftig aplaudierendem Publikum noch eine Zugabe zu Geh\xf6r brachten."}),"\n",(0,t.jsx)(n.h2,{id:"20--23-juni--internationaler-chorwettbewerb-in-salzburg",children:"20. \u2013 23. Juni \u2013 Internationaler Chorwettbewerb in Salzburg"}),"\n",(0,t.jsx)(n.h3,{id:"donnerstag-20-juni-2019-fronleichnam",children:"Donnerstag 20. Juni 2019 (Fronleichnam)"}),"\n",(0,t.jsx)(n.p,{children:"Streng nach dem Ablaufplan, den Andreas Kronimus wieder in gewohnter Pr\xe4zision gefertigt hatte, begann unsere Tour nach Salzburg am 20. Juni 2019 um ca. 04:30 Uhr am B\xfcrgerhaus. Dabei w\xe4ren fast noch ein paar \u201eblinde\u201c Passagiere mit an Bord gekommen, die sich aber dann doch anders entschieden. F\xfcnfzig Passagiere im Bus, in einem 9\xb4er Bus acht weitere Mitfahrer und vier Personen in einem Privat-Pkw starteten ann\xe4hernd zeitgleich und trafen sich bei einer ersten Rast. Danach setzte man unabh\xe4ngig voneinander die Reise fort. Bei der ersten gr\xf6\xdferen Pause mit dem \u201egro\xdfen\u201c Bus und Weck, Worscht und Bier zeichnete sich bereits die hervorragende Stimmung ab, die uns w\xe4hrend der kompletten Fahrt begleiten sollte. In Salzburg angekommen, galt es, den sog. Slot zu erwischen, ein Zeitfenster von 20 Minuten, das die Stadt Salzburg den Reisebussen an einem Busterminal am Rande der Altstadt zugesteht und das gerade zum Ein- und Aussteigen reicht. Alles klappte perfekt und Busfahrer Laszlo von der Fa. Menges (der uns auch schon 2013 nach Verona gefahren hatte) parkte den Bus anschlie\xdfend auf einem Parkplatz in der Peripherie. Bus und Fahrer wurden dann bis zur Abfahrt sonntags dank der zentralen Lage unserer Hotels \u201eKasererbr\xe4u\u201c und \u201eWei\xdfe Taube\u201c nicht mehr gebraucht."}),"\n",(0,t.jsx)(n.p,{children:"Danach stand der Nachmittag zur freien Verf\xfcgung, weil die geplante Parade durchaus im Sinne der meisten Mitfahrer wegen schlechter Wetterprognosen ausfiel und es galt, erste Eindr\xfccke von Salzburg zu sammeln. Ein zeitgleich auf dem Domplatz stattfindendes Blasmusikfestival half dabei dem ein oder anderen, die Wartezeit mit entsprechenden Kaltgetr\xe4nken bis zum Er\xf6ffnungskonzert am Abend zu \xfcberbr\xfccken. Lediglich Jens, Hans-Georg mit Kirsten und Johannes mit Sabine begaben sich in offizieller Mission ins Mozarteum, um den Chor zum Wettbewerb anzumelden und ein paar organisatorische Dinge abzusprechen. Tats\xe4chlich gab es dann in der Folge im w\xf6rtlichen Sinn aus heiterem Himmel das angek\xfcndigte Unwetter, so dass sich einige triefend nass nur noch ins n\xe4chste Lokal retten, andere aber dann doch auch noch das Er\xf6ffnungskonzert besuchen konnten. Der gro\xdfe Saal des Mozarteums war bis auf den letzten Platz gef\xfcllt und bot tats\xe4chlich die eindrucksvolle Kulisse, die man sich im Vorfeld erhofft hatte. Er erinnerte stark an den Majakowskisaal in Prag, was bereits als gutes Omen gedeutet wurde. Die Ch\xf6re boten durchwegs hochklassigen Gesang, wobei sich bereits hier der sp\xe4tere Gro\xdfpreisgewinner aus Singapoor besonders auszeichnete. Der erste Tag endete dann witterungstechnisch doch vers\xf6hnlich unter einem nur noch wolkenverhangenen Himmel, was den ein oder anderen Absacker provozierte, ohne dass man von offizieller Seite irgendwelche Ausw\xfcchse zu beklagen gehabt h\xe4tte."}),"\n",(0,t.jsx)(n.h3,{id:"freitag-21-juni-2019-wettbewerbstag",children:"Freitag, 21. Juni 2019 (Wettbewerbstag)"}),"\n",(0,t.jsx)(n.p,{children:"Nach einem einigerma\xdfen chaotischen Fr\xfchst\xfcck (was sich zum Gl\xfcck an den Folgetagen nicht wiederholen sollte), trafen sich Chorleiter und S\xe4nger und begaben sich fu\xdfl\xe4ufig zur Stellprobe im Mozarteum. Im Erdgescho\xdf begegneten wir zun\xe4chst noch der direkten Konkurrenz vom MGV \u201eConcordia\u201c Gunzenbach und er\xf6rterten gemeinsam die \u201eGefechtslage\u201c. Daneben stie\xdfen auch Thomas van Bajin und Alois Gotthardt zu uns, die sich bereits urlaubsbedingt in Salzburg aufhielten. Eine erste Teststellprobe f\xfcrs Gruppenfoto gab es am Treppenaufgang zum Gro\xdfen Saal des Mozarteum, die gemessen an einer \xe4hnlichen \u201eSituation\u201c in Prag doch noch ziemlich chaotisch verlief. Routiniert absolvierte man dann aber anschlie\xdfend die eigentliche Stellprobe auf der B\xfchne des Gro\xdfen Saals, nachdem wir unmittelbar zuvor intern mit den Beteiligten \xfcberein gekommen waren, Michael P\xf6rtners Platz in der 1. Reihe des ersten Bass mit Daniel Rick und Werner Blatt zu besetzen. Die urspr\xfcnglich geplante dritte Reihe im 1. Bass r\xfcckte komplett nach und wir konnten damit insgesamt in zwei Reihen auftreten, so wie wir dies urspr\xfcnglich schon einmal geplant hatten. Dank der Aufmerksamkeit und Disziplin aller Beteiligten war es uns sogar noch m\xf6glich, etliche Takte des Programms und das \u201eMy souls \u2026\u201c in G\xe4nze zu singen. Die Akustik lie\xdf nichts zu w\xfcnschen \xfcbrig, so dass sich auch von dieser Seite ideale Verh\xe4ltnisse f\xfcr den Wettbewerb abzeichneten, was vor dem Mozarteum nat\xfcrlich noch einmal Alles ausf\xfchrlich diskutiert wurde. Entsprechend motiviert ging man zur\xfcck zum Hotel, w\xe4hrend die Mitfahrer- und -rinnen ein ausf\xfchrliches Besichtigungsprogramm absolvierten. S\xe4nger und Chorleiter mussten sich auf dem R\xfcckweg ins Hotel mit einer Kurzfassung und stark komprimierten Eindr\xfccken von der sch\xf6nen Altstadt Salzburgs zufriedengeben."}),"\n",(0,t.jsx)(n.p,{children:"Letzte Instruktionen von Jens gab es anschlie\xdfend im hoteleigenen Kino (ganz bestimmt das \xe4lteste hoteleigene Kino \xd6sterreichs), dass zwar nicht die beste Luft, aber doch H\xf6rsaalniveau bot und auch hier wieder eine gut gelaunte und konzentrierte S\xe4ngerschaar erlebte. Danach begann dann f\xfcr Jeden die mentale und physische Vorbereitung weitestgehend in Eigenregie, entweder auf dem eigenen Zimmer oder in einem der vielen Gastst\xe4tten der Stadt."}),"\n",(0,t.jsx)(n.p,{children:"Dann wird\xb4s spannend, denn am sp\xe4ten Nachmittag ist der Wettbewerbsauftritt angesagt. P\xfcnktlich geht es zun\xe4chst in den Innenhof des nicht weit vom Mozarteum gelegenen Priesterseminars, in dem Alois Gotthardt logiert. Er hatte dankenswerterweise f\xfcr den Chor auch diese tolle Lokation zum Einsingen kurzfristig organisiert. Lediglich ein paar \xdcbungen zum Lockern der Stimmb\xe4nder und das unvermeidliche \u201eWohin soll ich mich wenden\u2026\u201c m\xfcssen jetzt als Grundlage f\xfcr einen idealen Auftritt ausreichen. Nach kurzer Begr\xfc\xdfung unserer Konkurrenz aus Gunzenbach im Foyer betreten wir schlie\xdflich so ca. 17:40 Uhr die B\xfchne im Gro\xdfen Saal des Mozarteum Salzburg und begr\xfc\xdfen \u2013 \xfcberwiegend freundlich l\xe4chelnd \u2013 die auf der Empore sitzende Jury. Unter den Augen und Ohren der mitgereisten Frauen und sonstigen Fans der FROHEN STUNDE gelingt ein in der unmittelbaren R\xfcckschau nach eigenen Ma\xdfst\xe4ben fehlerfreier Auftritt. Mit dem \u201eBon jour mon coer\u201c als gelungenem Entree, einem sauber intonierten \u201eUbi caritas\u201c und einem wirkungsvollen \u201eMy souls \u2026\u201c, bei dem der kleine Chor \u2013 durch die H\xf6he der Stufen etwas \xfcberrascht \u2013 sich dennoch rechtzeitig zu gemeinsamer Aufstellung und passgenauem Einsatz zusammenfindet, setzen S\xe4nger und Chorleiter im Wesentlichen all die Dinge um, die man in m\xfchevoller Probenarbeit als Optimum des Machbaren einstudiert hat. Mit der eigenen Leistung also vollauf zufrieden, kann man nun die Entscheidung \xfcber die musikalische Wertigkeit gemessen an den Rahmenbedingungen eines internationalen Wettbewerbs getrost der Jury \xfcberlassen und sich dem anschlie\xdfenden Begegnungskonzert in der Kirche \u201eSt. Andr\xe4\u201c zuwenden."}),"\n",(0,t.jsx)(n.p,{children:"Zeuge dieser Sternstunde des Chores wird im \xdcbrigen auch die Zufallsbekanntschaft unseres S\xe4ngers Christoph Weimer, eine gewisse Hera Lind, Ihres Zeichens ausgebildete Konzerts\xe4ngerin und Bestsellerautorin. Christoph hatte sie als vermeintliche Dirigentin in der Stadt angesprochen und nach Kl\xe4rung ihrer \u201ewahren Identit\xe4t\u201c von den Veranstaltungen im Mozarteum berichtet. Tats\xe4chlich nahm Hera Lind diesen Hinweis auf und wurde vielfach als aufmerksame Beobachterin der Ereignisse gesichtet. Sie bedankte sich gar im Anschluss an die Veranstaltungen ausdr\xfccklich bei Christoph f\xfcr seine unvermittelte Ansprache, die sie f\xfcr sich wohl als lebensverl\xe4ngernde Ma\xdfnahme einstufte. W\xf6rtlich sagte sie in Etwa: \u201eSie haben mir zwei Tage meines Lebens geschenkt\u201c."}),"\n",(0,t.jsx)(n.p,{children:"Das sich direkt an den Wettbewerb anschlie\xdfende Begegnungskonzert in der Kirche \u201eSt. Andr\xe4\u201c sah dann angesichts eines gelungenen Wettbewerbs entsprechend freudig strahlende Gesichter im Altarraum, als man sich zum gemeinsamen Singen mit Ch\xf6ren aus aller Herren L\xe4nder zusammenfand. Dort begeisterte besonders der tschechische Kinderchor mit seinen herausragenden Solistinnen. Begleitet wurden die Auftritte der Protagonisten von Blitz und Donner eines Gewitters, das in einer nicht enden wollenden Sturzflut endete und die jetzt hungrige und vor allem durstige Chorfamilie der FROHEN STUNDE nach Ende des Konzerts zun\xe4chst daran hinderte, zum \u201eSternbr\xe4u\u201c zu gelangen. In dieser alt eingesessenen Lokalit\xe4t hatte man bereits im Vorfeld entsprechende R\xe4umlichkeiten geordert, die es jetzt nur noch irgendwie einigerma\xdfen trocken zu erreichen galt. Getreu dem Motto \u201ealle Wege f\xfchren zum Sternbr\xe4u\u201c, erreichten schlie\xdflich aber doch alle \u2013 wenn auch in einem recht bunten Durcheinander \u2013 mittels Taxi zu Fu\xdf oder wie auch immer den angemieteten Saal im ersten Obergeschoss. Hier traf man dann gl\xfccklicherweise auf Profis der Gastronomie, die angesichts der etwas wirr und chaotisch auflaufenden Schar v\xf6llig unbeeindruckt in k\xfcrzester Zeit f\xfcr eine zufriedenstellende Nahrungsaufnahme sorgten und auch bei der Logistik f\xfcr die sich \xfcberschlagenden Getr\xe4nkebestellungen kaum W\xfcnsche offen lie\xdfen."}),"\n",(0,t.jsx)(n.p,{children:"Allerdings beginnt hier auch das \u201eDrama um die Jacken\u201c, das die lustige Schar noch l\xe4ngere Zeit besch\xe4ftigen sollte. Das Bem\xfchen vieler S\xe4nger, ihre meist stark durchfeuchtete schwarze Jacke wiedererkennungsf\xe4hig an der gemeinsamen Garderobe zu platzieren, geriet n\xe4mlich zum Desaster. Einfach zu viele hatten gleichzeitig dieselbe geniale Idee, n\xe4mlich entweder das gute St\xfcck \u201eganz rechts hinzuh\xe4ngen\u201c oder den Teilnehmerausweis als Erkennungsmerkmal zu verwenden, um beim Verlassen der Lokalit\xe4t jede Verwechslung auszuschlie\xdfen. Bereits beim Verlassen der guten Stube zur Fortsetzung des Abends eine Etage tiefer, waren deshalb erste Ungereimtheiten und vor\xfcbergehende Verluste zu beklagen. Der Abend endete allerdings zun\xe4chst dennoch bei fast Allen mit bester Laune und sehr individuell. Einzelne Darbietungen auf dem Heimweg zum Hotel wurden gar von aufmerksamen Salzburger B\xfcrgern ganz gezielt im buchst\xe4blichen Sinn mit fl\xfcssigen Aufmerksamkeiten \xfcbersch\xfcttet. Ungekl\xe4rt blieb allerdings der Verbleib eines gewissen Roy Robson Gr\xf6\xdfe 54"}),"\n",(0,t.jsx)(n.h3,{id:"samstag-22-juni",children:"Samstag 22. Juni"}),"\n",(0,t.jsx)(n.p,{children:"Samstag, der 22. Juni sollte der Tag der Entscheidung werden und bereits beim Fr\xfchst\xfcck entspannten sich erste Diskussionen dar\xfcber, wie denn unser Wettbewerbsauftritt nun tats\xe4chlich bei der Jury angekommen war, ob es f\xfcr ein Golddiplom gereicht hatte und wir m\xf6glicherweise beim Singen um den Gro\xdfpreis teilnehmen w\xfcrden."}),"\n",(0,t.jsx)(n.p,{children:"Angespannt aber dennoch guter Dinge lie\xdf man sich es dann \xfcber den Tag hinweg gut gehen. Einige besuchten den weiterlaufenden Wettbewerb, andere nutzten die Zeit zum Sightseeing und wieder andere zum Shoppen. Dank der f\xfcr die FROHE STUNDE neu eingef\xfchrten Kommunikationsplattform \u201eCocuun\u201c waren alle Teilnehmer zeitgleich in Echtzeit erreichbar, so dass zu keiner Zeit Hektik entstehen konnte. Als sich z.B. im Laufe des Nachmittags kein erneuter Auftritt abzeichnete, wurde \xfcber Cocuun angesichts erneut absehbaren \u201eschweren Wetters\u201c gegen 16:00 Uhr f\xfcr den Abschlussabend mit Gro\xdfpreissingen und Preisverleihung die Order zum Freizeithemd mit Jeans ausgegeben, um f\xfcr alle Individualit\xe4ten ger\xfcstet zu sein. Dies erm\xf6glichte Allen die freie Gestaltung der verbleibenden Zeit."}),"\n",(0,t.jsx)(n.p,{children:"Im Hintergrund fand auf dieser Ebene auch das Drama um die Jacken seine Fortsetzung. Eine kurzzeitige Entspannung zeichnete sich ab, als Roy Robson Gr. \u201eWie auch immer zur\xfcckfand\u201c, allerdings fehlte jetzt eine solche Gr\xf6\xdfe 54 und dann war ja immer auch noch eine Strellson zuviel, die dann zwar zugeordnet werden konnte, aber gleichzeitig zum \xdcberhang einer Benvenuto f\xfchrte. Ein S\xe4nger machte sich \u2013 wahrscheinlich unbegr\xfcndet \u2013 Sorgen, dass man seine Jacke weiten w\xfcrde, ein anderer konstatierte zutreffend, Zitat: \u201cWas ein Gl\xfcck, dass wir nur Jacken suchen und finden und keine kleinen Kinder dabei haben. Aber auch an dieser Front blieb der alles entscheidende Anruf an diesem Tag aus. Tats\xe4chlich fand das \u201eDrama um die Jacken\u201c erst nach der Ankunft in Weroth seinen Abschluss."}),"\n",(0,t.jsx)(n.p,{children:"W\xe4hrend Jens am sp\xe4ten Nachmittag bereits beim Jurygespr\xe4ch vor Ort weilte, um mit einem konstruktiv kritischen fachlichen Feedback der Juroren in Einzelgespr\xe4chen den Wettbewerbsauftritt der FROHEN STUNDE hoffentlich auch gewinnbringend zu analysieren, fand sich die Chorfamilie dann doch sehr gespannt direkt von \xfcberall her am und im Mozarteum ein. Bereits die Sitzordnung beim Preistr\xe4gerkonzert, nach der unsere Mitstreiter aus Gunzenbach mit uns auf gleicher H\xf6he im Raum sa\xdfen und der Umstand, dass auch die Gunzenb\xe4cher S\xe4nger keine Einladung zum Preistr\xe4gerkonzert erhalten hatten, schien dabei bestimmte Schl\xfcsse zuzulassen. Zumindest wurde mit diesen mutma\xdflichen Parallelen der Eindruck unserer Fans best\xe4tigt, die beide Ch\xf6re im Wettbewerb geh\xf6rt und ann\xe4hernd gleichauf gesehen hatten."}),"\n",(0,t.jsx)(n.p,{children:"Das eigentliche Preistr\xe4gerkonzert verlief dann zun\xe4chst zwar \xe4u\xdferst unterhaltsam auf h\xf6chstem musikalischem Niveau, erfuhr dann aber tats\xe4chlich seinen emotionalen H\xf6hepunkt in der Pause, w\xe4hrend der sich die Jury zur Beratung zur\xfcckgezogen hatte. Flashmobartig nahm n\xe4mlich jetzt der Jugendchor aus S\xfcdafrika die B\xfchne und weitere Stuhlreihen davor in Besitz. Es war schon ein erhebender Moment, als sich auf den ganzen Saal \xfcbergreifend pl\xf6tzlich f\xfcr wenige Augenblicke Menschen aus S\xfcdafrika und Norwegen, aus Singapoor und Weroth, de facto aus allen Ecken und Enden der Erde eigeninitiativ und zwanglos musikalisch in den Armen lagen. Dies macht den besonderen Reiz internationaler Wettbewerbe aus. Ich glaube jeder, der solche Augenblicke bislang als S\xe4nger der FROHEN STUNDE erleben durfte, wird sie unabh\xe4ngig vom musikalischen Ergebnis als pers\xf6nlichen Gewinn erfahren haben. Davon unabh\xe4ngig bleibt nat\xfcrlich auch der Gewinn eines wie auch immer gearteten Diploms als Gemeinschaftserlebnis erkl\xe4rtes Ziel jedes Wettbewerbs und dementsprechend fieberte dann nat\xfcrlich auch jeder dem Ende der Pause entgegen."}),"\n",(0,t.jsx)(n.p,{children:"Als sich schlie\xdflich alle Vertreter der Ch\xf6re zur Bekanntgabe der Wertung und Entgegennahme der Preise auf der B\xfchne eingefunden hatten und nach dem \xfcblichen protokollarischen Gepr\xe4nge verlautbarte, dass es nur vier silberne, ansonsten aber Golddiplome geben w\xfcrde, fiel bei dem Ein oder Anderen schon ein Teil der Spannung ab. Als dann die Sprecherin zur Kategorie der M\xe4nnerch\xf6re kam und wie bei allen Ansagen zun\xe4chst in englischer Sprache einleitend darauf hinwies, dass es in dieser Klasse keinen Sieger bzw. Verlierer geben w\xfcrde, sondern die Jury beide Ch\xf6re dem allgemeinen Eindruck entsprechend gleichauf gesehen hatte, war schon ein wesentliches Ziel, n\xe4mlich nicht hintan zu stehen, erreicht. Schlie\xdflich gab es dann kein Halten mehr, als feststand,\u201c beide M\xe4nnerch\xf6re haben sich mit 22,97 Punkten ein Golddiplom des Level III ersungen\u201c."}),"\n",(0,t.jsx)(n.p,{children:"Freudestrahlend nehmen Hans-Georg, Jens und Johannes aus der Hand von Piroschka Horvath Golddiplom und Wertung entgegen und gesellen sich anschlie\xdfend im allgemeinen Freudentaumel in die Schar der S\xe4nger und mitgereisten Fans ein."}),"\n",(0,t.jsx)(n.p,{children:"Mit Weitblick waren Einzelne direkt im Anschluss bem\xfcht, den anstehenden Feierlichkeiten im Sternbr\xe4u den entsprechenden Rahmen zu sichern, allerdings war dort nichts mehr frei. Erneut zeigte sich aber die Flexibilit\xe4t dieser tollen Truppe, die Dank dieser Bem\xfchungen, der Anstrengungen des Sternbr\xe4u um ad\xe4quaten Ersatz und mit dem Einsatz digitaler Medien schnell umzudirigieren war und im Gablerbr\xe4u jenseits der Salzach noch R\xe4umlichkeiten fand, um das sensationelle Ergebnis angemessen zu feiern. Worte des Dankes f\xfcr unseren Chorleiter Jens sprach Hans-Georg und Jens antwortete in seiner bekannten Art lang und ausf\xfchrlich zu allen Details der verflossenen zehn Stunden. Den Abschluss fanden die Feierlichkeiten dann in einem Pub in Hoteln\xe4he, wo dann die S\xe4nger tats\xe4chlich jedes Detail noch einmal ausf\xfchrlich beleuchteten und diskutierten, aber beginnend mit der Gr\xfcndung der FROHEN STUNDE im Jahre des Herrn 1900, die seitdem auf internationalem Parkett keine entsprechende Punktzahl erreicht hatte."}),"\n",(0,t.jsx)(n.h3,{id:"sonntag-23-juni-2019",children:"Sonntag 23. Juni 2019"}),"\n",(0,t.jsx)(n.p,{children:"Erneut p\xfcnktlich und diszipliniert, mit \u201eGott und der Welt\u201c zufrieden und gl\xfccklich \xfcber das tolle Ergebnis findet sich die Chorfamilie nach ausgiebigem Fr\xfchst\xfcck beim Busterminal ein, um in dem Slot zwischen 10.00 Uhr und 10.20 Uhr die Heimreise anzutreten. Bereits w\xe4hrend der schlussendlich 10 st\xfcndigen Heimfahrt laufen \xfcber die sog. sozialen Medien auch die ersten Gl\xfcckw\xfcnsche von au\xdferhalb ein, u.a. von Ortsb\xfcrgermeister Achim Kremer, der aus Cannes von der Cote d\xb4Azur gr\xfc\xdfte."}),"\n",(0,t.jsx)(n.p,{children:"Dank einer \u201eAbk\xfcrzung\u201c, die uns von Salzburg kommend bereits am n\xf6rdlichen Ufer des Chiemsees vorbei weitestgehend \xfcber Bundesstra\xdfen fast bis nach W\xfcrzburg f\xfchrt, gelingt es unserem findigen Busfahrer Laszlo unfreiwillige Staustopps zu vermeiden und das Gef\xe4hrt zumindest am Fahren zu halten. Eine vom Vorsitzenden unseres Elferrats Sascha Bender kurzfristig anberaumte Kappensitzung im Bus, bei der jeder mehr oder weniger freiwillig aktiv teilnehmen konnte, verk\xfcrzte die Fahrtzeit gef\xfchlt enorm. Bei einem kurzen Halt auf der Rastst\xe4tte Camberg verabschiedeten wir dann zun\xe4chst unsere Mitstreiter Thomas Becker und Helmut Heun und kamen schlie\xdflich zwischen 20:30 Uhr und 21:00 Uhr in Weroth am B\xfcrgerhaus an. Hier war auch schon die Besatzung des 9\xb4er Busses eingetroffen, die ihr Gef\xe4hrt bereits beim Busunternehmen abgegeben hatten. Den Fahrern gilt unser besonderer Dank. Die Gemeinde hie\xdf \u2013 vertreten durch Lisa Weimer \u2013 zusammen mit einer kleinen Gruppe Daheimgebliebener die erfolgreiche Schar herzlich willkommen und bedankte sich mit einem w\xfcrdigen Empfang."}),"\n",(0,t.jsx)(n.p,{children:"Zu vorger\xfcckter Stunde endete dann dieses weitere musikalische Abenteuer, bei dem die FROHE STUNDE nicht nur erneut ein Golddiplom, sondern auch viele neue Freunde und was gar nicht hoch genug einzusch\xe4tzen ist, auch neue S\xe4nger gewonnen hat. In der am folgenden Mittwoch zum Abschluss des ersten Halbjahrs stattfindenden Probe, bei der man im Anschluss die Reise noch einmal Revue passieren lie\xdf, erkl\xe4rten Werner Blatt, G\xfcnter Sch\xf6pping und Reinhard Seelbach, der FROHEN STUNDE als \u201eregul\xe4re\u201c S\xe4nger beitreten zu wollen."}),"\n",(0,t.jsx)(n.h2,{id:"25-august--eigene-open-air-matinee-am-b\xfcrgerhaus",children:"25. August \u2013 Eigene Open-Air Matinee am B\xfcrgerhaus"}),"\n",(0,t.jsx)(n.p,{children:"Nachdem wir in den letzten Jahren schon mehrmals dar\xfcber nachdachten, einmal eine Open-Air Veranstaltung auf dem Platz vor unserem B\xfcrgerhaus zu veranstalten, setzten wir dies im letzten Jahr endlich in die Tat um. Am 25. August begann um 11.00 Uhr morgens unsere 1. Matinee-Veranstaltung unter freiem Himmel und das auch gleich bei herrlichstem Sonnenschein. Im Vorfeld hatten wir alles m\xf6gliche daf\xfcr getan, akustisch einigerma\xdfen akzeptable Verh\xe4ltnisse zu schaffen, damit es f\xfcr die Ch\xf6re wie auch f\xfcr die Zuh\xf6rer ein musikalisches Erlebnis w\xfcrde. Ebenso war nat\xfcrlich ausreichend f\xfcr Speis und Trank gesorgt. Als Gastgeber er\xf6ffneten wir dann das Programm und kamen auch gleich zu einem ersten H\xf6hepunkt. Die Veranstaltung nutzten wir n\xe4mlich auch um Ehrungen f\xfcr verdiente S\xe4nger vorzunehmen, aber vor allem unseren Chorleiter Jens R\xf6th f\xfcr seine inzwischen 25 j\xe4hrige T\xe4tigkeit bei uns zu ehren und zu danken. Johannes Hannappel hob in seiner Laudatio nicht nur seine musikalischen F\xe4higkeiten hervor, die uns zu den Erfolgen der letzten Jahre f\xfchrten, sondern auch besonders das freundschaftliche Verh\xe4ltnis zwischen Chor und Chorleiter, das sich in den Jahren entwickelt hat. Desweiteren wurde Edi Hannappel f\xfcr 65 j\xe4hrige aktive Mitgliedschaft, Manfred Fachinger f\xfcr 50 j\xe4hrige aktive Mitgliedschaft geehrt, wobei Manfred Fachinger auch gleichzeitig zum Ehrenmitglied unseres Vereines ernannt wurde. Und \u201elast but not least\u201d wurde Hans-Georg Jung f\xfcr seine inzwischen insgesamt 40 j\xe4hrige Vorstandst\xe4tigkeit geehrt. Johannes Hannappel dankte allen Geehrten f\xfcr deren langj\xe4hrige Unterst\xfctzung und \xfcberreichte die Geschenke seitens des Vereines. Dann ging es musikalisch mit den Auftritten unserer Gastch\xf6re weiter. Es begann der gemischte Chor \u201eLaudate\u201c aus Oberneisen, dann folgte der Reihe nach der \u201eFrohsinn\u201c aus Elz, sowohl als gro\xdfer M\xe4nnerchor sowie mit ihrer kleineren Abteilung, der \u201eU40\u201c, weiter ging es mit dem Frauenchor \u201eMimos\u201c aus Staffel und den Abschlu\xdf bildete der Katholische Kirchenchor aus Dreikirchen. Alle Ch\xf6re boten eine durchweg lockere Liedauswahl vor, was von deutschen Volksliedern bis hin zu modernen Pop- und Gospelarrangements reichte. Dem Publikum wurde ein musikalisch abwechslungsreiches Programm geboten, welches dies auch mit viel Beifall belohnte. Alles in allem eine recht gelungene Veranstaltung, die von vielen Beteiligten positiv aufgenommen wurde. Ein wenig entt\xe4uschen war jedoch der Zuspruch besonders aus der Wer\xf6ther Bev\xf6lkerung. Hier hatten wir uns mehr Beteiligung erhofft, aber bei denen die dabei waren war die Veranstaltung sehr gut angekommen."}),"\n",(0,t.jsx)(n.h2,{id:"31-august--chorfest-des-chorverbandes-rheinland-pfalz",children:"31. August \u2013 Chorfest des Chorverbandes Rheinland-Pfalz"}),"\n",(0,t.jsx)(n.p,{children:"Unter dem Motto \u201eRheinland-Pfalz singt\u201c fand am 1. August das 1. Chorfest des Chorverbandes RheinlandPfalz in Mainz statt. Weit \xfcber 100 Ch\xf6re aus unserem Bundesland waren an diesem Tag auf verschiedenen B\xfchnen rund um den Mainzer Dom zu sehen und zu h\xf6ren. Auch wir nahmen an diesem Event teil und absolvierten unseren Auftritt auf der sogenannten \u201eSchott-Meisterchorb\xfchne\u201c im Weihergarten, dem idyllisch gelegenen Innenhof des Mainzer Musikverlages Schott. Bei hochsommerlichen Temperaturen betraten wir am Sp\xe4tnachmittag die B\xfchne und absolvierten einen insgesamt sehr gelungenen Auftritt. Wir brachten die ganze Bandbreite unserer derzeitigen Chorliteratur auf einem sehr hohen Niveau dar, sodass das begeisterte Publikum noch eine Zugabe einforderte. Den Abschluss des Tages bildete dann standesgem\xe4\xdf der Besuch eines Brauhauses, wo bei Speis und Trank noch etliche Lieder gesungen wurden, die sogar zwei amerikanische G\xe4ste derart begeisterten, dass sie sich spontan zu uns gesellten und mitsangen."}),"\n",(0,t.jsx)(n.h2,{id:"07-dezember--adventskonzert-der-mimos-staffel",children:"07. Dezember \u2013 Adventskonzert der Mimos Staffel"}),"\n",(0,t.jsx)(n.p,{children:"Den musikalischen Abschuss des Jahres bildete die Mitwirkung beim Adventskonzert des Frauenchores \u201eMimos\u201c Staffel am 7. Dezember in der Staffeler evangelischen Kirche. Seit ihrem Bestehen veranstaltet der Frauenchor dieses Adventskonzert in jedem Jahr und hatte mit unserem Chor erstmals einen Gastchor eingeladen. Die Frauen bestritten dann auch den gr\xf6\xdften Teil des Konzertes, unterbrochen von einigen Solodarbietungen und Vortr\xe4gen einiger Weihnachtsgeschichten. Wir trugen unsere Vortr\xe4ge zum Schluss vor und zwar wegen der Enge des Chorraumes der Kirche von der Empore aus. Erst zum gemeinsamen Abschlusslied mit dem Frauenchor gruppierten wir uns dann hinter den \u201eMimos\u201c und trugen zum Schluss das \u201eO holy Night\u201c vor. Schade war bei dieser Veranstaltung, das sie durchaus mehr Zuh\xf6rer verdient h\xe4tte. Aber diejenigen, die anwesend waren zeigten sich sehr angetan von unserem Gesang. Alles in allem ein gelungener Abschluss unseres musikalischen Jahres 2019."}),"\n",(0,t.jsx)(n.h2,{id:"was-passiert-2019-sonst-noch-",children:"Was passiert 2019 sonst noch \u2026"}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"20. Januar"})," \u2013 Gedenkgottestdienst f\xfcr die lebenden und verstorbenen Mitglieder der Frohen Stunde"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"21. Mai"})," \u2013 Mitgestaltung des 2. Seelenamts unseres verstorbenen Ehrenmitglieds Josef Hannappel"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"22. Juni"})," \u2013 Tod unseres f\xf6rdernden Mitglieds Karl-Heinz Jungerts"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"23. Juli"})," \u2013 Beerdigung unseres aktiven S\xe4ngers Axel Metternich"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"24. Juli"})," \u2013 Thekendienst auf der Sportwoche der Spvgg. Steinefrenz/Weroth"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"25. Oktober"})," \u2013 Thekendienst an der 90er Jahre Party der Kirmesgesellschaft"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"26. Oktober"})," \u2013 Tod unseres Ehrenmitglieds Walter Goerg"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"27. November"})," \u2013 Gedenkfeier am Ehrenmal (Volkstrauertag)"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)("b",{children:"28. November"})," \u2013 Organisation des Weihnachtsmarkts in Weroth"]}),"\n"]})]})}function c(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(l,{...e})}):l(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const t={},s=i.createContext(t);function a(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:a(e.components),i.createElement(s.Provider,{value:n},e.children)}}}]);