"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[9153],{6131:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>c,contentTitle:()=>o,default:()=>b,frontMatter:()=>u,metadata:()=>i,toc:()=>g});const i=JSON.parse('{"id":"archiv/2010-2019/2015","title":"2015","description":"Die Frohe Stunde nimmt am Internationalen Anton-Bruckner Chorwettbewerb in Linz (\xd6sterreich) teil.","source":"@site/docs/archiv/2010-2019/2015.md","sourceDirName":"archiv/2010-2019","slug":"/archiv/2010-2019/2015","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2015","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2010-2019/2015.md","tags":[],"version":"current","sidebarPosition":5,"frontMatter":{"sidebar_position":5,"description":"Die Frohe Stunde nimmt am Internationalen Anton-Bruckner Chorwettbewerb in Linz (\xd6sterreich) teil."},"sidebar":"archivSidebar","previous":{"title":"2016","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2016"},"next":{"title":"2014","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2014"}}');var t=r(4848),s=r(8453),a=r(3554),h=r.n(a),d=r(5537),l=r(9329);const u={sidebar_position:5,description:"Die Frohe Stunde nimmt am Internationalen Anton-Bruckner Chorwettbewerb in Linz (\xd6sterreich) teil."},o="2015",c={},g=[{value:"19. April \u2013 Brunchkonzert beim Vocalensemble Diez",id:"19-april--brunchkonzert-beim-vocalensemble-diez",level:2},{value:"26. April \u2013 Kritiksingen der S\xe4ngergruppe Ost",id:"26-april--kritiksingen-der-s\xe4ngergruppe-ost",level:2},{value:"03. \u2013 07. Juni \u2013 Anton-Bruckner Chorwettbewerb in Linz, \xd6sterreich",id:"03--07-juni--anton-bruckner-chorwettbewerb-in-linz-\xf6sterreich",level:2},{value:"05. Juli \u2013 Lindenbl\xfctenfest in Zehnhausen",id:"05-juli--lindenbl\xfctenfest-in-zehnhausen",level:2},{value:"14. November \u2013 Stimmbildungstag des CV Rhein-Lahn in Lahnstein",id:"14-november--stimmbildungstag-des-cv-rhein-lahn-in-lahnstein",level:2},{value:"05. Dezember \u2013 Jahresabschlussfeier",id:"05-dezember--jahresabschlussfeier",level:2},{value:"Was passiert 2015 sonst noch \u2026",id:"was-passiert-2015-sonst-noch-",level:2}];function m(e){const n={h1:"h1",h2:"h2",header:"header",hr:"hr",li:"li",p:"p",strong:"strong",ul:"ul",...(0,s.R)(),...e.components},{ImageWithText:r}=n;return r||function(e,n){throw new Error("Expected "+(n?"component":"object")+" `"+e+"` to be defined: you likely forgot to import, pass, or provide it.")}("ImageWithText",!0),(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"2015",children:"2015"})}),"\n",(0,t.jsx)(n.h2,{id:"19-april--brunchkonzert-beim-vocalensemble-diez",children:"19. April \u2013 Brunchkonzert beim Vocalensemble Diez"}),"\n",(0,t.jsx)(n.p,{children:"Aus Anlass seines 25-j\xe4hrigen Bestehens veranstaltete das Vocalensemble Diez am 19. April ein Brunch-Konzert in der Mehrzweckhalle in Birlenbach. Wie schon der Name der Veranstaltung vermuten lie\xdf, fand das Konzert an diesem sonnigen Sonntagmorgen in sehr lockerer Atmosph\xe4re statt. Alle mitwirkenden Ch\xf6re boten in erster Linie gef\xe4llige Volkslieder oder moderne Liedbearbeitungen dar, sodass insgesamt gesehen ein recht abwechslungsreiches Programm geboten wurde. Auch wir brachten dem Publikum eine Auswahl unserer Volkslieder dar und zeigten, trotz eher kleiner Besetzung, eine durchaus \xfcberzeugende Leistung, sodass wir ohne eine Zugabe nicht von der B\xfchne durften."}),"\n",(0,t.jsx)(n.h2,{id:"26-april--kritiksingen-der-s\xe4ngergruppe-ost",children:"26. April \u2013 Kritiksingen der S\xe4ngergruppe Ost"}),"\n",(0,t.jsx)(n.p,{children:"Zum allj\xe4hrlichen Kritiksingen trafen sich die Ch\xf6re der S\xe4ngergruppe OST diesmal in Nentershausen. Am 26. April trug jeder der teilnehmenden Ch\xf6re zwei St\xfccke vor, die dann von Kritiker Prof. Harald Jers von der Musikhochschule Mannheim bewertet wurden. Wir hatten im Hinblick auf den internationalen Wettbewerb in Linz die St\xfccke \u201eDer Abendhimmel\u201c von Bruckner und \u201ePeriti Autem\u201c von Mendelssohn-Bartholdy zum Vortrag ausgew\xe4hlt und waren auch gleich als zweiter Chor schon ziemlich fr\xfch dran. Beide St\xfccke gelangen uns recht ansehnlich. Besonders beim \u201eAbendhimmel\u201c, den wir erstmals vor Publikum vortrugen, waren wir wie auch unserer Chorleiter mit unserer Leistung zufrieden. Auch Harald Jers lobte in seiner sehr ausf\xfchrlichen Kritikbesprechung den Chor f\xfcr seinen engagierten Auftritt. Er ging aber auch auf die Schwierigkeiten bei diesem St\xfcck ein und zeigte uns, wo wir noch Defizite haben. Auch zu dem Thema Tonangabe gab er uns einige n\xfctzliche Tipps. Alles in allem konnten wir aus dem letztj\xe4hrigen Kritiksingen doch einiges Positive f\xfcr unsere zuk\xfcnftige Chorarbeit mitnehmen."}),"\n",(0,t.jsx)(n.h2,{id:"03--07-juni--anton-bruckner-chorwettbewerb-in-linz-\xf6sterreich",children:"03. \u2013 07. Juni \u2013 Anton-Bruckner Chorwettbewerb in Linz, \xd6sterreich"}),"\n",(0,t.jsxs)(d.A,{children:[(0,t.jsxs)(l.A,{value:"text",label:"Bericht",default:!0,children:[(0,t.jsx)(r,{image:{src:"/archiv-frohe-stunde/img/2015/Linz/linz_102.jpg",alt:"Gruppenfoto Linz 2015"},text:(0,t.jsx)(t.Fragment,{children:"Musikalischer H\xf6hepunkt des letzten Jahres war der Besuch des internationalen Anton-Bruckner-Chorwettbewerbes im nieder\xf6sterreichischen Linz vom 3. bis 7. Juni. Hier kann man ohne zu \xfcbertreiben feststellen, dass die Reise in die Stadt an der Donau ein voller Erfolg war. Daf\xfcr gab es mehrere Gr\xfcnde. Da war zum ersten schon mal das tolle Wetter, was uns die ganzen 4 Tage mit Sonne satt und hochsommerlichen Temperaturen verw\xf6hnte sowie die Stadt Linz selbst, die doch weit mehr zu bieten hatte als wir es zu Anfang bef\xfcrchtet hatten. Da waren zum zweiten die \xe4u\xdferen Rahmenbedingungen, angefangen bei der reibungslosen Organisation bis hin zu den eindrucksvollen Veranstaltungsorten des Festivals. Sieht man mal von der immer schlechtgelaunten Seniorchefin ab, war auch das City-Hotel Linz, wo wir w\xe4hrend der Tage untergebracht waren, eigentlich ganz in Ordnung. Zum dritten war auch unsere Reisegruppe wieder einmal eine sehr harmonische Truppe, die sich immer gegenseitig unterst\xfctzte und bei aller Disziplin auch sehr viel Spa\xdf hatte. Und zum letzten der Wettbewerb an sich, der durch eine au\xdferordentlich hohe Qualit\xe4t gl\xe4nzte."})}),(0,t.jsx)(n.p,{children:"Zun\xe4chst ein besonderes Lob dem Veranstalter des Festivals, der Fa. Interkultur mit Sitz in Fernwald. Die Mitarbeiter von Interkultur standen uns vor und w\xe4hrend der Tage immer mit kompetentem Rat zu Seite, waren dabei immer freundlich und zuvorkommend. Dann waren da die genialen Veranstaltungsorte des Festivals. Der pr\xe4chtige, gro\xdfe Mariendom, in dem das Er\xf6ffnungskonzert stattfand. Das Augustiner Stift St. Florian, welches einige Kilometer au\xdferhalb von Linz liegt, und in dessen Stiftskirche und im Marmorsaal Freundschaftskonzerte mit den teilnehmenden Wettbewerbsch\xf6ren abgehalten wurden. Auch wir durften hier im Marmorsaal gemeinsam mit Frauench\xf6ren aus M\xfcnchen und Finnland, einem gemischten Chor aus Estland und einem Kinderchor aus Singapur konzertieren, was allein schon wegen der genialen Akustik des Saales ein H\xf6rgenuss war. Beeindruckend auch das Brucknerhaus, im Jahr 1974 von Herbert von Karajan er\xf6ffnet, direkt an der Donau gelegen, mit einer eigenartigen Architektur und einem beeindruckenden Konzertsaal. Hier fanden alle Wettbewerbsveranstaltungen statt."}),(0,t.jsx)(n.p,{children:"Und zum Wettbewerb selbst passte wohl am besten das Motto klein aber sehr, sehr fein. Obwohl nur 19 Ch\xf6re aus \xd6sterreich, Estland, Litauen, Finnland, Norwegen, Kroatien, Singapur, Rum\xe4nien, Deutschland, den Philippinen und den USA am Wettbewerb teilnahmen war die Qualit\xe4t au\xdferordentlich hoch. Ausgenommen den Kinderchor aus Singapur beeindruckten alle Ch\xf6re durch ein hohes Ma\xdf an Musikalit\xe4t und Klangsch\xf6nheit. Allein an einem solch hochwertigen Wettbewerb teilnehmen zu d\xfcrfen ist schon eine Ehre, und so hatten wir uns zum Ziel gesetzt mit unserer bestm\xf6glichen Leistung hier nicht mehr als n\xf6tig gegen\xfcber den anderen Ch\xf6ren abzufallen. Schon im Vorfeld hatten wir immer wieder betont, bei dieser Reise die volle Aufmerksamkeit auf den Wettbewerb zu legen. Selbst dort in Linz hatten wir noch zus\xe4tzliche Proben in unserem Hotel durchgef\xfchrt, was sich besonders in punkto Konzentration positiv auswirkte."}),(0,t.jsx)(n.p,{children:"Den Lohn f\xfcr die teilweise anstrengende Arbeit konnten wir dann bei unserem Wettbewerbsauftritt einfahren. Durch eine engagierte, konzentrierte, aber dennoch immer lockere und musikalisch sehr hohe Leistung gelangen uns die Vortr\xe4ge allesamt auf hohem Niveau. Wir trugen in unserer Kategorie A2, M\xe4nnerch\xf6re mit Pflichtst\xfcck, folgende Chorwerke vor: als Pflichtst\xfcck \u201eDer Abendhimmel\u201c von Anton Bruckner, dann das \u201ePeriti autem\u201c von Felix Mendelssohn, weiter den \u201eVeit Sto\xdf\u201c von Klaus Ochs und zum Abschluss \u201eWohlauf, ihr G\xe4ste\u201c von Erasmus Widmann. Ein zufriedener Chorleiter sagte nach unserem Auftritt: \u201eMehr geht nicht, das war das Maximum was dieser Chor derzeit zeigen kann\u201c. Man kann die Leistung wohl mit der beim Wettbewerb in Prag durchaus gleich setzten."}),(0,t.jsx)(n.p,{children:"Auch die Jury, bestehend aus 5 international hochkar\xe4tigen Fachleuten, w\xfcrdigte unsere Leistung mit der Bewertung von 19,53 Punkten sowie der Vergabe eines Silberdiploms der h\xf6chsten Stufe 10. Gro\xdf war der Jubel und alle Beteiligten lagen sich in den Armen. Mit dieser Wertung lagen wir im guten Mittelfeld des Wettbewerbes und es hatte sich wieder einmal gezeigt, dass wir als Laienchor in der Lage sind bei einer solch hochkar\xe4tigen Veranstaltung zu bestehen."}),(0,t.jsx)(n.p,{children:"H\xf6hepunkte des Wettbewerbes waren f\xfcr mich zum einen die Beratungsrunde und zum anderen der Abschlu\xdfwettbewerb um den Gro\xdfen Preis. In der Beratungsrunde, die schon gleich am ersten Tag stattfand, hatten wir Gelegenheit vorab schon mal unser Wettbewerbsprogramm 3 Juroren zu pr\xe4sentieren und mit ihnen durchzusprechen. Im Einzelnen waren das die Schwedin Helene Stureborg, der Slowene Primoz Kerstanj und Peter Habermann, Leiter des bekannten Jugend-Rundfunkchores Wernigerode. Und es war schon sehr interessant und lehrreich mit diesen Fachleuten knapp eine Stunde zusammenarbeiten zu d\xfcrfen. Nat\xfcrlich gaben sie uns auch wichtige Hinweise zur Verbesserung unserer Vortr\xe4ge und wie uns Peter Habermann nachher sagte, seien wir so ziemlich der einzige Chor gewesen, der die Verbesserungsvorschl\xe4ge beim Wettbewerb auch umgesetzt h\xe4tte."}),(0,t.jsx)(n.p,{children:"Beim abschlie\xdfenden Wettbewerb um den Gro\xdfen Preis sangen die besten Ch\xf6re in den jeweiligen Kategorien den Gesamtsieger aus. Und was man da an wundervollem Chorgesang zu h\xf6ren bekam war schon einmalig und relativierte die Wertung nochmals. Der verdiente Sieger des Wettbewerbes, der gemischte Universit\xe4tschor aus Tacoma von der nordamerikanischen Westk\xfcste zeigte hier, dass er zu Recht zu den weltweit besten Chorformationen z\xe4hlt. Und wir durften dies miterleben."}),(0,t.jsx)(n.p,{children:"Nat\xfcrlich boten wir an den Tagen auch ein touristisches Programm an. Schon auf der Hinfahrt machten wir Zwischenstation in Passau und hatten Gelegenheit die Altstadt mit dem Dom zu besichtigen. Der mitgereiste Anhang hatte Gelegenheit das herrlich an der Donau gelegene Kloster Melk zu besichtigen und auch von dem Angebot einer interessanten und informativen Stadtbegehung durch die Linzer Altstadt mit Rundgang durch den Mariendom wurde Gebrauch gemacht."}),(0,t.jsx)(n.p,{children:"Nat\xfcrlich kam auch das Gesellige nicht zu kurz, daf\xfcr sorgte unsere Reisegruppe schon selbst. Bei den abendlichen Besuchen der \xf6rtlichen Bierg\xe4rten, sei es im Klosterhof oder im Stadtbr\xe4u Josefs, kam bei k\xfchlem Bier und gutem Essen schnell gute Stimmung auf und es wurde viel gesungen. Einige lie\xdfen es sich sogar nicht nehmen, am fr\xfchen Sonntagmorgen, so gegen 6.00 Uhr, unseren schon fr\xfcher abreisenden Chorleiter pers\xf6nlich zu verabschieden. Alles in allem durften wir in Linz wieder einmal erlebnisreiche Tage verbringen, die wohl noch lange in unserem Ged\xe4chtnis bleiben werden."})]}),(0,t.jsx)(l.A,{value:"videos",label:"Videos",children:(0,t.jsxs)("p",{children:[(0,t.jsx)(n.p,{children:(0,t.jsx)(n.strong,{children:"Konzert im Augustiner Stift"})}),(0,t.jsx)(h(),{controls:!0,url:"https://youtu.be/XI6A_jsKttQ"})]})})]}),"\n",(0,t.jsx)(n.h2,{id:"05-juli--lindenbl\xfctenfest-in-zehnhausen",children:"05. Juli \u2013 Lindenbl\xfctenfest in Zehnhausen"}),"\n",(0,t.jsx)(n.p,{children:"Auf Einladung des Lindenvereins Zehnhausen gestalteten wir am 5. Juli den sonnt\xe4glichen Gottesdienst bei deren allj\xe4hrlich stattfindenden Lindenbl\xfctenfest mit. Da Jens R\xf6th an diesem Tag terminlich verhindert war, standen wir wieder einmal unter der bew\xe4hrten Leitung unseres Vizes Manfred Fachinger. Obwohl wir, gerade im 2. Bass, personell etwas unterbesetzt waren, gelangen uns die ausgew\xe4hlten sakralen Vortr\xe4ge allesamt sehr gut, sodass uns die Gottesdienstbesucher zum Abschluss mit Applaus belohnten. Bei hochsommerlichen Temperaturen von \xfcber 30 Grad ging es dann direkt mit einem ausgiebigen Fr\xfchschoppen weiter, bei dem gemeinsam mit den Sangesfreunden aus Zehnhausen noch einige Lieder angestimmt wurden."}),"\n",(0,t.jsx)(n.h2,{id:"14-november--stimmbildungstag-des-cv-rhein-lahn-in-lahnstein",children:"14. November \u2013 Stimmbildungstag des CV Rhein-Lahn in Lahnstein"}),"\n",(0,t.jsx)(n.p,{children:"Den musikalischen Abschluss des Jahres bildete am 14. November ein Konzert in der Aula des Johannes-Gymnasiums in Niederlahnstein. Der CV Rhein-Lahn f\xfchrte an diesem Samstag dort eine Chorfreizeit durch und hatte uns zur Mitwirkung beim Abschlusskonzert eingeladen. Neben den teilnehmenden Projektch\xf6ren konnten wir hier in zwei jeweils ca. 30-min\xfctigen Auftritten die ganze Bandbreite unseres musikalischen Repertoires zeigen. Beide Auftritte gelangen auch sehr gut, was uns vom fachkundigen Publikum mit langanhaltendem Beifall belohnt wurde, sodass wir am Ende noch zwei Zugaben geben mussten. Auch J\xfcrgen Fassbender, der als Dozent f\xfcr die M\xe4nnerch\xf6re an dem Workshop mitwirkte, zeigte sich von unseren Vortr\xe4gen beiendruckt und begl\xfcckw\xfcnschte uns zu unserer Leistung. Zum geselligen Abschluss des Abends wanderten wir dann zur nahegelegenen Maximilians-Brauerei, wo wir bei gutem Essen und Bier vom Fass noch einige Lieder zum Besten gaben."}),"\n",(0,t.jsx)(n.h2,{id:"05-dezember--jahresabschlussfeier",children:"05. Dezember \u2013 Jahresabschlussfeier"}),"\n",(0,t.jsx)(n.p,{children:"Zum Ausklang des Jahres veranstalteten wir am 5. Dezember eine Feier in unserem B\xfcrgerhaus. Eingeladen waren alle Mitglieder mit Ihren Ehepartnern sowie die Witwen unserer verstorbenen Mitglieder und \xfcber 90 Personen waren der Einladung auch gefolgt. Bei leckerem Essen und k\xfchlen Getr\xe4nken lie\xdfen wir es uns an diesem Abend richtig gut gehen. Die Feier war auch Anlass einige verdiente S\xe4nger f\xfcr langj\xe4hrige Mitgliedschaft zu ehren. Hans-J\xfcrgen Sch\xf6nberger blickte im Jahr 2015 auf seine 50-j\xe4hrige aktive Mitgliedschaft zur\xfcck und wurde daf\xfcr mit einem Geschenk seitens des Vereins geehrt. Ebenso wurden Markus Fein, Johannes Hannappel, Hans-Georg Jung, Christoph Malm, Thomas Malm und Bernd-Josef Metternich f\xfcr 40-j\xe4hrige aktive Mitgliedschaft geehrt. Edi Hannappel dankte in seiner Laudatio allen Geehrten f\xfcr die geleistete Arbeit und \xfcberreichte die Geschenke. Ein besonderer Dank geht hier aber nochmals an Leo Weyand, der zusammen mit seinem Kochteam, bestehend aus Manfred Fachinger, Andreas Malm, Torsten Meuer, Thomas van Baaijen, Christoph und Paul Weimer, wieder einmal ein vorz\xfcglich angerichtetes B\xfcffee pr\xe4sentierte."}),"\n",(0,t.jsx)(n.hr,{}),"\n",(0,t.jsx)(n.h2,{id:"was-passiert-2015-sonst-noch-",children:"Was passiert 2015 sonst noch \u2026"}),"\n",(0,t.jsxs)(n.ul,{children:["\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"10. Januar"})," \u2013 Jahreshauptversammlung mit Ehrung Markus Fein f\xfcr 25 Jahre Notenwart"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"18. Januar"})," \u2013 St\xe4ndchen zum 75. Geburtstag unseres S\xe4ngers Eduard Hannappel"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"20. Januar"})," \u2013 Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder der Frohen Stunde"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"12. Juni"})," \u2013 St\xe4ndchen zur Silbernen Hochzeit von Hans-Peter und Gabriele Kaiser"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"26. Juli"})," \u2013 Thekendienst auf der Sportwoche der Spvgg. Steinefrenz/Weroth"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"26. Juli"})," \u2013 Tod unseres \xe4ltesten Mitglieds Fritz Thierbach"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"26. September"})," \u2013 St\xe4ndchen zum 80. Geburtstag unseres passiven Mitglieds Walter Gl\xe4ser"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"10. Oktober"})," \u2013 Bewirtung des Konzerts der \u201evocaw\xe4ller\u201c im B\xfcrgerhaus Weroth"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"15. November"})," \u2013 Gedenkfeier am Ehrenmal (Volkstrauertag)"]}),"\n",(0,t.jsxs)(n.li,{children:[(0,t.jsx)(n.strong,{children:"02. Dezember"})," \u2013 St\xe4ndchen zum 75. Geburtstag unseres S\xe4ngers G\xfcnter R\xf6th nach der Chorprobe"]}),"\n"]})]})}function b(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(m,{...e})}):m(e)}}}]);