"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[7453],{1945:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>o,frontMatter:()=>t,metadata:()=>i,toc:()=>l});const i=JSON.parse('{"id":"archiv/2010-2019/2011","title":"2011","description":"Erstmals ist der ganze Chor mit dem Flugzeug unterwegs. Ziel: Der internationale Wettbewerb \\"Europe and its songs\\" in Barcelona.","source":"@site/docs/archiv/2010-2019/2011.md","sourceDirName":"archiv/2010-2019","slug":"/archiv/2010-2019/2011","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2011","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2010-2019/2011.md","tags":[],"version":"current","sidebarPosition":9,"frontMatter":{"sidebar_position":9,"description":"Erstmals ist der ganze Chor mit dem Flugzeug unterwegs. Ziel: Der internationale Wettbewerb \\"Europe and its songs\\" in Barcelona."},"sidebar":"archivSidebar","previous":{"title":"2012","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2012"},"next":{"title":"2010","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2010"}}');var s=r(4848),a=r(8453);const t={sidebar_position:9,description:'Erstmals ist der ganze Chor mit dem Flugzeug unterwegs. Ziel: Der internationale Wettbewerb "Europe and its songs" in Barcelona.'},d="2011",h={},l=[{value:"09. April \u2013 Fr\xfchjahrskonzert beim MGV \u201eEintracht\u201c Nentershausen",id:"09-april--fr\xfchjahrskonzert-beim-mgv-eintracht-nentershausen",level:2},{value:"10. April \u2013 Kritiksingen der S\xe4ngergruppe Ost in Nentershausen",id:"10-april--kritiksingen-der-s\xe4ngergruppe-ost-in-nentershausen",level:2},{value:"02. Juni \u2013 Internationales Harmonie-Festival in Lindenholzhausen",id:"02-juni--internationales-harmonie-festival-in-lindenholzhausen",level:2},{value:"21. August \u2013 Chor- und Orchesterkonzert in Frickhofen",id:"21-august--chor--und-orchesterkonzert-in-frickhofen",level:2},{value:"21. \u2013 24. September \u2013 13. Internationales Volkslied Festival \u201eEurope and its Songs\u201c in Barcelona",id:"21--24-september--13-internationales-volkslied-festival-europe-and-its-songs-in-barcelona",level:2},{value:"06. November \u2013 Konzert beim MGV \u201eFrohsinn\u201c Baiertal",id:"06-november--konzert-beim-mgv-frohsinn-baiertal",level:2},{value:"Was passiert 2011 sonst noch \u2026",id:"was-passiert-2011-sonst-noch-",level:2}];function u(e){const n={h1:"h1",h2:"h2",header:"header",li:"li",p:"p",strong:"strong",ul:"ul",...(0,a.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"2011",children:"2011"})}),"\n",(0,s.jsx)(n.h2,{id:"09-april--fr\xfchjahrskonzert-beim-mgv-eintracht-nentershausen",children:"09. April \u2013 Fr\xfchjahrskonzert beim MGV \u201eEintracht\u201c Nentershausen"}),"\n",(0,s.jsx)(n.p,{children:"Nach einem \xe4u\xdferst ruhigen Jahresbeginn kamen dann an einem Wochenende im Fr\xfchling gleich 3 Termine nacheinander. Unsere Sangesfreunde vom MGV \u201eEintracht\u201c Nentershausen hatten uns kurzfristig, da einige Ch\xf6re die eigentlich schon zugesagte Mitwirkung wieder zur\xfcckgenommen hatten, zu Ihrem Fr\xfchjahrskonzert am 9. April eingeladen. Obwohl dem Vorstand bewusst war, dass auch bei uns einige S\xe4nger aus privaten und beruflichen Gr\xfcnden nicht anwesend sein konnten, wollten wir doch unseren Nachbarverein nicht entt\xe4uschen und nahmen die Einladung an. Und alles in allem gelang uns in der neuen Halle in Nentershausen auch ein ganz ordentlicher Auftritt. Trotz einiger intonatorischer Schwierigkeiten beim \u201eSoon ah will be done\u201c, zu meiner Schande in erster Linie verursacht beim 2. Tenor, lies uns das Publikum nicht ohne eine Zugabe, dem \u201eFeuersturm der Reben\u201c, von der B\xfchne. Den meisten Zuh\xf6rern waren die missratenen Stellen nicht aufgefallen und voll des Lobes f\xfcr unsere gezeigte Leistung."}),"\n",(0,s.jsx)(n.h2,{id:"10-april--kritiksingen-der-s\xe4ngergruppe-ost-in-nentershausen",children:"10. April \u2013 Kritiksingen der S\xe4ngergruppe Ost in Nentershausen"}),"\n",(0,s.jsx)(n.p,{children:"Gleich am n\xe4chsten Tag, dem 10. April, fand das diesj\xe4hrige Kritiksingen der S\xe4ngergruppe Ost ebenfalls in Nentershausen statt. Als Kritiker fungierte wieder einmal der K\xf6lner Domkapellmeister Eberhard Metternich, uns durch seine verwandtschaftliche Verbundenheit mit Weroth bestens bekannt und deswegen auch umso kritischer in seinen Beobachtungen. Denn wie er selbst sagte, m\xfcssen bei unserem Chor gerade wegen der Teilnahme am letztj\xe4hrigen Deutschen Chorwettbewerb nun auch viel h\xf6here Ma\xdfst\xe4be angelegt werden. Und da kamen wir bei unserem 1. St\xfcck, dem \u201ePeriti autem\u201c nicht so gut weg. Die erste H\xe4lfte des Werkes gelang uns sehr gut, doch mit Beginn der Fuge waren die Punktierungen nicht mehr exakt, der Chor fing an zu hudeln. Dadurch, dass die Tonh\xf6he noch um einen halben Ton stieg, war auch der Schluss nicht mehr ganz rein. Alles Dinge, die vom Kritiker erkannt und angesprochen wurden. Beim 2. St\xfcck, dem \u201eSoon ah will be done\u201c, gab es von Eberhard Metternich viel Lob und nur kleine Anmerkungen zur Verbesserung. Ihm hatte das Spiritual wirklich sehr gut gefallen. Besonderes Lob erhielt der 2. Bass f\xfcr sein Solo am Ende des Mittelteils, was er mit dem Pr\xe4dikat \u201edas war einfach GRANDIOS\u201c auszeichnete. Alles in allem war Eberhard Metternich von der Entwicklung des Chores schon beeindruckt, zeigte aber auch die Dinge auf, die noch zu verbessern sind. Eine, nach Meinung aller Beteiligten, f\xfcr uns sehr fruchtbare Kritikbesprechung."}),"\n",(0,s.jsx)(n.h2,{id:"02-juni--internationales-harmonie-festival-in-lindenholzhausen",children:"02. Juni \u2013 Internationales Harmonie-Festival in Lindenholzhausen"}),"\n",(0,s.jsx)(n.p,{children:"Den 1. musikalischen H\xf6hepunkt des Jahres bildete sicherlich die Teilnahme am 6. internationalen Harmonie Festival am 2. Juni, dem Himmelfahrtstag, in Lindenholzhausen. Wie schon beim letzten Festival 2005 traten wir auch diesmal in Chorgemeinschaft mit den S\xe4ngern der \u201eEintracht\u201c Frickhofen als M\xe4nnerchor Frickhofen/Weroth auf. Jedoch, anders als vor 6 Jahren, hatten wir dieses mal nicht f\xfcr den Chorwettbewerb sondern in der Kategorie 11 \u2013 M\xe4nnerch\xf6re mit Volksliedprogramm \u2013 gemeldet."}),"\n",(0,s.jsx)(n.p,{children:"Nach nur 5 gemeinsamen Proben hatten sich beide Ch\xf6re doch erstaunlich schnell zusammengefunden und unter der professionellen Leitung von Jens R\xf6th hatte sich wieder ein aus ca. 85 S\xe4ngern bestehender homogener Klangk\xf6rper gebildet. Konkurrenten in unserer Kategorie waren der MGV \u201eHarmonie\u201c 1853 Delkenheim und der \u201eKemper Werkschor\u201c Olpe aus Deutschland sowie die beiden Vertreter aus \xd6sterreich, der M\xe4nnerchor G\xf6tzis und der LIEDERm\xe4nnerChor Alberschwende, beide Ch\xf6re aus dem Vorarlberg."}),"\n",(0,s.jsx)(n.p,{children:"Veranstaltungsort war diesmal das Konzertzelt B, das aber f\xfcr ein Zelt \xfcber eine sehr gute Akustik verf\xfcgte. Die Jury bildete sich aus einem Trio von h\xf6chstem internationalem Rehnohme, bestehend aus der Bulgarin Theodora Pavlovitch, Professorin aus Sofia, dem deutschen Prof. Wolfgang Sch\xe4fer aus Frankf\xfcrt und, als Juryvorsitzender, Robert Sund aus Schweden. Jeder der Ch\xf6re durfte 3 Volkslieder vortragen, wir hatten uns f\xfcr die St\xfccke, \u201eWhat shall we do with the drunken sailor\u201c, \u201eDas schw\xe4bische Echo\u201c von Gustav Dingemann und das Spiritual \u201eSoon ah will be done\u201c von William L. Dawson entschieden."}),"\n",(0,s.jsx)(n.p,{children:"Unter dem Beifall des zahlreich erschienen Fachpublikums betraten wir die B\xfchne und pr\xe4sentierten unser, insgesamt gesehen, sehr gelungenes Programm auf einem guten Niveau. Chor und Chorleiter verlie\xdfen die B\xfchne sehr zufrieden und waren der Meinung, dass man sich hier sehr gut dargestellt hatte. Doch auch die anderen Ch\xf6re, besonders der Chor aus Olpe und beide Ch\xf6re aus \xd6sterreich zeigten sehr gute Leistungen, sodass sehr schnell klar wurde, dass es in dieser Klasse ein knappes Ergebnis geben wird. Dies wurde uns auch durch unsere zahlreich mitgereisten Fans best\xe4tigt."}),"\n",(0,s.jsx)(n.p,{children:"Und so kam es dann auch. Der Werkschor aus Olpe erhielt sehr gute 22,17 Punkte, unsere Chorgemeinschaft 22,00 Punkte und mit 21,00 Punkten erhielt der M\xe4nnerchor G\xf6tzis die dritth\xf6chste Wertung. Nat\xfcrlich war man zun\xe4chst etwas entt\xe4uscht, aber schnell war man sich bewusst, dass man gegen den \u201eAuswahlchor\u201c aus Olpe mit ihren 20 ausgesuchten S\xe4ngern gar nicht so schlecht da stand. Lohn der M\xfchen war das Zertifikat in \u201eSilber\u201c und das Pr\xe4dikat \u201eMit sehr gutem Erfolg teilgenommen\u201c. Entsprechend aufw\xe4ndig fiel denn bereits die Feier vor Ort aus und auch B\xfcrgermeister Reusch brauchte anl\xe4sslich eines kleinen Empfangs vor dem Vereinslokal mit Lob nicht zu sparen."}),"\n",(0,s.jsx)(n.p,{children:"Einmal mehr wird f\xfcr ihn mit diesem seltenen Erfolg deutlich, dass die FROHE STUNDE ein w\xfcrdiger Vertreter der heimischen Chorszene ist. Dies wird auch deutlich wenn man das Wettbewerbsergebnis im gesamten sieht. So lagen wir in der K\xfcnstlerischen Note mit 22,67 Punkten sogar an 1. Stelle aller deutschen M\xe4nnerch\xf6re und nur 3 Ch\xf6re aus der M\xe4nnerkammerchorklasse aus Taipei und den USA erhielten hier eine h\xf6here Punktzahl. Von den insgesamt 114 teilnehmenden Ch\xf6ren, von denen manche sicherlich zur internationalen Spitze z\xe4hlen, lagen wir mit dem 36. Rang im guten oberen Drittel des Festivals."}),"\n",(0,s.jsx)(n.p,{children:"Sowieso war dieses Festival nicht nur f\xfcr die Teilnehmer als auch f\xfcr die Zuh\xf6rer und G\xe4ste ein einzigartiges Erlebnis. Ein dickes Lob an die Harmonie Lindenholzhausen f\xfcr eine tolle Organisation dieser Veranstaltung, die das interessierte und fachkundige Publikum immer wieder zu Begeisterungsst\xfcrmen veranlasste. Es ist wohl einmalig in Deutschland, innerhalb weniger Tage ein solch hochwertiges Klangerlebnis von Ch\xf6ren aus aller Welt mit zu erleben."}),"\n",(0,s.jsx)(n.h2,{id:"21-august--chor--und-orchesterkonzert-in-frickhofen",children:"21. August \u2013 Chor- und Orchesterkonzert in Frickhofen"}),"\n",(0,s.jsx)(n.p,{children:"Das \u201eCanticum\u201c Weilburg veranstaltete am 21. August in der Pfarrkirche Frickhofen ein eindruckvolles Chor und Orchesterkonzert. Mitwirkende waren alle R\xf6th-Ch\xf6re, also neben dem \u201eCanticum\u201c auch der Kirchenchor Steinefrenz/Weroth und die M\xe4nnerch\xf6re aus Frickhofen und Weroth. Das wirklich sehr gute und beeindruckende Orchester stellte sich aus Mitgliedern des Frankfurter Sinfonieorchesters und des Staatstheaters Wiesbaden zusammen. Wir bestritten dieses Konzert ausschlie\xdflich in Chorgemeinschaft mit den S\xe4ngern aus Frickhofen und brachten hier sechs geistliche Werke dar, wobei das \u201eAve Maria\u201c von Franz Biebl und das \u201eSoon ah will be done\u201c beim Publikum besonders gut ankamen. Im Mittelpunkt des Konzertes stand aber sicherlich die Auff\xfchrung der \u201eKr\xf6nungsmesse\u201c von Mozart, beiendruckend vorgetragen vom \u201eCanticum\u201c und dem Kirchenchor unterst\xfctzt vom Orchester. Stehende Ovationen des Publikums waren der verdiente Lohn f\xfcr die S\xe4nger und Musiker. In der Nassauischen Neuen Presse war sp\xe4ter zu lesen: \u201eGro\xdfer Musikgenuss! Zum ersten Mal zeigte Musikdirektor Jens R\xf6th mit all seinen Ensembles wie sch\xf6n Chormusik sein kann\u201c."}),"\n",(0,s.jsx)(n.h2,{id:"21--24-september--13-internationales-volkslied-festival-europe-and-its-songs-in-barcelona",children:"21. \u2013 24. September \u2013 13. Internationales Volkslied Festival \u201eEurope and its Songs\u201c in Barcelona"}),"\n",(0,s.jsx)(n.p,{children:"Wieder einmal hatte sich der Verein im Jahr 2011 einen bis dahin in der Vereinsgeschichte einmaligen H\xf6hepunkt gesetzt. Einmalig schon deshalb, weil der Chor erstmals zu einem Wettbewerb per Flugzeug anreiste. Ziel war die katalanische Metropole Barcelona, wo vom 21.-24. September das 13. internationale Volkslied Festival \u201eEuropa and its songs\u201c stattfand. Einmalig auch dies, trat die \u201eFrohe Stunde\u201c doch erstmals auf spanischem Boden auf. Zun\xe4chst ging es jedoch, nach einem angenehmen Flug, vom Flughafen Barcelona in den etwa 60 Kilometer entfernten Badeort Callela, wo wir w\xe4hrend der Tage im Hotel Santa Monika untergebracht waren."}),"\n",(0,s.jsx)(n.p,{children:"Nachdem wir die Zimmer bezogen und ein erstes Mittagessen im Hotel zu uns genommen hatten ging es dann am fr\xfchen Nachmittag erstmals in die Innenstatt von Barcelona. Schon auf der Fahrt bemerkte man die steigende Verkehrsdichte auf den Strassen je n\xe4her man dem Zentrum kam und wo die Verkehrsregeln, typisch f\xfcr eine s\xfcdeurop\xe4ische Stadt, schon mal sehr gro\xdfz\xfcgig ausgelegt werden. Aber unseren Busfahrer Raul lie\xdf das Alles kalt. Der \xfcberholte schon mal einen anderen Bus mitten auf einer Kreuzung auf der Au\xdfenspur ohne mit der Wimper zu zucken. Als Raul uns dann mitten auf der Rambla aus dem Bus lie\xdf, gewannen wir schon mal einen ersten Einblick von der hektischen Betriebsamkeit dieser Stadt."}),"\n",(0,s.jsx)(n.p,{children:"Die Rambla ist ein Einkaufsboulevard, der sich von der Plaza de Catalunya quer durch die Altstadt bis zum Meer erstreckt und eine der Touristenattraktionen dieser Stadt. Da musste wir schon aufpassen, dass wir uns in diesem Menschengew\xfchl nicht aus den Augen verloren. Nach einem kurzen Fu\xdfmarsch erreichten wir dann den Platz vor der Kirche Santa Maria del Pi. In dieser Kirche fand an diesem Abend, quasi als Einstimmung und Begr\xfc\xdfung f\xfcr den Wettbewerb, das Er\xf6ffnungskonzert statt. Alle 19 teilnehmenden Ch\xf6re brachten w\xe4hrend dieses Konzertes ein Lied dar. Wir waren auch gleich als erster Chor dran und trugen das \u201eAve Maria\u201c von Franz Biebl vor. Der Auftritt gelang alles in allem recht passabel aber bei weitem nicht optimal. Trotz einer recht guten Akustik kam der Chor nie so recht ins musizieren und wirkte sehr unkonzentriert, was wohl in erster Linie an den Reisestrapazen des Tages und an den hohen Temperaturen in der Kirche lag, an die man sich erst gew\xf6hnen musste. Wir wussten aber schon mal, auf was wir beim Wettbewerb am n\xe4chsten Tag achten und was wir verbessern mussten."}),"\n",(0,s.jsx)(n.p,{children:"Die Mehrzahl unserer S\xe4nger entfloh anschlie\xdfend der Hitze in der Kirche und trafen sich im gegen\xfcberliegenden Stra\xdfenkaffee, um hier bei angenehmen Temperaturen und einem k\xfchlen Bier den weiteren Abend zu verbringen. Und schnell wurden auch schon einige Lieder angestimmt, die von vorbeigehenden Passanten mit Beifall belohnt wurden. Diejenigen, die bis zum Schluss des Konzertes in der Kirche geblieben waren berichteten von einem teilweise sehr hohem Niveau der anderen Ch\xf6re. Und so ging es dann etwas sp\xe4ter als geplant wieder zur\xfcck in unser Hotel nach Callela."}),"\n",(0,s.jsx)(n.p,{children:"Am n\xe4chsten Tag trafen wir uns kurz nach dem Fr\xfchst\xfcck zu einer intensiven Probe im Hotel. Hier sprach Chorleiter Jens R\xf6th noch mal die Fehler vom Er\xf6ffnungskonzert an, ging auf jedes einzelne der vorzutragenden St\xfccke ein und wies auf die Schwierigkeiten hin, auf die wir beim Wettbewerb unbedingt achten m\xfcssten. Bis zum Mittagessen blieb dann noch gen\xfcgend Zeit, sodass sich ein Gro\xdfteil der S\xe4nger die Badesachen anzogen und zum Strand gingen, wo die meisten der mitgereisten Frauen schon in der Sonne lagen. Hier genossen wir dann f\xfcr eine Stunde das Strandleben und erfrischten uns bei einem Bad im Mittelmeer, einige auch auf dem Wasser. Diejenigen, die nicht am Strand waren erkundigten zu Fu\xdf die Stadt Callela oder machten es sich am Hotel-Pool gem\xfctlich."}),"\n",(0,s.jsx)(n.p,{children:"Dann ging es am fr\xfchen Nachmittag in Konzertkleidung wieder in die Innenstadt von Barcelona zur Kirche Santa Maria del Pi. Hier stand um 16.00 Uhr jedem Chor eine 15-min\xfctige Einsingezeit zur Verf\xfcgung. Wir nutzten die Zeit in erster Linie um uns die optimale Aufstellung f\xfcr unseren Chor zu merken und uns wieder an die \xe4u\xdferen Umst\xe4nde in der Kirche zu gew\xf6hnen. Bis zum Wettbewerbsbeginn um 19.00 Uhr blieb dann noch Zeit, zumindest das gotische Viertel Barcelonas, in dem wir uns befanden, ein wenig auf eigene Faust zu erkunden. Ob nun beim Besuch der riesigen Markthalle \u201ela Boqueria\u201c, ein Erlebnis f\xfcr unsere Gastronomieabteilung oder beim Gang durch die nahegelegene Kathedrale, dem Dom der Stadt, mit seinem beeindruckendem Kreuzgang, in dessen G\xe4rten Magnolien und Palmen wachsen und G\xe4nse frei herumlaufen, \xfcberall str\xf6mten eine Vielzahl von Eindr\xfccken auf uns ein. Und \xfcberall in den verschlungenen Gassen und auf allen Pl\xe4tzen wurde musiziert und getanzt, feierte man doch in dieser Woche \u201eLa Merce\u201c, das h\xf6chste Stadtfest Barcelonas, bei dem auch gigantische, gro\xdfk\xf6pfige Puppen in der Stadt aufmarschierten."}),"\n",(0,s.jsx)(n.p,{children:"Dann wurde es erstmalig ernst. Wieder als erster Chor er\xf6ffneten wir den abendlichen Wettbewerb, als einziger in der Klasse M1, M\xe4nnerch\xf6re ab 40 S\xe4nger und alleiniger Vertreter Deutschlands. Die international besetzte Jury bestand aus der Estin Kai-T\xf6e Ellermaa, dem \xd6stereicher Felix Resch, dem Spanier Xavier Baulies i Bochaca und Michael Rinscheid aus Deutschland, der auch den Vorsitz inne hatte. Wir brachten nacheinander die St\xfccke \u201eWhat shall we do with the drunken sailor\u201c, \u201eDas Herz\u201c, \u201eRosemarie\u201c, \u201eSteal away\u201c und \u201eSoon ah will be done\u201c zum Vortrag. Schon vom ersten Lied an merkte man, dass der Chor diesmal ganz anders zu Werke ging als am Vortag. Ausgenommen vielleicht beim \u201eRosemarie, gelangen alle St\xfccke nahezu perfekt. Es wurde fein musiziert und der Chor war mit Freude aber dennoch konzentriert bei der Sache. Der langanhaltende Beifall des Publikums wie auch das wohlwollende Nicken einiger Jurymitglieder best\xe4tigten uns in unserer Meinung, wieder einmal einen Auftritt auf allerh\xf6chstem Niveau bew\xe4ltigt zu haben, \xe4hnlich denen in Prag oder Dortmund. Auch viele der nachfolgenden Ch\xf6re aus Argentinien, Venezuela, \xd6sterreich, Norwegen, Finnland, Estland, Tschechien, Lettland, Georgien und der Ukraine zeigten teilweise au\xdfergew\xf6hnliche Leistungen, die das Publikum begeisterten."}),"\n",(0,s.jsx)(n.p,{children:"Nach einem diesmal nur kurzen Besuch des gegen\xfcberliegenden Stra\xdfenkaffees, ging es schon um 21.00 Uhr wieder zur\xfcck nach Callela, inklusive eines Zwischenstopps, bei dem der sonst eher ruhige Fahrer Raul kurzzeitig die Fassung verlor, als die Mehrzahl der S\xe4nger nicht die WC\u2019s aufsuchten, sondern sich im Freien erleichterten. \u201ePolicia\u201c und \u201eSancion\u201c, dem spanischen Wort f\xfcr Bestrafung, waren mehrmals der spanischen Wortflut zu entnehmen. Nachdem Raul sich wieder beruhigt hatte kamen wir wohlbehalten am Hotel an, wo wir mit einer verdienten Feier den Tag ausklingen."}),"\n",(0,s.jsx)(n.p,{children:"Am n\xe4chsten Tag stand dann zun\xe4chst eine gro\xdfe Stadtrund fahrt an. An der Stierkampfarena vorbei war der \u201eTemplo de la Sagrada Familia\u201c unser erstes Ziel. Dieses beeindruckende Kirchenbauwerk des spanischen Architekten Antoni Gaudi ist wohl jetzt schon, obwohl noch lange nicht fertiggestellt, das Wahrzeichen der Stadt. Bei einem Rundgang um die Kirche konnten wir die einzigartige Architektur dieses Bauwerks mit seinen 4 bis jetzt fertigen T\xfcrmen bewundern. Entg\xfcltig fertig, wird die Kirche einmal aus 18 T\xfcrmen bestehen, von denen der H\xf6chste eine H\xf6he von 170 Metern erreichen wird. Erleben werden das aber nur die wenigsten von uns. Vorbei an weiteren Sehensw\xfcrdigkeiten ging es dann zum Stadion des FC Barcelona, dem \u201eCamp Nou\u201c. Nach einem Rundgang durch das Museum konnten wir dort auch das Innere des Stadions besichtigen. Vorbei am Nationalpalast mit den Wasserspielen der \u201eFuente Magica\u201c ging es dann auf der alten Formel 1-Rennstrecke hinauf zum Berg \u201eMontjuic\u201c. Von hier, wo auch das Olympiagel\xe4nde der Spiele von 1992 liegt, hat man einen sehr sch\xf6nen Ausblick auf die Stadt."}),"\n",(0,s.jsx)(n.p,{children:"Nach einem sehr guten Mittagessen in einem Lokal an der Placa Reial, wo sich allerdings die Begleichung der Rechnung ein wenig problematisch gestaltete, ging es dann zu Fu\xdf die Rambla hinauf wieder zur Santa Maria del Pi, wo um 17 Uhr in der nun \xfcbervollen Kirche die Preisverleihung des Wettbewerbs begann. Nach den Dankesreden des Veranstalters Hans-Albert Courtial und Herrn Hans Urrigshardt, dem Pr\xe4sidenten des Wettbewerbs, gab der Vorsitzende der Jury Michael Rinscheid die Punkte und die jeweilig erreichte Medaille bekannt. Und die Freude unter uns war sehr gro\xdf, als er uns mit erreichten 94 Punkten das Erringen der Goldmedaille mitteilte. Mit der siebtbesten Wertung aller 18 teilnehmenden Ch\xf6re konnten wir auch hier in Barcelona \xfcberzeugen. Umso h\xf6her ist der Erfolg einzusch\xe4tzen, stellte man uns doch hier auf eine Stufe mit manch semiprofessionellem Esemble. Und dies auch verdienterma\xdfen, wie uns von einigen Jurymitgliedern best\xe4tigt wurde."}),"\n",(0,s.jsx)(n.p,{children:"Ein freudestrahlender Jens R\xf6th nahm die Urkunde und Medaille in Empfang und da diese aus Gold war, durften wir als Kr\xf6nung auch noch am abschlie\xdfenden Preistr\xe4gerkonzert teilnehmen. Auf Wunsch der Jury trugen wir hier noch mal das \u201eSteal away\u201c unseres Dirigenten vor was mit viel Beifall belohnt wurde. H\xf6hepunkt hier waren sicherlich die Vortr\xe4ge des \u201ejungen Chores MASKA\u201c aus Lettland, der von der Jury zu Recht die Maximalpunktzahl von 100 Punkten erhielt."}),"\n",(0,s.jsx)(n.p,{children:"Nach diesem freudigen Ereignis ging es dann, wieder zu Fu\xdf, hinunter zum Hafen, wo der Veranstalter alle Mitwirkenden zur \u201eFiesta Catalunya eingeladen hatte. F\xfcr den Fu\xdfmarsch, wo sich die letzten 1000 m doch bedenklich lange zogen, wurden wir am Hafengel\xe4nde mit einer tollen Party belohnt. Und alles umsonst. Nicht nur das sehr gute mehrg\xe4ngige Men\xfc mit leckeren Fischspezialit\xe4ten aller Art, auch jegliche Getr\xe4nke waren von Pr\xe4sident Hans Urrigshardt gesponsert. Als Kr\xf6nung gab es auch noch ein \u201eFauerwork\u201c vom gegen\xfcberliegenden Ufer. Schlie\xdflich spielte noch eine sehr gute Live-Band Hits aus den 70er und 80er Jahren nach denen bis in die Nacht getanzt wurde. Es war schon ein sch\xf6nes Erlebnis, wie all die Menschen verschiedenster Nationalit\xe4t miteinander gefeiert haben. Wobei bei uns die deutsch-norwegischen Beziehungen eindeutig im Vordergrund standen. Mit einst\xfcndiger Versp\xe4tung betraten wir dann wieder den Bus und trafen erst sp\xe4t in der Nacht wieder am Hotel ein. Am n\xe4chsten Morgen erst mal ein ungewohntes Bild, denn nach all den sonnigen Tagen regnete es in Callela."}),"\n",(0,s.jsx)(n.p,{children:"Nach dem Fr\xfchst\xfcck und dem Auschecken aus dem Hotel ging es ein letztes mal in die Innenstadt von Barcelona, wo auf der Placa de Catalunya das letzte Konzert der Wettbewerbsch\xf6re stattfand. Hier hatte der Veranstalter aber f\xfcr eher bescheidene \xe4ussere Bedingungen gesorgt. Es stand nur ein sehr kleines Zelt mit einer noch kleineren B\xfchne zur Verf\xfcgung und da es auch in Barcelona regnete war es hier doch ziemlich ungem\xfctlich. So verlie\xdfen wir doch recht schnell nach unserem Auftritt die Veranstaltung um noch einwenig die Stadt zu erkunden, ehe es dann am fr\xfchen Nachmittag wieder zum Flughafen Barcelona ging. Nachdem wir uns dort von unserem lieben Busfahrer Raul verabschiedet hatten, bestiegen wir dort das Flugzeug in Richtung Heimat."}),"\n",(0,s.jsx)(n.p,{children:"Zuhause wurden wir nat\xfcrlich standesgem\xe4\xdf durch die Vertreter der Ortsgemeinde Frank Reusch und Hermann Kleppel empfangen, die uns beide zu unserem Erfolg gratulierten und uns daf\xfcr dankten, dass wir den Namen unserer Gemeinde auch in Spanien so w\xfcrdig vertreten hatten. So gingen dann die erlebnisreichen Tage von Barcelona erneut mit einer Feier inklusive Freibier von der Ortsgemeinde zu Ende."}),"\n",(0,s.jsx)(n.p,{children:"Bedanken m\xf6chte ich mich an dieser Stelle einmal bei allen Mitgereisten. Wir vom Vorstand hatten es im Vorfeld mit einigen Unw\xe4gbarkeiten zu tun und wussten auch nicht genau, welche Probleme vor Ort auf uns zukommen w\xfcrden. Wir hofften hier auf das Verst\xe4ndnis der Mitreisenden. Aber auch diesmal hatten wir wieder eine tolle Truppe beisammen, die sich gegenseitig half und diszipliniert alle Absprachen folgte. Nie gab es ein Wort der Kritik. Das erleichtert die Arbeit der Verantwortlichen ungemein. Hierf\xfcr nochmals herzlichen Dank."}),"\n",(0,s.jsx)(n.h2,{id:"06-november--konzert-beim-mgv-frohsinn-baiertal",children:"06. November \u2013 Konzert beim MGV \u201eFrohsinn\u201c Baiertal"}),"\n",(0,s.jsx)(n.p,{children:"Am 6. November wirkten wir bei einem Konzert des MGV \u201eFrohsinn\u201c Baiertal mit. Seit den Tagen in Dortmund beim DCW verbindet uns mit dem Chor aus der Kurpfalz quasi schon eine kleineFreundschaft. Das Konzert fand im Palatin, dem Kulturzentrum von Wiesloch statt, ein beeindruckendes Haus mit einer sehr guten Akustik. Neben den beiden M\xe4nnerch\xf6ren wirkte auch der Frauenchor \u201eBellacanta\u201e der Baiertaler mit, ebenfalls unter Leitung von Stefan Kistner. Dieser feierte an diesem Tag auch noch sein 25-j\xe4hriges Chorleiterjubil\xe4um, wof\xfcr er auch geehrt wurde. Nachdem wir uns ausgiebig am Kuchenb\xfcffet gest\xe4rkt hatten ging es dann auf die B\xfchne und es gelang uns ein ausnehmend guter Auftritt. Ausgenommen vom \u201eAve Maria\u201c, wo der 1. Tenor am Ende ein wenig die Geduld verlor und so den Schlu\xdf ziemlich ruinierte, gelangen uns alle St\xfccke sehr gut, was uns vom zahlreich erschienenen Publikum mit langanhaltendem Beifall best\xe4tigt wurde. Nach dem Konzert ging es dann ins benachbarte Baiertal, wo mit einem gemeinsamen Abendessen der Tag endete. Ein grosses Lob geht an dieser Stelle an die Baiertaler f\xfcr die Gastfreundschaft und besondere Herzlichkeit, die wir an diesem Tag erleben durften. Nicht nur dass alle Speisen und Getr\xe4nke frei waren, nein auch in den Gespr\xe4chen und in den gemeinsam gesungenen Liedern war die grosse \xc4hnlichkeit in den Strukturen der beiden Verein zu sp\xfcren. Da m\xfcssen wir uns anstrengen, dies in gleicher Weise bei unserem geplanten Konzert zur\xfcck zu geben."}),"\n",(0,s.jsx)(n.h2,{id:"was-passiert-2011-sonst-noch-",children:"Was passiert 2011 sonst noch \u2026"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"07. Januar"})," \u2013 Jahreshauptversammlung mit Ehrung Edi Hannappel (40 Jahre Vorstand)"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"20. Januar"})," \u2013 Gedenkgottesdienst f\xfcr die lebenden und verstorbenen Mitglieder des Vereins"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"10. April"})," \u2013 St\xe4ndchen zum 85. Geburtstag des Ehrenmitglieds Josef Kremer"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"14. Mai"})," \u2013 Fr\xfchjahrskonzert beim MGV \u201eEintracht\u201c Frickhofen"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"5. Juni"})," \u2013 St\xe4ndchen zur Goldenen Hochzeit von Wolfgang und Anneliese Diefenbach"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"16. Juli"})," \u2013 Thekendienst auf der Sportwoche der Spvgg. Steinefrenz/Weroth"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"04. August"})," \u2013 Fackelzug zur Goldenen Hochzeit der Eheleute Mersteiner"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"03. September"})," \u2013 Jubil\xe4umskonzert 110 Jahre MGV \u201eFrohsinn\u201c Steinefrenz"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"22. Oktober"})," \u2013 St\xe4ndchen zur Goldenen Hochzeit von Alfred und Inge Kremer"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"13. November"})," \u2013 Gedenkfeier am Ehrenmal (Volkstrauertag)"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"26. November"})," \u2013 Jahresabschlussfeier im B\xfcrgerhaus"]}),"\n"]})]})}function o(e={}){const{wrapper:n}={...(0,a.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(u,{...e})}):u(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>t,x:()=>d});var i=r(6540);const s={},a=i.createContext(s);function t(e){const n=i.useContext(a);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:t(e.components),i.createElement(a.Provider,{value:n},e.children)}}}]);