"use strict";(self.webpackChunkarchiv_frohe_stunde=self.webpackChunkarchiv_frohe_stunde||[]).push([[9021],{1904:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>h,contentTitle:()=>d,default:()=>o,frontMatter:()=>a,metadata:()=>i,toc:()=>l});const i=JSON.parse('{"id":"archiv/2010-2019/2016","title":"2016","description":"Das Ensemble QUARTONAL ist zu Gast bei unserem Eigenen Konzert im B\xfcrgerhaus und begeistert das Publikum.","source":"@site/docs/archiv/2010-2019/2016.md","sourceDirName":"archiv/2010-2019","slug":"/archiv/2010-2019/2016","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2016","draft":false,"unlisted":false,"editUrl":"https://github.com/Frohe-Stunde-Weroth/archiv-frohe-stunde/blob/main/docs/archiv/2010-2019/2016.md","tags":[],"version":"current","sidebarPosition":4,"frontMatter":{"sidebar_position":4,"description":"Das Ensemble QUARTONAL ist zu Gast bei unserem Eigenen Konzert im B\xfcrgerhaus und begeistert das Publikum."},"sidebar":"archivSidebar","previous":{"title":"2017","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2017"},"next":{"title":"2015","permalink":"/archiv-frohe-stunde/docs/archiv/2010-2019/2015"}}');var s=r(4848),t=r(8453);const a={sidebar_position:4,description:"Das Ensemble QUARTONAL ist zu Gast bei unserem Eigenen Konzert im B\xfcrgerhaus und begeistert das Publikum."},d="2016",h={},l=[{value:"09. Januar \u2013 Jahreshauptversammlung",id:"09-januar--jahreshauptversammlung",level:2},{value:"19. M\xe4rz \u2013 Konzert beim Offenen Chor des MGV 1875 Nauort",id:"19-m\xe4rz--konzert-beim-offenen-chor-des-mgv-1875-nauort",level:2},{value:"16. April \u2013 Konzert beim MGV \u201eEintracht\u201c Frickhofen",id:"16-april--konzert-beim-mgv-eintracht-frickhofen",level:2},{value:"04. Juni \u2013 Chorfestival in Bad-Soden/Saalm\xfcnster",id:"04-juni--chorfestival-in-bad-sodensaalm\xfcnster",level:2},{value:"10. September \u2013 Familien-/Wandertag",id:"10-september--familien-wandertag",level:2},{value:"08. Oktober \u2013 Konzert beim Chor \u201eLaudate\u201c Oberneisen",id:"08-oktober--konzert-beim-chor-laudate-oberneisen",level:2},{value:"19. November \u2013 Eigenes Konzert mit dem Ensemble QUARTONAL",id:"19-november--eigenes-konzert-mit-dem-ensemble-quartonal",level:2},{value:"26. November \u2013 Weihnachtsmarkt am B\xfcrgerhaus",id:"26-november--weihnachtsmarkt-am-b\xfcrgerhaus",level:2},{value:"Was passiert 2016 sonst noch \u2026",id:"was-passiert-2016-sonst-noch-",level:2}];function u(e){const n={h1:"h1",h2:"h2",header:"header",li:"li",p:"p",strong:"strong",ul:"ul",...(0,t.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"2016",children:"2016"})}),"\n",(0,s.jsx)(n.h2,{id:"09-januar--jahreshauptversammlung",children:"09. Januar \u2013 Jahreshauptversammlung"}),"\n",(0,s.jsx)(n.p,{children:"Auch im Jahr 2016 begannen die Vereinst\xe4tigkeiten mit der Jahreshauptversammlung am 9. Januar im Vereinslokal der Pizzeria \u201eBella Napoli\u201c. In der von Hans-Georg Jung geleiteten Versammlung ging man zun\xe4chst in den einzelnen Berichten auf die Ereignisse im Jahr 2015 ein, wobei ein besonderer Augenmerk auf der erfolgreichen Teilnahme am internationalen Anton-Bruckner-Chorwettbewerb im \xf6sterreichischen Linz lag. Im Mittelpunkt der Veranstaltung aber standen die Tagesordnungspunkte 10 und 11, in denen mit der Beratung \xfcber eine Beitragserh\xf6hung bei den aktiven Mitgliedern sowie den Neuwahlen des Vorstandes wichtige Entscheidungen f\xfcr die Zukunft des Vereins anstanden."}),"\n",(0,s.jsx)(n.p,{children:"Wegen der negativen Entwicklung der Vereinskasse wurde \xfcber eine mehrfach vom Vorstand angemahnte Beitragserh\xf6hung bei den aktiven Mitgliedern gesprochen. Nach langer und ausgiebiger Aussprache wurde mit 22 Ja-Stimmen, 4 Enthaltung und keiner Gegenstimme beschlossen den Mitgliedsbeitrag der aktiven S\xe4nger um 3 Euro im Monat zu erh\xf6hen. Der Mitgliedsbeitrag der Aktiven betr\xe4gt also ab dem 01.01.2016 monatlich 9 Euro. Bedingung allerdings ist, das man die finanzielle Entwicklung weiter beobachten wird um dann in 3 Jahren erneut \xfcber das Thema sprechen wird. Bevor man dann zu den Neuwahlen kam, galt es zun\xe4chst den ausscheidenden Vorstandsmitgliedern zu danken. Leo Weyand und Martin Kr\xe4mer stellten sich aus privaten sowie beruflichen Gr\xfcnden nicht mehr zur Wahl und Hans-Georg Jung bedankte sich bei Beiden f\xfcr ihre engagierte Mitarbeit im Vorstand. Ebenso dankte er Torsten Meuer f\xfcr dessen vorbildliche Arbeit als Kassierer. Torsten Meuer legte zwar das Amt des Kassierers nieder, bleibt dem Vorstand aber weiterhin als Beisitzer erhalten. Hier das Ergebnis der Neuwahlen: Der gesch\xe4ftsf\xfchrende Vorstand besteht aus den Chorsprechern Hans-Georg Jung und Johannes Hannappel, Bernd-Josef Metternich als Gesch\xe4ftsf\xfchrer, sowie, neu hinzugekommen, Manfred Fachinger als Kassierer. In der S\xe4ule 2 sind Markus Fein als Notenwart, Andreas Kronimus als stellvertretender Gesch\xe4ftsf\xfchrer sowie Paul Weimer und Torsten Meuer als Beisitzer t\xe4tig. Die S\xe4ule 3 besteht aus Edi Hannappel als stellvertretender Kassierer und Norbert Hannappel als Beisitzer. Neu hinzugekommen sind hier Kurt Konh\xe4user und Elias Hannappel. Die insgesamt doch recht harmonisch verlaufende wurde traditionell mit dem Lied \u201eDas Morgenrot\u201c geschlossen."}),"\n",(0,s.jsx)(n.h2,{id:"19-m\xe4rz--konzert-beim-offenen-chor-des-mgv-1875-nauort",children:"19. M\xe4rz \u2013 Konzert beim Offenen Chor des MGV 1875 Nauort"}),"\n",(0,s.jsx)(n.p,{children:"Den ersten offiziellen Auftritt bildete dann das Konzert beim gemischten Chor 1876 Nauort am 19. M\xe4rz. Da Jens R\xf6th wegen einer hochgradigen grippalen Infektion das Bett h\xfcten musste hatte sich dankenswerter Weise Manfred Fachinger bereit erkl\xe4rt den Chor bei dieser Veranstaltung zu leiten. Dies bedeutete aber, das fast keines der gemeldeten St\xfccke vorgetragen werden konnte und auf schon etwas \xe4lteres M\xe4nnerchorrepertoire zur\xfcckgegriffen werden musste. Was nat\xfcrlich bei dem Pop- und Schlager-lastigen Programm etwas aus der Reihe fiel. Aber trotz oder gerade weil wir hier nur mit unserem Vizechorleiter auftreten konnten gelang uns doch eine feine und sehr gut anzuh\xf6rende Leistung. Konzentriert und auf musikalisch ansprechendem Niveau trugen wir unsere Lieder vor, die auch dem Publikum gefielen, sodass noch eine Zugabe f\xe4llig wurde."}),"\n",(0,s.jsx)(n.h2,{id:"16-april--konzert-beim-mgv-eintracht-frickhofen",children:"16. April \u2013 Konzert beim MGV \u201eEintracht\u201c Frickhofen"}),"\n",(0,s.jsx)(n.p,{children:"Im Jahr 1991, also vor ziemlich genau 25 Jahren, \xfcbernahm Jens R\xf6th die Chorleitung beim MGV \u201eEintracht\u201c Frickhofen. Dieses Jubil\xe4um nahmen sich die Frickh\xf6fer S\xe4nger zum Anlass f\xfcr ein Konzert, zu dem sie alle derzeit von Jens R\xf6th geleiteten Ch\xf6re einluden, also neben unserem Chor auch den Kirchenchor St. Matthias Steinefrenz/Weroth und das M\xe4nnerchorensemble \u201evocaw\xe4ller\u201c. Die doch recht zahlreich erschienenen Zuh\xf6rer durften ein sehr anspruchsvolles Konzert erleben, in dem jeder einzelne Chor seine St\xe4rken pr\xe4sentieren konnte. Es war ein sehr abwechslungsreicher und kurzweiliger Abend auf musikalisch sehr ansprechendem Niveau. Auch wir wussten bei diesem Konzert sehr zu gefallen, wobei besonders die \u201eSchilflieder\u201c und das \u201eRock me Amadeus\u201c das Publikum beeindruckte. Und das die R\xf6th-Ch\xf6re auch zu feiern wissen ist ja bekannt, sodass es nicht verwunderte, das die Heimfahrt mehrmals nach hinten verschoben wurde und wir erst sp\xe4t in der Nacht die Halle verlie\xdfen. Ein gro\xdfes Lob geht aber wieder einmal an Jens R\xf6th. Er verbrachte ja fast die ganze Veranstaltung auf der B\xfchne, aber es gelang ihm, jedes einzelne Lied mit der gleichen Konzentration und Hingabe zu dirigieren. Das war wieder einmal eine eindrucksvolle Leistung von ihm."}),"\n",(0,s.jsx)(n.h2,{id:"04-juni--chorfestival-in-bad-sodensaalm\xfcnster",children:"04. Juni \u2013 Chorfestival in Bad-Soden/Saalm\xfcnster"}),"\n",(0,s.jsx)(n.p,{children:"Den H\xf6hepunkt im ersten Halbjahr bildete die Teilnahme am Chorfestival im hessischen Bad-Soden/ Saalm\xfcnster am 4. Juni. Nachdem wir schon jahrelang keinen nationalen Wettbewerb mehr besucht hatten, entschlossen wir uns hier am Volksliederwettbewerb teilzunehmen. Wir hatten uns in der Kategorie M3V1 angemeldet, gemeinsam mit den Mitkonkurenten, den M\xe4nnerch\xf6ren aus Ebsdorf, Gernsdorf und Panrod. Bei herrlichstem Sommerwetter ging es schon recht fr\xfch am Morgen mit dem Bus in Richtung Spessart los. Das Festival fand in dem neu und sehr gro\xdfz\xfcgig gestalteten Spessartforum statt und bot allen ca. 60 teilnehmenden Ch\xf6ren auch ausreichend Platz. Jedoch war die B\xfchne der Konzerthalle doch eher klein und die Akustik war auch eher bescheiden. Von unserem Chorleiter bestens vorbereitet betraten wir gegen Mittag als zweiter Verein in der Klasse die B\xfchne. Zum Vortrag berachten wir drei Volkslieder, in der Reihenfolge \u201eVive L\u2019Amour\u201c von Parker/Shaw, \u201eO du stille Zeit\u201c von Bernd Englbrecht und \u201eWohlauf, ihr G\xe4ste\u201c von Erasmus Widmann."}),"\n",(0,s.jsx)(n.p,{children:"Boten wir die St\xfccke \u201eVive L\u2019Amour\u201c und \u201eWohlauf, ihr G\xe4ste\u201c auf einem bemerkenswert hohen Niveau, gelang es uns nicht bei dem ruhigen \u201eO du stille Zeit\u201c die Spannung zu halten und fielen hier in der Leistung etwas ab. Dies wurde uns auch durch die Jury best\xe4tigt, die sich aus den renomierten Fachleuten Michael Rinnscheid, Felix Resch und Kalman Strau\xdf zusammensetzte. W\xe4hrend wir beim 1. und letzten St\xfcck mit je 92,33 und sogar 93,67 Punkten \xfcber dem Schitt lagen fielen die 90,33 Punkte f\xfcr \u201eO du stille Zeit\u201c doch deutlich ab. Alles in allem aber dennoch eine gro\xdfartige Wertung, errangen wir doch mit 92,11 Punkten im Mittel mit gro\xdfem Abstand zu den Mitkonkurenten ein Golddiplom. Dies wurde nat\xfcrlich auch ausgiebig gefeiert bei k\xfchlem Bier im schattigen Kurpark. Nat\xfcrlich wurden auch noch einige Lieder angestimmt und wie der Zufall es will gelangten Aufnahmen von unserem Chor in die Abendausgabe der Hessenschau. Ein gro\xdfes Lob an den Veranstalter, der diese Mammutveranstaltung doch recht z\xfcgig und ohne gro\xdfe Verz\xf6gerungen durchf\xfchrte, soda\xdf wir abends noch zu einer christlichen Zeit und zufrieden mit unserer Leistung die Heinreise antraten."}),"\n",(0,s.jsx)(n.h2,{id:"10-september--familien-wandertag",children:"10. September \u2013 Familien-/Wandertag"}),"\n",(0,s.jsx)(n.p,{children:"Zu einem Familien- Wandertag hatten wir am Samstag, den 10. September geladen und viele Mitglieder waren der Einladung gefolgt. Los ging es nachmittags bei hochsommerlichen Temperaturen vom B\xfcrgerhaus in Richtung Steinefrenzer Bahnhof, wo wir am idyllisch gelegenen Angelweiher eine Rast einlegten. Gest\xe4rkt durch leckeren Blechkuchen, Kaffe und Kaltgetr\xe4nken ging es dann \xfcber die \xd6hlm\xfchle und Steinefrenz zum Sporthaus Weroth, wo der Tag bei Essen vom Grill, selbstgemachten Salaten und k\xfchlen Getr\xe4nken in fr\xf6hlicher Runde ausklang."}),"\n",(0,s.jsx)(n.h2,{id:"08-oktober--konzert-beim-chor-laudate-oberneisen",children:"08. Oktober \u2013 Konzert beim Chor \u201eLaudate\u201c Oberneisen"}),"\n",(0,s.jsx)(n.p,{children:"Auf Einladung des gemischten Chores \u201eLaudate Oberneisen\u201c nahmen wir an deren Konzert im B\xfcrgerhaus Hahnst\xe4tten teil. Auch hier best\xe4tigte sich der Trend der letzten Konzertveranstaltungen, hin zu moderner Pop-, Schlager- und Gospelliteratur, meist in englischer Sprache gesungen. Da wei\xdf man ja nie so richtig, ob man da als einziger traditioneller M\xe4nnerchor \xfcberhaupt noch reinpasst. Aber es ist uns auch in Hahnst\xe4tten gelungen, das Publikum zu begeistern. Der lang anhaltende Beifall nach unserem abwechslungsreichen und gut dargebotenen Programm war Beweis genug. Auch hier kamen wir um eine Zugabe nicht herum. Und auch unsere Lieder nach dem Konzert werden den Beteiligten in Hahnst\xe4tten wohl in Erinnerung bleiben."}),"\n",(0,s.jsx)(n.h2,{id:"19-november--eigenes-konzert-mit-dem-ensemble-quartonal",children:"19. November \u2013 Eigenes Konzert mit dem Ensemble QUARTONAL"}),"\n",(0,s.jsx)(n.p,{children:"Den musikalischen H\xf6hepunkt im Jahr bildete zweifellos unser eigenes Konzert im B\xfcrgerhaus am 19. November. Da es die 10. Veranstaltung im Rahmen der Konzertreihe \u201eDie FROHE STUNDE l\xe4dt ein\u201c war, hatten wir uns f\xfcr dieses kleine Jubil\xe4um ganz besondere G\xe4ste als Mitwirkende eingeladen. Mit dem Vokalensemble QUARTONAL war es uns gelungen eines der derzeit besten Gesangsgruppen Deutschlands f\xfcr diesen Abend zu engagieren. Als angenehme Auflockerung des Programms wirkten ebenfalls noch der Frauenchor ChoryFeen Staudt unter der engagierten Leitung von Jessica Burggraf mit. Schon bei den Vorbereitungen f\xfcr dieses Konzert sp\xfcrte man das hohe Engagement unserer S\xe4nger nicht nur beim Kartenvorverkauf, sodass wir schon Wochen vor der Veranstaltung feststellen konnten, dass zumindest die finanzielle Seite sicher abgedeckt war. F\xfcr den musikalischen Erfolg sorgten dann die Mitwirkenden des Abends in beeindruckender Weise selbst. Allen Beteiligten war die Freude am Musizieren anzusehen und dieser Funke sprang dann auch schnell auf das Publikum \xfcber. Nat\xfcrlich ragten hier die vier Jungs von QUARTONAL besonders heraus. Schon bei den ersten T\xf6nen des Quartetts stand f\xfcr uns fest, dass sich alle Kosten und M\xfchen bei der Vorbereitung gelohnt hatten. \u201eQuartonal, das ist Kunstgenuss mit vier Stimmen, der keine W\xfcnsche offen l\xe4sst\u201c, oder \u201eMusik wie von einem anderen Stern\u201c so hie\xdf es anschlie\xdfend in den Artikeln der heimischen Presse und beschreibt wohl am besten die Leistung der vier Stimmakrobaten aus dem hohen Norden Deutschlands."}),"\n",(0,s.jsx)(n.p,{children:"Nicht allein wegen der perfekten Sangeskunst, nein, auch durch ihr symphatisches und freundliches Auftreten zogen sie die Zuh\xf6rer schnell in ihren Bann. Frenetischer Beifall des begeisterten Publikums f\xfcr alle Chorformationen des Abends war Beweis f\xfcr ein au\xdfergew\xf6hnliches Musikerlebnis, das wir hier in Weroth erleben durften. Zumindest k\xfcnstlerisch gesehen war es wohl das bisher erfolgreichste in der Geschichte unserer Konzertreihe. Dass die vier Jungs auch gerne feiern, zeigten sie im Anschluss der Veranstaltung. Bis sp\xe4t in die Nacht blieben sie, zur Freude der vielen weiblichen Anh\xe4ngerinnen, bei uns im B\xfcrgerhaus und vertilgten dabei so manches Glas Bier und auch hochprozentige Sachen. So kehrten sie erst sp\xe4t, mit Zwischenstopp bei Kohnh\xe4users und teilweise auch noch in der Disco in Staffel, in ihre Unterkunft im Hotel Freim\xfchle zur\xfcck. Allen S\xe4ngern und Helfern des Abends, der Feuerwehr sowie unserer Kirmesgesellschaft sei hier noch allen f\xfcr ihre Arbeit gedankt. Sie alle haben dazu beigetragen, dass diese Veranstaltung ein voller Erfolg wurde."}),"\n",(0,s.jsx)(n.h2,{id:"26-november--weihnachtsmarkt-am-b\xfcrgerhaus",children:"26. November \u2013 Weihnachtsmarkt am B\xfcrgerhaus"}),"\n",(0,s.jsx)(n.p,{children:"Eine Woche sp\xe4ter fand schon die n\xe4chste Veranstaltung der FROHEN STUNDE statt, diesmal vor unserem B\xfcrgerhaus. Nachdem jahrelang die P\xe4ckelscher Uniteds mit Erfolg den Wer\xf6ther Weihnachtsmarkt veranstaltet hatten und eine Fortf\xfchrung abgelehnt hatten, suchte die Ortsgemeinde unter den Vereinen einen Nachfolger. Nach einigen Diskussionen innerhalb des Vorstandes wie auch innerhalb des Chores, sagten wir schlie\xdflich doch zu und veranstalteten am 26. November den diesj\xe4hrigen Weihnachtsmarkt. Alles in allem kann man von einer durchaus gelungenen Veranstaltung reden, die bei allen Besuchern sehr gelobt wurde. In den drei aufgebauten Buden wurden Grillw\xfcrste, Leberk\xe4se und Waffeln und gen\xfcgend Kalt- und Hei\xdfgetr\xe4nke angeboten. Und auch f\xfcr die Kinder gab es gen\xfcgend S\xfc\xdfigkeiten, die nat\xfcrlich vom ebenfalls anwesenden Nikolaus verteilt wurden. Auf dem sch\xf6n geschm\xfcckten Platz verbreitete sich schnell eine heimelige Adventsstimmung. Der Hunds\xe4nger Musikverein sorgte f\xfcr die weihnachtliche Musik und das Wetter spielte auch mit, sodass zum Schluss alle zufrieden waren und man einen gelungenen Jahresabschluss feiern konnte."}),"\n",(0,s.jsx)(n.h2,{id:"was-passiert-2016-sonst-noch-",children:"Was passiert 2016 sonst noch \u2026"}),"\n",(0,s.jsxs)(n.ul,{children:["\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"20. Februar"})," \u2013 Tod unseres langj\xe4hrigen S\xe4ngers und Vorstandsmitglied Rudi Pehl"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"26. Mai"})," \u2013 Mitgestaltung des Pfarrfest zum Jubil\xe4um \u201e100 Jahre St. Matthias\u201c Steinefrenz"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"14. Juli"})," \u2013 Tod unseres aktiven Ehrenmitglieds Helmut Schwarz"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"17. Juli"})," \u2013 Thekendienst bei der Sportwoche der Spvgg. Steinefrenz/Weroth"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"11. September"})," \u2013 St\xe4ndchen zum Goldenen Priesterjubil\xe4um von Ottmar Steinebach"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"02. Oktober"})," \u2013 Thekendienst an der 90er Jahre Party der Kirmesgesellschaft"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"13. November"})," \u2013 Gedenkfeier am Ehrenmal (Volkstrauertag)"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"17. Dezember"})," \u2013 Thekendienst beim Empfang zum Jubil\xe4umsgottesdienst in Steinefrenz"]}),"\n",(0,s.jsxs)(n.li,{children:[(0,s.jsx)(n.strong,{children:"30. Dezember"})," \u2013 Tod unseres langj\xe4hrigen f\xf6rdernden Mitglieds Norbert Kremer"]}),"\n"]})]})}function o(e={}){const{wrapper:n}={...(0,t.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(u,{...e})}):u(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var i=r(6540);const s={},t=i.createContext(s);function a(e){const n=i.useContext(t);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:a(e.components),i.createElement(t.Provider,{value:n},e.children)}}}]);